import React, { useState, useEffect, useRef } from 'react';
import { fetchChannels, fetchHeaders, fetchCombinations, fetchSections } from '../services/api';
import './PathNavigation.css';

const PathNavigation = ({ 
  selectedChannel, 
  selectedHeader, 
  selectedCombination,
  onChannelSelect,
  onHeaderSelect,
  onCombinationSelect,
  viewMode,
  onViewModeChange,
  selectedSection,
  onSectionSelect
}) => {
  const [channels, setChannels] = useState([]);
  // eslint-disable-next-line no-unused-vars
  const [headers, setHeaders] = useState([]);
  // eslint-disable-next-line no-unused-vars
  const [combinations, setCombinations] = useState([]);
  const [showNavigationPanel, setShowNavigationPanel] = useState(false);
  const [channelLevels, setChannelLevels] = useState([]);
  const [selectedLevels, setSelectedLevels] = useState([]);

  // Section related states
  const [sections, setSections] = useState([]);
  const [sectionLevels, setSectionLevels] = useState([]);
  const [selectedSectionLevels, setSelectedSectionLevels] = useState([]);
  const [activePanelType, setActivePanelType] = useState(null); // 'channel' or 'section'

  const navigationRef = useRef(null);

  // Parse channel into hierarchical levels
  const parseChannelLevels = (channelList) => {
    const levelMap = {};

    channelList.forEach(channel => {
      const channelKey = channel.채널키;
      if (!channelKey) return;

      const levels = channelKey.split('_');

      // Initialize levelMap for each depth
      levels.forEach((level, index) => {
        const parentPath = index === 0 ? '' : levels.slice(0, index).join('_');
        const currentPath = index === 0 ? level : `${parentPath}_${level}`;

        if (!levelMap[index]) {
          levelMap[index] = [];
        }

        // Check if this level item already exists in the array
        const existingItem = levelMap[index].find(item => 
          item.name === level && item.parentPath === parentPath
        );

        if (!existingItem) {
          levelMap[index].push({
            name: level,
            fullPath: currentPath,
            parentPath
          });
        }
      });
    });

    return levelMap;
  };

  // Parse section into hierarchical levels (similar to parseChannelLevels)
  const parseSectionLevels = (sectionList) => {
    const levelMap = {};

    sectionList.forEach(section => {
      const sectionKey = section.섹션키;
      if (!sectionKey) return;

      const levels = sectionKey.split('_');

      // Initialize levelMap for each depth
      levels.forEach((level, index) => {
        const parentPath = index === 0 ? '' : levels.slice(0, index).join('_');
        const currentPath = index === 0 ? level : `${parentPath}_${level}`;

        if (!levelMap[index]) {
          levelMap[index] = [];
        }

        // Check if this level item already exists in the array
        const existingItem = levelMap[index].find(item => 
          item.name === level && item.parentPath === parentPath
        );

        if (!existingItem) {
          levelMap[index].push({
            name: level,
            fullPath: currentPath,
            parentPath
          });
        }
      });
    });

    return levelMap;
  };

  // Fetch channels on component mount
  useEffect(() => {
    const getChannels = async () => {
      try {
        const response = await fetchChannels();
        const channelList = response.list || [];
        setChannels(channelList);

        // Parse channel levels
        const levels = parseChannelLevels(channelList);
        setChannelLevels(levels);
      } catch (error) {
        console.error('Error fetching channels:', error);
      }
    };

    getChannels();
  }, []);

  // Fetch sections on component mount
  useEffect(() => {
    const getSections = async () => {
      try {
        const response = await fetchSections();
        const sectionList = response.list || [];
        setSections(sectionList);

        // Parse section levels
        const levels = parseSectionLevels(sectionList);
        setSectionLevels(levels);
      } catch (error) {
        console.error('Error fetching sections:', error);
      }
    };

    getSections();
  }, []);

  // Fetch headers when a channel is selected
  useEffect(() => {
    if (selectedChannel) {
      const getHeaders = async () => {
        try {
          const response = await fetchHeaders(selectedChannel);
          setHeaders(response.list || []);
        } catch (error) {
          console.error('Error fetching headers:', error);
        }
      };

      getHeaders();

      // Update selected levels based on selected channel
      const levels = selectedChannel.split('_');
      setSelectedLevels(levels);
    } else {
      setHeaders([]);
      setSelectedLevels([]);
    }
  }, [selectedChannel]);

  // Fetch combinations when a header is selected
  useEffect(() => {
    if (selectedChannel && selectedHeader) {
      const getCombinations = async () => {
        try {
          const response = await fetchCombinations(selectedChannel, selectedHeader);
          setCombinations(response.list || []);
        } catch (error) {
          console.error('Error fetching combinations:', error);
        }
      };

      getCombinations();
    } else {
      setCombinations([]);
    }
  }, [selectedChannel, selectedHeader]);

  // Update local state when selectedSection prop changes
  useEffect(() => {
    if (selectedSection) {
      const levels = selectedSection.split('_');
      setSelectedSectionLevels(levels);
    } else {
      setSelectedSectionLevels([]);
    }
  }, [selectedSection]);

  // Close navigation panel when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (navigationRef.current && !navigationRef.current.contains(event.target)) {
        setShowNavigationPanel(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const toggleNavigationPanel = (panelType) => {
    if (showNavigationPanel && activePanelType === panelType) {
      setShowNavigationPanel(false);
    } else {
      setShowNavigationPanel(true);
      setActivePanelType(panelType);
    }
  };

  const handleLevelSelect = (level, index) => {
    // Create a full channel path up to the selected level
    const newSelectedLevels = [...selectedLevels];
    newSelectedLevels[index] = level.name;

    // Trim any levels after the selected index
    const trimmedLevels = newSelectedLevels.slice(0, index + 1);
    const newPath = trimmedLevels.join('_');

    // Update selected levels first
    setSelectedLevels(trimmedLevels);

    // Find the full channel that matches this path
    const channel = channels.find(c => c.채널키 === newPath);

    if (channel) {
      onChannelSelect(channel.채널키);
      setShowNavigationPanel(false);
    } else {
      // If it's a partial path, we keep the navigation panel open
      // to show lower levels without navigating away
      const matchingChannel = channels.find(c => c.채널키.startsWith(newPath + '_'));
      if (matchingChannel) {
        // Don't call onChannelSelect yet, just update the local selectedLevels
        // This allows users to see the next level before committing to a navigation
      } else {
        // If no matching channels at all, close the panel
        setShowNavigationPanel(false);
      }
    }
  };

  const handleSectionLevelSelect = (level, index) => {
    // Create a full section path up to the selected level
    const newSelectedLevels = [...selectedSectionLevels];
    newSelectedLevels[index] = level.name;

    // Trim any levels after the selected index
    const trimmedLevels = newSelectedLevels.slice(0, index + 1);
    const newPath = trimmedLevels.join('_');

    // Update selected levels first
    setSelectedSectionLevels(trimmedLevels);

    // Find the full section that matches this path
    const section = sections.find(s => s.섹션키 === newPath);

    if (section) {
      onSectionSelect(section.섹션키);
      setShowNavigationPanel(false);
    } else {
      // If it's a partial path, we keep the navigation panel open
      // to show lower levels without navigating away
      const matchingSection = sections.find(s => s.섹션키.startsWith(newPath + '_'));
      if (matchingSection) {
        // Don't call onSectionSelect yet, just update the local selectedSectionLevels
        // This allows users to see the next level before committing to a navigation
      } else {
        // If no matching sections at all, close the panel
        setShowNavigationPanel(false);
      }
    }
  };

  // eslint-disable-next-line no-unused-vars
  const getFilteredLevelItems = (levelIndex) => {
    if (!channelLevels[levelIndex]) return [];

    // For the first level, return all items
    if (levelIndex === 0) {
      return channelLevels[0];
    }

    // For deeper levels, filter based on parent path
    const parentPath = selectedLevels.slice(0, levelIndex).join('_');
    return channelLevels[levelIndex].filter(item => item.parentPath === parentPath);
  };

  const getFilteredSectionLevelItems = (levelIndex) => {
    if (!sectionLevels[levelIndex]) return [];

    // For the first level, return all items
    if (levelIndex === 0) {
      return sectionLevels[0];
    }

    // For deeper levels, filter based on parent path
    const parentPath = selectedSectionLevels.slice(0, levelIndex).join('_');
    return sectionLevels[levelIndex].filter(item => item.parentPath === parentPath);
  };

  const renderChannelPath = () => {
    if (!selectedChannel) {
      return (
        <div className="nav-section-button" onClick={() => toggleNavigationPanel('channel')} style={{ width: '100%' }}>
          <div className="nav-section-label">채널:</div>
          <div className="nav-section-value">선택해주세요</div>
          <span className="dropdown-arrow">▼</span>
        </div>
      );
    }

    return (
      <div className="nav-section-button" onClick={() => toggleNavigationPanel('channel')} style={{ width: '100%' }}>
        <div className="nav-section-label">채널:</div>
        <div className="nav-section-path" style={{ flex: '1 1 auto' }}>
          {selectedLevels.map((level, index) => (
            <React.Fragment key={index}>
              {index > 0 && <span className="path-separator">&gt;</span>}
              <div className="path-level">
                {level}
              </div>
            </React.Fragment>
          ))}
        </div>
        <span className="dropdown-arrow">▼</span>
      </div>
    );
  };

  const renderSectionPath = () => {
    if (!selectedSection) {
      return (
        <div className="nav-section-button" onClick={() => toggleNavigationPanel('section')} style={{ width: '100%' }}>
          <div className="nav-section-label">섹션:</div>
          <div className="nav-section-value">선택해주세요</div>
          <span className="dropdown-arrow">▼</span>
        </div>
      );
    }

    return (
      <div className="nav-section-button" onClick={() => toggleNavigationPanel('section')} style={{ width: '100%' }}>
        <div className="nav-section-label">섹션:</div>
        <div className="nav-section-path" style={{ flex: '1 1 auto' }}>
          {selectedSectionLevels.map((level, index) => (
            <React.Fragment key={index}>
              {index > 0 && <span className="path-separator">&gt;</span>}
              <div className="path-level">
                {level}
              </div>
            </React.Fragment>
          ))}
        </div>
        <span className="dropdown-arrow">▼</span>
      </div>
    );
  };

  const toggleViewMode = () => {
    const newMode = viewMode === 'edit' ? 'browse' : 'edit';
    onViewModeChange(newMode);
  };

  const renderNavigationPanel = () => {
    if (activePanelType === 'channel') {
      return (
        <div className="navigation-panel">
          <div className="level-columns">
            {/* Render columns for each level */}
            {Object.keys(channelLevels).map((levelIndex) => {
              const index = parseInt(levelIndex);
              let items = [];

              if (index === 0) {
                items = channelLevels[0] || [];
              } else if (index <= selectedLevels.length) {
                const parentPath = selectedLevels.slice(0, index).join('_');
                items = channelLevels[index] ? 
                  channelLevels[index].filter(item => item.parentPath === parentPath) : 
                  [];
              }

              // Only render the column if there are items
              if (items.length === 0) return null;

              return (
                <div className="level-column" key={index}>
                  <div className="level-column-header">
                    채널레벨 {index + 1}
                  </div>
                  <div className="level-column-items">
                    {items.map((item, itemIndex) => (
                      <div 
                        key={itemIndex}
                        className={`level-item ${selectedLevels[index] === item.name ? 'selected' : ''}`}
                        onClick={() => handleLevelSelect(item, index)}
                      >
                        {item.name}
                      </div>
                    ))}
                  </div>
                </div>
              );
            })}

            {/* Add a button to complete navigation when ready */}
            {selectedLevels.length > 0 && (
              <div className="level-column">
                <div className="level-column-header">
                  작업
                </div>
                <div 
                  className="level-item action-button"
                  onClick={() => {
                    const path = selectedLevels.join('_');
                    onChannelSelect(path);
                    setShowNavigationPanel(false);
                  }}
                >
                  이 경로로 이동
                </div>
              </div>
            )}
          </div>
        </div>
      );
    } else if (activePanelType === 'section') {
      return (
        <div className="navigation-panel">
          <div className="level-columns">
            {/* Render columns for each section level */}
            {Object.keys(sectionLevels).map((levelIndex) => {
              const index = parseInt(levelIndex);
              let items = [];

              if (index === 0) {
                items = sectionLevels[0] || [];
              } else if (index <= selectedSectionLevels.length) {
                const parentPath = selectedSectionLevels.slice(0, index).join('_');
                items = sectionLevels[index] ? 
                  sectionLevels[index].filter(item => item.parentPath === parentPath) : 
                  [];
              }

              // Only render the column if there are items
              if (items.length === 0) return null;

              return (
                <div className="level-column" key={index}>
                  <div className="level-column-header">
                    섹션레벨 {index + 1}
                  </div>
                  <div className="level-column-items">
                    {items.map((item, itemIndex) => (
                      <div 
                        key={itemIndex}
                        className={`level-item ${selectedSectionLevels[index] === item.name ? 'selected' : ''}`}
                        onClick={() => handleSectionLevelSelect(item, index)}
                      >
                        {item.name}
                      </div>
                    ))}
                  </div>
                </div>
              );
            })}

            {/* Add a button to complete section navigation when ready */}
            {selectedSectionLevels.length > 0 && (
              <div className="level-column">
                <div className="level-column-header">
                  작업
                </div>
                <div 
                  className="level-item action-button"
                  onClick={() => {
                    const path = selectedSectionLevels.join('_');
                    onSectionSelect(path);
                    setShowNavigationPanel(false);
                  }}
                >
                  이 섹션으로 이동
                </div>
              </div>
            )}
          </div>
        </div>
      );
    }

    return null;
  };

  return (
    <div className="path-navigation" ref={navigationRef}>
      <div className="navigation-container">
        <div className="navigation-sections">
          <div className="nav-section" style={{ flex: '1 1 auto' }}>
            {renderChannelPath()}
          </div>

          <div className="nav-section">
            {renderSectionPath()}
          </div>

          <div className="nav-section mode-toggle">
            <div className="nav-section-button" onClick={toggleViewMode}>
              <div className="nav-section-value">
                {viewMode === 'edit' ? 'Edit' : 'Browse'}
              </div>
              <span className="mode-icon">{viewMode === 'edit' ? '✏️' : '👁️'}</span>
            </div>
          </div>
        </div>

        {showNavigationPanel && renderNavigationPanel()}
      </div>
    </div>
  );
};

export default PathNavigation;
