import React, { useState, useEffect } from 'react';
import './WorkspaceColumn.css';

const WorkspaceColumn = ({ selectedFile, fileContent }) => {
  const [selectedTab, setSelectedTab] = useState('view');
  const [content, setContent] = useState('');
  const [tableData, setTableData] = useState({
    headers: [],
    rows: []
  });
  const [processedData, setProcessedData] = useState({
    headers: [],
    rows: []
  });
  const [showProcessed, setShowProcessed] = useState(false);

  useEffect(() => {
    // Reset content when file changes
    if (selectedFile) {
      if (selectedFile.type === 'xlsx_header') {
        // Handle XLSX header selection
        const { sheetData, headerIndex } = selectedFile;

        // Extract the column data for this header
        const headerName = sheetData.headers[headerIndex];
        const columnData = sheetData.rows.map(row => row[headerIndex]);

        // Set the table data for display
        setTableData({
          headers: [headerName],
          rows: columnData.map(value => [value])
        });

        // Reset processed data
        setProcessedData({
          headers: [],
          rows: []
        });

        // Reset view state
        setShowProcessed(false);

        // Switch to view tab
        setSelectedTab('view');

        // Set content to empty to use table view instead
        setContent('');
      } else if (selectedFile.type === 'file') {
        // Regular file selected, show file info
        setContent(JSON.stringify({
          fileName: selectedFile.name,
          path: selectedFile.path,
          type: selectedFile.type
        }, null, 2));

        // Clear table data
        setTableData({
          headers: [],
          rows: []
        });

        // Reset processed data
        setProcessedData({
          headers: [],
          rows: []
        });

        // Reset view state
        setShowProcessed(false);
      } else if (selectedFile.type === 'xlsx_sheet') {
        // Sheet selected, show all data from the sheet
        const { sheetData } = selectedFile;

        setTableData({
          headers: sheetData.headers,
          rows: sheetData.rows
        });

        // Reset processed data
        setProcessedData({
          headers: [],
          rows: []
        });

        // Reset view state
        setShowProcessed(false);

        // Switch to view tab
        setSelectedTab('view');

        // Set content to empty to use table view instead
        setContent('');
      }
    } else {
      // No file selected
      setContent(JSON.stringify({
        message: "No file selected",
        instruction: "Select a file or header from the file explorer"
      }, null, 2));

      // Clear table data
      setTableData({
        headers: [],
        rows: []
      });

      // Reset processed data
      setProcessedData({
        headers: [],
        rows: []
      });

      // Reset view state
      setShowProcessed(false);
    }
  }, [selectedFile]);

  const handleTabChange = (tab) => {
    setSelectedTab(tab);
  };

  // 분리분류 함수: 값을 분리하고 분류합니다
  const handleMagicSplit = () => {
    if (!tableData.headers.length || tableData.headers.length !== 1) {
      // 헤더가 하나가 아닌 경우 처리할 수 없음
      alert('분리분류 기능은 단일 컬럼 데이터에서만 사용할 수 있습니다.');
      return;
    }

    // 원본 데이터의 첫 번째 (그리고 유일한) 컬럼을 가져옵니다
    const originalHeader = tableData.headers[0];
    const originalValues = tableData.rows.map(row => row[0]);

    // 모든 고유한 토큰을 저장할 Set
    const uniqueTokensSet = new Set();

    // 각 행별 토큰을 저장
    const rowTokensMap = [];

    // 원본 값들을 처리합니다
    originalValues.forEach((value, rowIndex) => {
      if (value === null || value === undefined) {
        rowTokensMap[rowIndex] = new Set();
        return;
      }

      // 문자열로 변환
      const strValue = String(value);

      // 이 행의 토큰들을 저장할 Set
      const rowTokens = new Set();
      rowTokensMap[rowIndex] = rowTokens;

      // 0) 괄호() 처리: 괄호 안의 내용을 추출 (다른 종류의 괄호는 무시)
      // 정규식은 소괄호() 내용만 추출
      const bracketResults = strValue.match(/\(([^)]+)\)/g);

      let valueWithoutBrackets = strValue;
      if (bracketResults) {
        bracketResults.forEach((bracket) => {
          // 괄호 내용만 가져오기
          const innerContent = bracket.slice(1, -1).trim();

          // 원래 텍스트에서 괄호 제거
          valueWithoutBrackets = valueWithoutBrackets.replace(bracket, ' ');
        });
      }

      // 1) 공백, 쉼표로 분리
      const segments = valueWithoutBrackets
        .split(/[\s,]+/)
        .map(segment => segment.trim())
        .filter(segment => segment);

      // 분리된 각 세그먼트 저장
      segments.forEach((segment) => {
        rowTokens.add(segment);
        uniqueTokensSet.add(segment);
      });
    });

    // 토큰을 뒷글자부터 앞글자순으로 정렬
    const sortedTokens = Array.from(uniqueTokensSet).sort((a, b) => {
      // 뒷글자부터 앞글자순 정렬
      return a.split('').reverse().join('').localeCompare(b.split('').reverse().join(''));
    });

    // 분절값들을 행으로 변환하여 결과 구성
    const newRows = sortedTokens.map(token => {
      // 각 토큰(분절값)에 대한 행 생성
      return [
        token, // 첫 번째 열은 분절값
        ...originalValues.map((_, rowIdx) => {
          // 각 원본 데이터에 이 토큰이 있는지 확인
          return rowTokensMap[rowIdx]?.has(token) ? 'O' : '';
        })
      ];
    });

    // 새로운 헤더 생성 (첫 번째는 분절값, 나머지는 각 행 번호)
    const newHeaders = [
      '분절값',
      ...originalValues.map((_, idx) => `데이터 ${idx + 1}`)
    ];

    // 처리된 데이터 설정
    setProcessedData({
      headers: newHeaders,
      rows: newRows
    });

    // 처리된 뷰 표시
    setShowProcessed(true);
  };

  const renderTable = () => {
    // 표시할 데이터 선택
    const displayData = showProcessed ? processedData : tableData;

    if (!displayData.headers.length) return null;

    return (
      <div className="data-table-container">
        <h4>{selectedFile?.name} - {showProcessed ? '분리분류 결과' : '데이터 뷰'}</h4>

        {/* 원본 데이터로 돌아가는 버튼 (처리된 데이터를 보는 경우에만) */}
        {showProcessed && (
          <button 
            className="return-to-original" 
            onClick={() => setShowProcessed(false)}
          >
            원본 데이터로 돌아가기
          </button>
        )}

        <table className="data-table">
          <thead>
            <tr>
              {displayData.headers.map((header, idx) => (
                <th key={idx}>{header}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {displayData.rows.map((row, rowIdx) => (
              <tr key={rowIdx}>
                {row.map((cell, cellIdx) => (
                  <td key={cellIdx}>{cell !== undefined && cell !== null ? String(cell) : ''}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="column workspace-column">
      <div className="toolbar">
        <div className="tabs">
          <button 
            className={`tab ${selectedTab === 'view' ? 'active' : ''}`} 
            onClick={() => handleTabChange('view')}
          >
            View
          </button>
          <button 
            className={`tab ${selectedTab === 'split' ? 'active' : ''}`} 
            onClick={() => handleTabChange('split')}
          >
            Split
          </button>
          <button 
            className={`tab ${selectedTab === 'segment' ? 'active' : ''}`} 
            onClick={() => handleTabChange('segment')}
          >
            Segment
          </button>
          <button 
            className={`tab ${selectedTab === 'classify' ? 'active' : ''}`} 
            onClick={() => handleTabChange('classify')}
          >
            Classify
          </button>
        </div>
        <div className="actions">
          {selectedFile?.type === 'xlsx_header' && (
            <button 
              className="action-button magic-split"
              onClick={handleMagicSplit}
              title="분리분류 마법"
            >
              <span>✨</span>
            </button>
          )}
          <button className="action-button">
            <span>⚙️</span>
          </button>
          <button className="action-button">
            <span>💾</span>
          </button>
        </div>
      </div>

      <div className="content-area">
        {selectedTab === 'view' && (
          <div className="view-mode">
            {(tableData.headers.length > 0 || processedData.headers.length > 0) ? (
              renderTable()
            ) : (
              <pre className="json-viewer">{content}</pre>
            )}
          </div>
        )}

        {selectedTab === 'split' && (
          <div className="split-mode">
            <h4>Split Data</h4>
            <div className="split-options">
              <div className="option">
                <label>Split by:</label>
                <select>
                  <option>Key</option>
                  <option>Value</option>
                  <option>Array elements</option>
                </select>
              </div>
              <div className="option">
                <label>Target path:</label>
                <input type="text" placeholder="/properties/items" />
              </div>
              <button className="apply-button">Apply Split</button>
            </div>
            <div className="preview-area">
              <h5>Preview</h5>
              <div className="preview-content">Split preview will appear here</div>
            </div>
          </div>
        )}

        {selectedTab === 'segment' && (
          <div className="segment-mode">
            <h4>Segmentation Options</h4>
            <p>Configure how to segment your data</p>
            <div className="segment-options">
              <div className="option">
                <label>Segment method:</label>
                <select>
                  <option>By structure</option>
                  <option>By semantic content</option>
                  <option>By size</option>
                </select>
              </div>
              <button className="apply-button">Apply Segmentation</button>
            </div>
          </div>
        )}

        {selectedTab === 'classify' && (
          <div className="classify-mode">
            <h4>Classification Tools</h4>
            <p>Classify data based on rules or patterns</p>
            <div className="classify-options">
              <div className="option">
                <label>Classification type:</label>
                <select>
                  <option>Type detection</option>
                  <option>Pattern matching</option>
                  <option>Semantic grouping</option>
                </select>
              </div>
              <button className="apply-button">Run Classification</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default WorkspaceColumn;
