import React, { useState, useEffect } from 'react';
import { fetchHeaderList } from '../services/api';
import './HeaderList.css';

const HeaderList = ({ channel, onHeaderSelect }) => {
  const [headers, setHeaders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedHeader, setSelectedHeader] = useState(null);

  useEffect(() => {
    const getHeaders = async () => {
      if (!channel) return;

      setLoading(true);
      try {
        const response = await fetchHeaderList(channel);
        console.log('Header list response:', response);
        setHeaders(response.list || []);
        setError(null);
      } catch (err) {
        setError(err.message);
        setHeaders([]);
      } finally {
        setLoading(false);
      }
    };

    getHeaders();
    // Reset selected header when changing channel
    setSelectedHeader(null);
  }, [channel]);

  const handleHeaderClick = (header) => {
    setSelectedHeader(header);
    if (onHeaderSelect) {
      onHeaderSelect(header);
    }
  };

  if (!channel) return <div className="no-selection">채널을 선택해주세요.</div>;
  if (loading) return <div className="loading">헤더 목록 로딩 중...</div>;
  if (error) return <div className="error">에러: {error}</div>;

  return (
    <div className="header-list-container">
      <h2>헤더 목록: {channel}</h2>
      <div className="header-list">
        {headers && headers.length > 0 ? (
          headers.map((item, index) => (
            <div 
              key={index} 
              className={`header-item ${selectedHeader === item.header ? 'selected' : ''}`}
              onClick={() => handleHeaderClick(item.header)}
            >
              {item.header}
            </div>
          ))
        ) : (
          <div className="no-headers">헤더 정보가 없습니다.</div>
        )}
      </div>
    </div>
  );
};

export default HeaderList;
