import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ChannelList.css';

const ChannelList = () => {
  const [channels, setChannels] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchChannels = async () => {
      try {
        let config = {
          method: 'get',
          maxBodyLength: Infinity,
          url: 'https://api-backend.nventure.co.kr/solution/crawlChannelList',
          headers: { 
            'Authorization': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..BGbPFVbUSF04rnPA7p23kB1uaIY_ouX9KOVxGYyxnxE'
          }
        };

        const response = await axios.request(config);
        setChannels(response.data.result);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchChannels();
  }, []);

  if (loading) return <div className="loading">로딩 중...</div>;
  if (error) return <div className="error">에러: {error}</div>;

  return (
    <div className="channel-list-container">
      <h2>채널 목록</h2>
      <div className="channel-list">
        {channels && channels.length > 0 ? (
          channels.map((channel) => (
            <div key={channel.id || channel._id} className="channel-item">
              {channel.thumbnail && (
                <img 
                  src={channel.thumbnail} 
                  alt={channel.name || '채널 썸네일'} 
                  className="channel-thumbnail" 
                />
              )}
              <div className="channel-info">
                <h3>{channel.name || 'Unknown Channel'}</h3>
                {channel.description && <p>{channel.description}</p>}
                {channel.url && (
                  <a href={channel.url} target="_blank" rel="noopener noreferrer">
                    채널 방문
                  </a>
                )}
              </div>
            </div>
          ))
        ) : (
          <div className="no-channels">채널 정보가 없습니다.</div>
        )}
      </div>
    </div>
  );
};

export default ChannelList;
