import React, { useState, useEffect } from 'react';
import './WorkspaceColumn.css';

const WorkspaceColumn = ({ selectedFile, fileContent }) => {
  const [selectedTab, setSelectedTab] = useState('view');
  const [content, setContent] = useState('');
  const [tableData, setTableData] = useState({
    headers: [],
    rows: []
  });
  const [originalTableData, setOriginalTableData] = useState(null);

  useEffect(() => {
    // Reset content when file changes
    if (selectedFile) {
      if (selectedFile.type === 'xlsx_header') {
        // Handle XLSX header selection
        const { sheetData, headerIndex } = selectedFile;

        // Extract the column data for this header
        const headerName = sheetData.headers[headerIndex];
        const columnData = sheetData.rows.map(row => row[headerIndex]);

        // Set the table data for display
        const newTableData = {
          headers: [headerName],
          rows: columnData.map(value => [value])
        };

        setTableData(newTableData);
        setOriginalTableData({...newTableData});

        // Switch to view tab
        setSelectedTab('view');

        // Set content to empty to use table view instead
        setContent('');
      } else if (selectedFile.type === 'file') {
        // Regular file selected, show file info
        setContent(JSON.stringify({
          fileName: selectedFile.name,
          path: selectedFile.path,
          type: selectedFile.type
        }, null, 2));

        // Clear table data
        setTableData({
          headers: [],
          rows: []
        });
        setOriginalTableData(null);
      } else if (selectedFile.type === 'xlsx_sheet') {
        // Sheet selected, show all data from the sheet
        const { sheetData } = selectedFile;

        const newTableData = {
          headers: sheetData.headers,
          rows: sheetData.rows
        };

        setTableData(newTableData);
        setOriginalTableData({...newTableData});

        // Switch to view tab
        setSelectedTab('view');

        // Set content to empty to use table view instead
        setContent('');
      }
    } else {
      // No file selected
      setContent(JSON.stringify({
        message: "No file selected",
        instruction: "Select a file or header from the file explorer"
      }, null, 2));

      // Clear table data
      setTableData({
        headers: [],
        rows: []
      });
      setOriginalTableData(null);
    }
  }, [selectedFile]);

  const handleTabChange = (tab) => {
    setSelectedTab(tab);
  };

  // Î∂ÑÎ¶¨Î∂ÑÎ•ò Í∏∞Îä• Ï≤òÎ¶¨ Ìï®Ïàò
  const handleSeparationMagic = () => {
    if (!tableData.headers.length || !tableData.rows.length) return;

    // ÏõêÎ≥∏ Îç∞Ïù¥ÌÑ∞Í∞Ä ÏûàÎäî Í≤ΩÏö∞ ÌôúÏö© (Î¶¨ÏÖã Í∞ÄÎä•ÌïòÎèÑÎ°ù)
    const dataToProcess = originalTableData ? [...originalTableData.rows] : [...tableData.rows];

    // Ï§ëÎ≥µ Ï†úÍ±∞Î•º ÏúÑÌïú Set Í∞ùÏ≤¥Îì§
    const bracketSet = new Set();
    const valueSet = new Set();

    // Í∞Å Ìñâ Ï≤òÎ¶¨
    dataToProcess.forEach(row => {
      const cellValue = row[0];

      // null, undefined, Îπà Î¨∏ÏûêÏó¥ Îì± Í±¥ÎÑàÎõ∞Í∏∞
      if (cellValue === null || cellValue === undefined || String(cellValue).trim() === '') {
        return;
      }

      const cellValueStr = String(cellValue);

      // 1. Í¥ÑÌò∏ ÎÇ¥Ïö© Ï≤òÎ¶¨ - Îã§ÏñëÌïú Í¥ÑÌò∏ Ïú†Ìòï Ï≤òÎ¶¨ (ÏÜåÍ¥ÑÌò∏, ÎåÄÍ¥ÑÌò∏, Ï§ëÍ¥ÑÌò∏)
      const bracketContentRegex = /\(([^)]*)\)|\[([^\]]*)\]|\{([^}]*)\}/g;
      let textWithoutBrackets = cellValueStr.replace(bracketContentRegex, (match, p1, p2, p3) => {
        const content = p1 || p2 || p3; // Í¥ÑÌò∏ Ï¢ÖÎ•òÏóê Îî∞Îùº p1, p2, p3 Ï§ë ÌïòÎÇòÏóê Í∞íÏù¥ ÏûàÏùå
        if (content && content.trim()) {
          // Ï§ëÎ≥µ Ï†úÍ±∞Î•º ÏúÑÌï¥ SetÏóê Ï∂îÍ∞Ä
          bracketSet.add(content.trim());
        }
        return ' '; // Í¥ÑÌò∏ÏôÄ ÎÇ¥Ïö©ÏùÑ Í≥µÎ∞±ÏúºÎ°ú ÎåÄÏ≤¥
      });

      // 2. Í≥µÎ∞±Í≥º ÏâºÌëúÎ°ú Î∂ÑÎ¶¨
      const separatedValues = textWithoutBrackets
        .split(/[\s,]+/) // Í≥µÎ∞±Í≥º ÏâºÌëúÎ°ú Î∂ÑÎ¶¨
        .map(value => value.trim())
        .filter(value => value.length > 0); // Îπà Î¨∏ÏûêÏó¥ Ï†úÍ±∞

      // Ï§ëÎ≥µ Ï†úÍ±∞Î•º ÏúÑÌï¥ SetÏóê Ï∂îÍ∞Ä
      separatedValues.forEach(value => {
        if (value && value.trim()) {
          valueSet.add(value.trim());
        }
      });
    });

    // 3. Í≤∞Í≥º Ìñâ ÏÉùÏÑ± (Ï§ëÎ≥µ Ï†úÍ±∞Îêú Í∞íÎì§)
    let resultRows = [];

    // Í¥ÑÌò∏ ÎÇ¥Ïö© Ï∂îÍ∞Ä (Ï§ëÎ≥µ Ï†úÍ±∞Îê®)
    bracketSet.forEach(content => {
      resultRows.push([`[Í¥ÑÌò∏] ${content}`]);
    });

    // Î∂ÑÎ¶¨Îêú ÏùºÎ∞ò Í∞í Ï∂îÍ∞Ä (Ï§ëÎ≥µ Ï†úÍ±∞Îê®)
    valueSet.forEach(value => {
      resultRows.push([value]);
    });

    // Í≤∞Í≥ºÍ∞Ä ÏóÜÏúºÎ©¥ "Î∂ÑÎ¶¨Îêú Í∞íÏù¥ ÏóÜÏäµÎãàÎã§" ÌëúÏãú
    if (resultRows.length === 0) {
      resultRows.push(["Î∂ÑÎ¶¨Îêú Í∞íÏù¥ ÏóÜÏäµÎãàÎã§"]);
    }

    // 4. Ï†ïÎ†¨ 
    // Î®ºÏ†Ä Í¥ÑÌò∏ ÎÇ¥Ïö©Í≥º ÏùºÎ∞ò Í∞íÏúºÎ°ú Íµ¨Î∂Ñ
    const bracketRows = resultRows.filter(row => String(row[0]).startsWith('[Í¥ÑÌò∏]'));
    const normalRows = resultRows.filter(row => !String(row[0]).startsWith('[Í¥ÑÌò∏]'));

    // Î¨∏ÏûêÏó¥ Îí§ÏßëÍ∏∞ Ìï®Ïàò
    const reverseString = str => str.split('').reverse().join('');

    // Îí∑Í∏ÄÏûêÎ∂ÄÌÑ∞ Ï†ïÎ†¨ (Î¨∏ÏûêÏó¥ÏùÑ Îí§ÏßëÏñ¥ÏÑú ÎπÑÍµê)
    bracketRows.sort((a, b) => {
      const aValue = String(a[0]).replace('[Í¥ÑÌò∏] ', '');
      const bValue = String(b[0]).replace('[Í¥ÑÌò∏] ', '');

      return reverseString(aValue).localeCompare(reverseString(bValue));
    });

    normalRows.sort((a, b) => {
      return reverseString(String(a[0])).localeCompare(reverseString(String(b[0])));
    });

    // 5. Îí∑Í∏ÄÏûê Í∏∞Ï§Ä Í≤πÏπòÎäî Î∂ÄÎ∂Ñ Î¨∏ÏûêÏó¥ Ï∞æÍ∏∞
    // Í¥ÑÌò∏ ÎÇ¥Ïö© Í∑∏Î£πÌôî
    const findOverlappingSuffix = (rows) => {
      if (rows.length <= 1) return rows.map(row => ["", row[0]]);

      const processed = [];

      for (let i = 0; i < rows.length; i++) {
        const currentValue = String(rows[i][0]);
        let currentReversed = reverseString(currentValue.replace('[Í¥ÑÌò∏] ', ''));

        // Í≤πÏπòÎäî Î∂ÄÎ∂Ñ Ï∞æÍ∏∞ (ÌòÑÏû¨ Ìï≠Î™©Í≥º Ïù¥Ï†Ñ Ìï≠Î™© ÎπÑÍµê)
        let commonSuffix = "";

        if (i > 0) {
          const prevValue = String(rows[i-1][0]);
          let prevReversed = reverseString(prevValue.replace('[Í¥ÑÌò∏] ', ''));

          // Í≥µÌÜµÎêú Îí∑Î∂ÄÎ∂Ñ Ï∞æÍ∏∞
          let j = 0;
          while (j < currentReversed.length && 
                j < prevReversed.length && 
                currentReversed[j] === prevReversed[j]) {
            j++;
          }

          if (j > 0) {
            // 2Í∏ÄÏûê Ïù¥ÏÉÅ Í≤πÏπòÎäî Í≤ΩÏö∞Îßå ÌëúÏãú
            if (j >= 2) {
              commonSuffix = reverseString(currentReversed.substring(0, j));
            }
          }
        }

        processed.push([commonSuffix, rows[i][0]]);
      }

      return processed;
    };

    // Í¥ÑÌò∏ÏôÄ ÏùºÎ∞ò Í∞í Í∞ÅÍ∞Å Ï≤òÎ¶¨
    const processedBracketRows = findOverlappingSuffix(bracketRows);
    const processedNormalRows = findOverlappingSuffix(normalRows);

    // Í¥ÑÌò∏ ÎÇ¥Ïö©ÏùÑ ÏúÑÏóê, ÏùºÎ∞ò Í∞íÏùÑ ÏïÑÎûòÏóê Î∞∞Ïπò (2Ïó¥Î°ú Íµ¨ÏÑ±)
    const finalRows = [...processedBracketRows, ...processedNormalRows];

    // Î≥ÄÌôòÎêú Í≤∞Í≥ºÎ°ú ÌÖåÏù¥Î∏î Îç∞Ïù¥ÌÑ∞ ÏóÖÎç∞Ïù¥Ìä∏ (Ìó§ÎçîÎèÑ 2Í∞úÎ°ú Î≥ÄÍ≤Ω)
    setTableData({
      headers: ["Í≤πÏπòÎäî Î∂ÄÎ∂Ñ", tableData.headers[0]],
      rows: finalRows
    });
  };

  // ÏõêÎ≥∏ Îç∞Ïù¥ÌÑ∞Î°ú Î≥µÏõêÌïòÎäî Ìï®Ïàò
  const resetToOriginal = () => {
    if (originalTableData) {
      setTableData({...originalTableData});
    }
  };

  const renderTable = () => {
    if (!tableData.headers.length) return null;

    return (
      <div className="data-table-container">
        <h4>{selectedFile?.name} - Data View</h4>
        <table className="data-table">
          <thead>
            <tr>
              {tableData.headers.map((header, idx) => (
                <th key={idx}>{header}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {tableData.rows.map((row, rowIdx) => (
              <tr key={rowIdx}>
                {row.map((cell, cellIdx) => {
                  // Ï≤´ Î≤àÏß∏ ÏÖÄÏùÄ Í≤πÏπòÎäî Î∂ÄÎ∂Ñ
                  if (cellIdx === 0) {
                    return (
                      <td key={cellIdx} className="overlap-cell">
                        {cell}
                      </td>
                    );
                  }

                  // Îëê Î≤àÏß∏ ÏÖÄÏùÄ ÏõêÎûò ÎÇ¥Ïö©
                  // Í¥ÑÌò∏ ÎÇ¥Ïö© Ïó¨Î∂Ä ÌôïÏù∏
                  const isBracketContent = String(cell).startsWith('[Í¥ÑÌò∏]');
                  let displayContent = cell;

                  // Í¥ÑÌò∏ ÎÇ¥Ïö©Ïù∏ Í≤ΩÏö∞ ÌäπÎ≥Ñ Ï≤òÎ¶¨
                  if (isBracketContent) {
                    displayContent = (
                      <>
                        <span className="bracket-indicator">‚äÇ‚äÉ</span>
                        {String(cell).replace('[Í¥ÑÌò∏] ', '')}
                      </>
                    );
                  }

                  return (
                    <td 
                      key={cellIdx} 
                      data-bracket-content={isBracketContent ? "true" : "false"}
                    >
                      {displayContent}
                    </td>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="column workspace-column">
      <div className="toolbar">
        <div className="tabs">
          <button 
            className={`tab ${selectedTab === 'view' ? 'active' : ''}`} 
            onClick={() => handleTabChange('view')}
          >
            View
          </button>
          <button 
            className={`tab ${selectedTab === 'split' ? 'active' : ''}`} 
            onClick={() => handleTabChange('split')}
          >
            Split
          </button>
          <button 
            className={`tab ${selectedTab === 'segment' ? 'active' : ''}`} 
            onClick={() => handleTabChange('segment')}
          >
            Segment
          </button>
          <button 
            className={`tab ${selectedTab === 'classify' ? 'active' : ''}`} 
            onClick={() => handleTabChange('classify')}
          >
            Classify
          </button>
        </div>
        <div className="actions">
          {tableData.headers.length > 0 && (
            <>
              <button 
                className="action-button magic-button" 
                title="Î∂ÑÎ¶¨Î∂ÑÎ•ò ÎßàÎ≤ï - Í¥ÑÌò∏ ÎÇ¥Ïö© Î∂ÑÎ¶¨ Î∞è Í≥µÎ∞±/ÏâºÌëú Í∏∞Ï§Ä Î∂ÑÎ¶¨"
                onClick={handleSeparationMagic}
              >
                <span>‚ú®</span>
              </button>
              <button 
                className="action-button reset-button" 
                title="ÏõêÎ≥∏ Îç∞Ïù¥ÌÑ∞Î°ú Î≥µÏõê"
                onClick={resetToOriginal}
              >
                <span>üîÑ</span>
              </button>
            </>
          )}
          <button className="action-button">
            <span>‚öôÔ∏è</span>
          </button>
          <button className="action-button">
            <span>üíæ</span>
          </button>
        </div>
      </div>

      <div className="content-area">
        {selectedTab === 'view' && (
          <div className="view-mode">
            {tableData.headers.length > 0 ? (
              renderTable()
            ) : (
              <pre className="json-viewer">{content}</pre>
            )}
          </div>
        )}

        {selectedTab === 'split' && (
          <div className="split-mode">
            <h4>Split Data</h4>
            <div className="split-options">
              <div className="option">
                <label>Split by:</label>
                <select>
                  <option>Key</option>
                  <option>Value</option>
                  <option>Array elements</option>
                </select>
              </div>
              <div className="option">
                <label>Target path:</label>
                <input type="text" placeholder="/properties/items" />
              </div>
              <button className="apply-button">Apply Split</button>
            </div>
            <div className="preview-area">
              <h5>Preview</h5>
              <div className="preview-content">Split preview will appear here</div>
            </div>
          </div>
        )}

        {selectedTab === 'segment' && (
          <div className="segment-mode">
            <h4>Segmentation Options</h4>
            <p>Configure how to segment your data</p>
            <div className="segment-options">
              <div className="option">
                <label>Segment method:</label>
                <select>
                  <option>By structure</option>
                  <option>By semantic content</option>
                  <option>By size</option>
                </select>
              </div>
              <button className="apply-button">Apply Segmentation</button>
            </div>
          </div>
        )}

        {selectedTab === 'classify' && (
          <div className="classify-mode">
            <h4>Classification Tools</h4>
            <p>Classify data based on rules or patterns</p>
            <div className="classify-options">
              <div className="option">
                <label>Classification type:</label>
                <select>
                  <option>Type detection</option>
                  <option>Pattern matching</option>
                  <option>Semantic grouping</option>
                </select>
              </div>
              <button className="apply-button">Run Classification</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default WorkspaceColumn;
