import React, { useState, useEffect } from 'react';

const SeparationClassifier = ({ data, onReset }) => {
  const [processedData, setProcessedData] = useState(null);
  const [pipeline, setPipeline] = useState([
    { id: 'bracket-separation', name: '괄호값 분리', active: true }
  ]);
  const [selectedStep, setSelectedStep] = useState('bracket-separation');

  // Process data through pipeline steps
  useEffect(() => {
    if (data) {
      // Process data through all active pipeline steps
      let result = { ...data };

      pipeline.forEach(step => {
        if (step.active) {
          if (step.id === 'bracket-separation') {
            result = processBracketSeparation(result);
          }
          // Add more step processors here in the future
        }
      });

      setProcessedData(result);
    } else {
      setProcessedData(null);
    }
  }, [data, pipeline]);

  const processBracketSeparation = (input) => {
    if (typeof input === 'string') {
      const bracketRegex = /\[(.*?)\]|\((.*?)\)|\{(.*?)\}/g;
      const matches = [];
      let match;

      while ((match = bracketRegex.exec(input)) !== null) {
        const value = match[1] || match[2] || match[3];
        if (value) matches.push(value);
      }

      return {
        original: input,
        separated: matches,
        type: 'bracket-separation'
      };
    } else if (Array.isArray(input)) {
      return input.map(item => processBracketSeparation(item));
    } else if (typeof input === 'object' && input !== null) {
      const result = {};
      for (const key in input) {
        result[key] = processBracketSeparation(input[key]);
      }
      return result;
    }

    return input;
  };

  const toggleStepActive = (stepId) => {
    setPipeline(pipeline.map(step => 
      step.id === stepId ? { ...step, active: !step.active } : step
    ));
  };

  const selectStep = (stepId) => {
    setSelectedStep(stepId);
  };

  // Function to add a new pipeline step (for future use)
  const addPipelineStep = (stepType) => {
    // This is a placeholder for future functionality
    console.log(`Adding step of type: ${stepType}`);
  };

  // Visual data rendering
  const renderVisualData = (item) => {
    if (item && item.type === 'bracket-separation') {
      return (
        <div className="visual-data-item">
          <div className="original-container">
            <div className="data-label">원본 데이터</div>
            <div className="original-value">
              {highlightBrackets(item.original)}
            </div>
          </div>

          <div className="transformation-arrow">
            <svg viewBox="0 0 24 24" width="40" height="40">
              <path d="M4,11v2h12l-5.5,5.5l1.42,1.42L19.84,12l-7.92-7.92L10.5,5.5L16,11H4z" fill="#4285F4" />
            </svg>
          </div>

          <div className="result-container">
            <div className="data-label">분리된 값</div>
            {item.separated.length > 0 ? (
              <div className="separated-values-grid">
                {item.separated.map((val, idx) => (
                  <div key={idx} className="separated-value-card">
                    {val}
                  </div>
                ))}
              </div>
            ) : (
              <div className="no-values">분리된 값 없음</div>
            )}
          </div>
        </div>
      );
    } else if (Array.isArray(item)) {
      return (
        <div className="array-visual-container">
          <div className="data-label">배열 데이터</div>
          <div className="array-items">
            {item.map((subItem, idx) => (
              <div key={idx} className="array-item-card">
                {renderVisualData(subItem)}
              </div>
            ))}
          </div>
        </div>
      );
    } else if (typeof item === 'object' && item !== null) {
      return (
        <div className="object-visual-container">
          {Object.entries(item).map(([key, value]) => (
            <div key={key} className="object-property-card">
              <div className="property-key">{key}</div>
              <div className="property-value">
                {renderVisualData(value)}
              </div>
            </div>
          ))}
        </div>
      );
    }

    // Fallback for simple values
    return <div className="simple-value-card">{String(item)}</div>;
  };

  // Helper to highlight brackets in original text
  const highlightBrackets = (text) => {
    if (typeof text !== 'string') return text;

    // Replace brackets with highlighted spans
    const highlighted = text.replace(
      /(\[.*?\]|\(.*?\)|\{.*?\})/g, 
      '<span class="highlighted-bracket">$1</span>'
    );

    return <div dangerouslySetInnerHTML={{ __html: highlighted }} />;
  };

