import React, { useState, useEffect, useRef, forwardRef, useImperativeHandle } from 'react';
import { fetchHeaderList, searchHeaderList, fetchHeaderSimilarityList } from '../services/api';
import './Column.css';

const HeaderList = forwardRef(({ 
  channel, 
  onHeaderSelect, 
  selectedHeader, 
  similarityGroups, 
  headerSearchTerm, 
  onSimilarityGroupUpdate,
  onRemoveGroup,
  similarityViewTrigger,
  onSimilarityViewProcessed
}, ref) => {
  const [headers, setHeaders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('all'); // 'all', 'withGroup', or 'withoutGroup'
  const [headerToGroupMap, setHeaderToGroupMap] = useState({});
  const [groupedHeaders, setGroupedHeaders] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [isSearchMode, setIsSearchMode] = useState(false);

  // Added for similar header selection functionality
  const [viewMode, setViewMode] = useState('list'); // 'list' or 'similarSelection'
  const [similarHeaders, setSimilarHeaders] = useState([]);
  const [selectedSimilarityGroup, setSelectedSimilarityGroup] = useState([]);
  const [representativeHeader, setRepresentativeHeader] = useState(null);
  const [isSelectedGroupCollapsed, setIsSelectedGroupCollapsed] = useState(false);
  const [similarHeaderLoading, setSimilarHeaderLoading] = useState(false);
  const [similarHeaderError, setSimilarHeaderError] = useState(null);
  const [currentSimilarityHeader, setCurrentSimilarityHeader] = useState(null);

  // 마지막 클릭된 헤더와 타임스탬프를 저장
  const lastClickRef = useRef({ header: null, timestamp: 0 });

  // Expose functions to parent component
  useImperativeHandle(ref, () => ({
    viewSimilarHeaders: (header) => {
      handleViewSimilarHeaders(header);
    }
  }));

  // Handle headerSearchTerm updates from right-clicks in other components
  useEffect(() => {
    if (headerSearchTerm && headerSearchTerm !== searchTerm) {
      setSearchTerm(headerSearchTerm);
      setIsSearchMode(true);
    }
  }, [headerSearchTerm, searchTerm]);

  // Handle similarity view trigger from HeaderCombinationList
  useEffect(() => {
    if (similarityViewTrigger && channel) {
      handleViewSimilarHeaders(similarityViewTrigger);
      // Call the callback to reset the trigger
      if (onSimilarityViewProcessed) {
        onSimilarityViewProcessed();
      }
    }
  }, [similarityViewTrigger, channel]);

  useEffect(() => {
    if (!channel) return;

    const getHeaders = async () => {
      setLoading(true);
      try {
        let response;
        if (isSearchMode && searchTerm.trim()) {
          // Use search API with keyword
          response = await searchHeaderList(channel, searchTerm);
        } else {
          // Use regular API without keyword
          response = await fetchHeaderList(channel);
        }
        console.log('Header list response:', response);
        setHeaders(response.list || []);
        setError(null);
      } catch (err) {
        setError(err.message);
        setHeaders([]);
      } finally {
        setLoading(false);
      }
    };

    getHeaders();
  }, [channel, searchTerm, isSearchMode]);

  // Handle search form submission
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      setIsSearchMode(true);
    } else {
      setIsSearchMode(false);
    }
  };

  // Handle search input change
  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    if (!value.trim()) {
      setIsSearchMode(false);
    }
  };

  // 유사군이 변경될 때마다 관련 데이터를 업데이트
  useEffect(() => {
    console.log('HeaderList - similarityGroups 변경감지:', similarityGroups);

    // 유사군에 포함된 모든 헤더 추출
    const newGroupedHeaders = similarityGroups && similarityGroups.length > 0
      ? similarityGroups.flatMap(group => 
          // 대표어를 제외한 헤더만 포함
          group.items
            .filter(item => item.header !== group.representative)
            .map(item => item.header)
        )
      : [];

    // 각 헤더별로 유사군 매핑 생성
    const newHeaderToGroupMap = {};
    if (similarityGroups && similarityGroups.length > 0) {
      similarityGroups.forEach((group, idx) => {
        console.log(`HeaderList - 유사군 #${idx + 1} 처리:`, group.representative, group.items.length);

        // 대표어에 유사군 카운트 태그 추가
        newHeaderToGroupMap[group.representative] = {
          isRepresentative: true,
          count: group.items.length
        };

        // 대표어가 아닌 항목들은 매핑에서 제외 (목록에 표시되지 않음)
        group.items.forEach(item => {
          if (item.header !== group.representative) {
            newHeaderToGroupMap[item.header] = {
              representative: group.representative,
              count: group.items.length
            };
          }
        });
      });
    }

    console.log('HeaderList - headerToGroupMap 업데이트:', newHeaderToGroupMap);
    console.log('HeaderList - groupedHeaders 업데이트:', newGroupedHeaders);

    setGroupedHeaders(newGroupedHeaders);
    setHeaderToGroupMap(newHeaderToGroupMap);
  }, [similarityGroups]);

  // Similar header selection functions
  const handleViewSimilarHeaders = (header) => {
    if (!channel || !header) return;

    // Reset selection state
    setSelectedSimilarityGroup([]);
    setRepresentativeHeader(null);

    // Store the current header being viewed
    setCurrentSimilarityHeader(header);

    // Change view mode and fetch similar headers
    setViewMode('similarSelection');
    fetchSimilarHeaders(header);
  };

  const fetchSimilarHeaders = async (header) => {
    if (!channel || !header) return;

    setSimilarHeaderLoading(true);
    try {
      const response = await fetchHeaderSimilarityList(channel, header);
      console.log('Similar headers response:', response);
      setSimilarHeaders(response.list || []);
      setSimilarHeaderError(null);
    } catch (err) {
      setSimilarHeaderError(err.message);
      setSimilarHeaders([]);
    } finally {
      setSimilarHeaderLoading(false);
    }
  };

  const handleBackToHeaderList = () => {
    setViewMode('list');
    setSelectedSimilarityGroup([]);
    setRepresentativeHeader(null);
    setCurrentSimilarityHeader(null);
  };

  const handleSimilarHeaderSelect = (header) => {
    // 중복 클릭 방지 및 디바운싱
    const now = Date.now();
    if (lastClickRef.current.header === header && 
        now - lastClickRef.current.timestamp < 500) {
      // 0.5초 이내에 동일한 헤더를 다시 클릭한 경우 무시
      return;
    }

    // 클릭 정보 업데이트
    lastClickRef.current = { header, timestamp: now };

    // 버그 수정: 중복 호출 방지를 위한 체크 추가
    const headerObj = similarHeaders.find(h => h.header === header);

    if (!headerObj) return; // 헤더가 존재하지 않으면 무시

    // 이미 선택된 헤더인지 확인
    const isAlreadySelected = selectedSimilarityGroup.some(h => h.header === header);

    if (isAlreadySelected) {
      // 이미 선택된 경우, 선택 해제
      const newGroup = selectedSimilarityGroup.filter(h => h.header !== header);
      setSelectedSimilarityGroup(newGroup);

      // 대표어가 제거된 경우 새로운 대표어 선택
      if (representativeHeader === header) {
        setRepresentativeHeader(newGroup.length > 0 ? newGroup[0].header : null);
      }
    } else {
      // 선택되지 않은 경우, 선택에 추가
      const newGroup = [...selectedSimilarityGroup, headerObj];
      setSelectedSimilarityGroup(newGroup);

      // 첫 번째 항목인 경우 대표어로 설정
      if (selectedSimilarityGroup.length === 0) {
        setRepresentativeHeader(header);
      }
    }
  };

  const handleRemoveFromGroup = (header) => {
    setSelectedSimilarityGroup(prev => prev.filter(h => h.header !== header));
    // If representative header is removed, reset it
    if (representativeHeader === header) {
      setRepresentativeHeader(selectedSimilarityGroup.length > 1 ? selectedSimilarityGroup[0].header : null);
    }
  };

  const handleSetRepresentative = (header) => {
    setRepresentativeHeader(header);
  };

  const handleSaveGroup = () => {
    if (selectedSimilarityGroup.length > 0 && representativeHeader) {
      // 유사군 데이터를 일관된 형식으로 변환
      const groupData = selectedSimilarityGroup.map(item => ({
        header: item.header,
        // 기타 필요한 속성 유지
        ...item
      }));

      // 선택한 그룹에 현재 선택한 헤더가 없다면 추가
      const hasCurrentHeader = groupData.some(item => item.header === currentSimilarityHeader);
      if (!hasCurrentHeader && currentSimilarityHeader) {
        groupData.push({ header: currentSimilarityHeader });
      }

      console.log('저장할 유사군 데이터:', groupData, representativeHeader);

      onSimilarityGroupUpdate && onSimilarityGroupUpdate(
        groupData, 
        representativeHeader
      );

      // Clear the selected group after saving
      setSelectedSimilarityGroup([]);
      setRepresentativeHeader(null);

      // Return to the list view
      setViewMode('list');
    }
  };

  // 유사군에 속하지 않거나 대표어인 헤더만 표시
  const filteredHeaders = headers.filter(item => 
    !groupedHeaders.includes(item.header)
  );

  // 유사군이 있는 헤더 (대표어)와 없는 헤더를 구분
  const headersWithGroups = filteredHeaders.filter(item => 
    headerToGroupMap[item.header] && headerToGroupMap[item.header].isRepresentative
  );

  const headersWithoutGroups = filteredHeaders.filter(item => 
    !headerToGroupMap[item.header] || !headerToGroupMap[item.header].isRepresentative
  );

  // 활성 탭에 따라 표시할 헤더 목록 선택
  let displayHeaders = filteredHeaders;
  if (activeTab === 'withGroup') {
    displayHeaders = headersWithGroups;
  } else if (activeTab === 'withoutGroup') {
    displayHeaders = headersWithoutGroups;
  }

  if (!channel) {
    return (
      <div className="column-container">
        <div className="column-empty">채널을 선택해주세요.</div>
      </div>
    );
  }

  // Create a list of all headers that are already part of saved groups
  const savedGroupHeaders = similarityGroups ? similarityGroups.flatMap(group => 
    group.items.map(item => item.header)
  ) : [];

  // Filter out headers that are already in the selected group or in any saved group
  const filteredSimilarHeaders = similarHeaders.filter(
    h => !selectedSimilarityGroup.some(selected => selected.header === h.header) &&
         !savedGroupHeaders.includes(h.header)
  );

  return (
    <div className="column-container">
      {viewMode === 'list' ? (
        <>
          <h2 className="column-title">
            헤더 목록
            {/* : {channel} */}
          </h2>

          <form onSubmit={handleSearchSubmit} className="search-form">
            <input
              type="text"
              value={searchTerm}
              onChange={handleSearchChange}
              placeholder="검색어를 입력하세요"
              className="search-input"
            />
            <button type="submit" className="search-button">검색</button>
          </form>

          {isSearchMode && (
            <div className="search-status">
              "{searchTerm}" 검색 결과 {headers.length}건
              <button 
                className="clear-search-button" 
                onClick={() => {
                  setSearchTerm('');
                  setIsSearchMode(false);
                }}
              >
                검색 취소
              </button>
            </div>
          )}

          <div className="column-content">
            {loading ? (
              <div className="loading">로딩 중...</div>
            ) : error ? (
              <div className="error">에러: {error}</div>
            ) : displayHeaders.length > 0 ? (
              displayHeaders.map((item, index) => {
                // 각 헤더 항목 처리 시 로그 추가
                const hasGroupInfo = headerToGroupMap[item.header];
                const isRepresentative = hasGroupInfo && headerToGroupMap[item.header].isRepresentative;
                const groupCount = isRepresentative ? headerToGroupMap[item.header].count : 0;

                if (isRepresentative) {
                  console.log(`헤더 렌더링 - 대표어 표시: ${item.header}, 유사군 수: ${groupCount}`);
                }

                return (
                  <div
                    key={index}
                    className={`column-item ${selectedHeader === item.header ? 'selected' : ''}`}
                  >
                    <div 
                      className="column-item-content"
                      onClick={() => onHeaderSelect(item.header)}
                    >
                      {item.header}
                      {isRepresentative && (
                        <span className="similarity-tag" title={`${groupCount}개의 유사어가 있습니다`}>
                          유사군 {groupCount}
                        </span>
                      )}
                      {hasGroupInfo && !isRepresentative && (
                        <span className="similarity-tag" title={`대표어: ${headerToGroupMap[item.header].representative}`}>
                          유사군
                        </span>
                      )}
                    </div>
                    {!hasGroupInfo && (
                      <button 
                        className="view-similar-btn" 
                        onClick={() => handleViewSimilarHeaders(item.header)}
                        title="유사헤더 보기"
                      >
                        유사
                      </button>
                    )}
                  </div>
                );
              })
            ) : (
              <div className="column-empty">
                {activeTab === 'withGroup' ? '유사군이 있는 헤더가 없습니다.' : 
                 activeTab === 'withoutGroup' ? '유사군이 없는 헤더가 없습니다.' : 
                 '헤더 정보가 없습니다.'}
              </div>
            )}
          </div>

          <div className="tabs">
            <button 
              className={`tab ${activeTab === 'all' ? 'active' : ''}`}
              onClick={() => setActiveTab('all')}
            >
              전체 ({filteredHeaders.length})
            </button>
            <button 
              className={`tab ${activeTab === 'withGroup' ? 'active' : ''}`}
              onClick={() => setActiveTab('withGroup')}
            >
              유사군 있음 ({headersWithGroups.length})
            </button>
            <button 
              className={`tab ${activeTab === 'withoutGroup' ? 'active' : ''}`}
              onClick={() => setActiveTab('withoutGroup')}
            >
              유사군 없음 ({headersWithoutGroups.length})
            </button>
          </div>
        </>
      ) : (
        // Similar header selection view
        <>
          <h2 className="column-title">
            <button className="back-button" onClick={handleBackToHeaderList}>
              ← 헤더 목록
            </button>
            <span className="title-path" title={currentSimilarityHeader}>헤더 목록 &gt; '{currentSimilarityHeader}'</span>
          </h2>

          {/* 선택된 유사군 섹션 - 더 컴팩트하게 */}
          {selectedSimilarityGroup.length > 0 && (
            <div className="selected-group-compact">
              <div className="selected-group-header">
                <div className="header-section">
                  <h3 className="group-subtitle">
                    선택된 유사군 ({selectedSimilarityGroup.length})
                    <button 
                      className="toggle-collapse-btn"
                      onClick={() => setIsSelectedGroupCollapsed(!isSelectedGroupCollapsed)}
                      title={isSelectedGroupCollapsed ? "펼치기" : "접기"}
                    >
                      {isSelectedGroupCollapsed ? "+" : "-"}
                    </button>
                  </h3>

                  {!isSelectedGroupCollapsed && (
                    <>
                      {!representativeHeader && (
                        <div className="no-rep-warning">대표 헤더를 선택해 주세요</div>
                      )}
                      {representativeHeader && (
                        <div className="representative-label">
                          대표: <span className="rep-name">{representativeHeader}</span>
                        </div>
                      )}
                    </>
                  )}
                </div>

                {!isSelectedGroupCollapsed && (
                  <button 
                    className="save-group-btn"
                    disabled={!representativeHeader || selectedSimilarityGroup.length < 2}
                    onClick={handleSaveGroup}
                    title="현재 선택된 유사 헤더를 그룹으로 저장합니다"
                  >
                    저장
                  </button>
                )}

                {isSelectedGroupCollapsed && selectedSimilarityGroup.length > 0 && (
                  <div className="collapsed-summary">
                    {selectedSimilarityGroup.length} 항목 선택됨
                    {representativeHeader && ` (대표: ${representativeHeader})`}
                  </div>
                )}
              </div>

              {!isSelectedGroupCollapsed && (
                <div className="selected-group-content">
                  <div className="selected-items-horizontal">
                    {selectedSimilarityGroup.map((item, index) => (
                      <div key={index} className="selected-tag">
                        <span className={`tag-text ${representativeHeader === item.header ? 'is-representative' : ''}`}>
                          {item.header}
                        </span>
                        <button 
                          className="remove-btn small"
                          onClick={() => handleRemoveFromGroup(item.header)}
                          title="선택에서 제거"
                        >
                          ×
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          )}

          <div className="similarity-vertical-layout">
            <div className="similarity-header-section">
              <h3 className="group-subtitle">유사 헤더 선택</h3>

              {/* 유사 헤더 아이템 목록 */}
              <div className="similarity-items">
                {similarHeaderLoading ? (
                  <div className="loading">로딩 중...</div>
                ) : similarHeaderError ? (
                  <div className="error">에러: {similarHeaderError}</div>
                ) : similarHeaders && similarHeaders.length > 0 ? (
                  filteredSimilarHeaders.map((item, index) => {
                    // 이미 선택된 항목인지 확인
                    const isSelected = selectedSimilarityGroup.some(h => h.header === item.header);
                    // 대표 헤더인지 확인
                    const isRepresentative = representativeHeader === item.header;

                    return (
                      <div 
                        key={index} 
                        className={`similarity-item ${isSelected ? 'selected' : ''}`}
                        onClick={() => handleSimilarHeaderSelect(item.header)}
                      >
                        {item.header}
                        {isRepresentative && (
                          <span className="rep-badge" title="대표 헤더">대표</span>
                        )}
                      </div>
                    );
                  })
                ) : (
                  <div className="empty-state">유사한 헤더가 없습니다.</div>
                )}
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
});

export default HeaderList;
