import React, { useState } from 'react';
import './SeparationClassifier.css';

const GroupPanel = ({ groupData }) => {
  const [activeTab, setActiveTab] = useState('groups'); // 'groups' or 'ungrouped'

  if (!groupData || !groupData.substrings || !groupData.groups) {
    return <div className="no-data">그룹화된 데이터가 없습니다</div>;
  }

  // Build a hierarchical structure for substrings
  const buildHierarchy = () => {
    // Create a map of all substrings
    const substringMap = new Map();

    // Initialize the map with all substrings
    groupData.substrings.forEach(item => {
      substringMap.set(item.substring, {
        item,
        children: [],
        parent: null
      });
    });

    // For each substring, find if it's contained within another
    groupData.substrings.forEach(itemA => {
      const nodeA = substringMap.get(itemA.substring);

      groupData.substrings.forEach(itemB => {
        // Skip if same substring
        if (itemA.substring === itemB.substring) return;

        const nodeB = substringMap.get(itemB.substring);

        // If A is contained in B and is shorter (part-of relationship)
        if (itemB.substring.includes(itemA.substring) && 
            itemA.substring.length < itemB.substring.length) {

          // Check if there's a more specific parent already
          let moreSpecificParentExists = false;

          // Check if there's already another substring C where:
          // A is contained in C, and C is contained in B
          for (const [substringC, nodeC] of substringMap.entries()) {
            if (substringC !== itemA.substring && 
                substringC !== itemB.substring && 
                substringC.includes(itemA.substring) && 
                itemB.substring.includes(substringC)) {
              moreSpecificParentExists = true;
              break;
            }
          }

          // If no more specific parent found, make B the parent of A
          if (!moreSpecificParentExists) {
            // Remove from previous parent if exists
            if (nodeA.parent) {
              const prevParentChildren = nodeA.parent.children;
              const index = prevParentChildren.indexOf(nodeA);
              if (index > -1) {
                prevParentChildren.splice(index, 1);
              }
            }

            // Set B as the parent of A
            nodeA.parent = nodeB;
            if (!nodeB.children.includes(nodeA)) {
              nodeB.children.push(nodeA);
            }
          }
        }
      });
    });

    // Sort children nodes by reverse characters
    for (const node of substringMap.values()) {
      if (node.children.length > 0) {
        node.children.sort((a, b) => {
          // Reverse the strings for sorting from end to beginning
          const aReversed = a.item.substring.split('').reverse().join('');
          const bReversed = b.item.substring.split('').reverse().join('');
          return aReversed.localeCompare(bReversed);
        });
      }
    }

    // Get all root nodes (those without parents)
    const rootNodes = Array.from(substringMap.values())
      .filter(node => node.parent === null)
      // Sort root nodes by characters from end to beginning
      .sort((a, b) => {
        // Reverse the strings for sorting from end to beginning
        const aReversed = a.item.substring.split('').reverse().join('');
        const bReversed = b.item.substring.split('').reverse().join('');
        return aReversed.localeCompare(bReversed);
      });

    return { rootNodes, substringMap };
  };

  const { rootNodes } = buildHierarchy();

  // TreeNode component for each substring item
  const TreeNode = ({ node, isLast, level }) => {
    const [expanded, setExpanded] = useState(true);
    const { item, children } = node;
    const hasChildren = children && children.length > 0;

    // Toggle expansion state
    const toggleExpand = () => setExpanded(!expanded);

    return (
      <div className="tree-node">
        {/* Tree connector and expand/collapse control */}
        <div className="tree-node-line">
          {level > 0 && (
            <>
              {Array(level).fill().map((_, i) => (
                <span 
                  key={i} 
                  className={`tree-vline ${i === level - 1 ? (isLast ? 'last' : '') : ''}`}
                />
              ))}
              <span className={`tree-hline ${isLast ? 'last' : ''}`} />
            </>
          )}

          {hasChildren && (
            <button 
              className={`tree-toggle ${expanded ? 'expanded' : 'collapsed'}`}
              onClick={toggleExpand}
            >
              {expanded ? '−' : '+'}
            </button>
          )}

          {/* Node content */}
          <div className="tree-node-content">
            <div className="tree-node-header">
              <span className="group-name">{item.substring}</span>
              <span className="group-count">({item.count})</span>
            </div>

            {/* Show values for this substring */}
            {expanded && (
              <ul className="tree-values">
                {groupData.groups[item.substring] && groupData.groups[item.substring].map((value, valueIdx) => (
                  <li key={valueIdx} className="tree-value-item">
                    <span className="value-tag grouped">
                      {/* 부분문자열을 강조 표시 */}
                      {value.includes(item.substring) ? (
                        <>
                          {value.indexOf(item.substring) > 0 && (
                            <span className="value-prefix">
                              {value.substring(0, value.indexOf(item.substring))}
                            </span>
                          )}
                          <span className="value-highlight">
                            {item.substring}
                          </span>
                          {value.indexOf(item.substring) + item.substring.length < value.length && (
                            <span className="value-suffix">
                              {value.substring(value.indexOf(item.substring) + item.substring.length)}
                            </span>
                          )}
                        </>
                      ) : value}
                    </span>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>

        {/* Render children nodes */}
        {expanded && hasChildren && (
          <div className="tree-node-children">
            {children.map((childNode, i) => (
              <TreeNode 
                key={childNode.item.substring}
                node={childNode}
                isLast={i === children.length - 1}
                level={level + 1}
              />
            ))}
          </div>
        )}
      </div>
    );
  };

  // 부분문자열로 그룹화된 항목들을 계층적 트리뷰 형태로 표시
  return (
    <div className="group-panel">
      <h3>부분문자열 그룹 (계층적)</h3>

      {/* Tab navigation */}
      <div className="tab-navigation">
        <button 
          className={`tab-button ${activeTab === 'groups' ? 'active' : ''}`}
          onClick={() => setActiveTab('groups')}
        >
          그룹
        </button>
        <button 
          className={`tab-button ${activeTab === 'ungrouped' ? 'active' : ''}`}
          onClick={() => setActiveTab('ungrouped')}
        >
          그룹화되지 않음 <span className="tab-badge">{groupData.ungrouped?.length || 0}</span>
        </button>
      </div>

      {/* Tab content */}
      <div className="tab-content">
        {/* Grouped items */}
        {activeTab === 'groups' && (
          <div className="clean-tree-view">
            <div className="tree-root">
              {rootNodes.map((rootNode, i) => (
                <TreeNode 
                  key={rootNode.item.substring} 
                  node={rootNode}
                  isLast={i === rootNodes.length - 1}
                  level={0}
                />
              ))}
            </div>
          </div>
        )}

        {/* Ungrouped items */}
        {activeTab === 'ungrouped' && groupData.ungrouped && groupData.ungrouped.length > 0 && (
          <div className="ungrouped-content">
            <div className="ungrouped-description">
              다음 항목들은 부분문자열 그룹화 규칙에 해당되지 않는 항목들입니다.
            </div>
            <ul className="ungrouped-list">
              {groupData.ungrouped.map((value, idx) => (
                <li key={idx} className="ungrouped-item">
                  <span className="value-tag ungrouped">{value}</span>
                </li>
              ))}
            </ul>
          </div>
        )}

        {/* Empty state for ungrouped */}
        {activeTab === 'ungrouped' && (!groupData.ungrouped || groupData.ungrouped.length === 0) && (
          <div className="no-items-message">
            그룹화되지 않은 항목이 없습니다.
          </div>
        )}
      </div>
    </div>
  );
};

export default GroupPanel;
