import React, { useState, useEffect, useRef } from 'react';
import { fetchHeaderCombinationList } from '../services/api';
import './Column.css';
import * as d3 from 'd3';

const HeaderCombinationList = ({ channel, selectedHeader, onCombinationSelect, selectedCombination }) => {
  const [combinationHeaders, setCombinationHeaders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('combination'); // 'combination' or 'mining'
  const [testMode, setTestMode] = useState(false); // Add test mode state
  const [miningTree, setMiningTree] = useState(null);
  const treeContainer = useRef(null);

  useEffect(() => {
    const getCombinationHeaders = async () => {
      // Test mode can work without channel or header selection
      if (!testMode && (!channel || !selectedHeader)) return;

      setLoading(true);
      try {
        if (testMode) {
          // Use sample data in test mode
          const response = await import('../sample_data/header_combination.json');
          console.log('Using sample data for combination headers:', response);
          setCombinationHeaders(response.list || []);

          // Generate FP Growth mining tree data for test mode
          generateFPGrowthTree(response.list || []);
        } else if (channel && selectedHeader) {
          // Use API in normal mode only if channel and header are selected
          const headers = Array.isArray(selectedHeader) ? selectedHeader : [selectedHeader];
          const response = await fetchHeaderCombinationList(channel, headers);
          console.log('Combination headers response:', response);
          setCombinationHeaders(response.list || []);

          // Generate FP Growth mining tree data from actual response
          generateFPGrowthTree(response.list || []);
        } else {
          // Clear data if test mode is turned off and no channel/header is selected
          setCombinationHeaders([]);
          setMiningTree(null);
        }
        setError(null);
      } catch (err) {
        setError(err.message);
        setCombinationHeaders([]);
        setMiningTree(null);
      } finally {
        setLoading(false);
      }
    };

    getCombinationHeaders();
  }, [channel, selectedHeader, testMode]); // Add testMode as dependency

  // Effect to render the D3 tree whenever miningTree data or active tab changes
  useEffect(() => {
    if (activeTab === 'mining' && miningTree && treeContainer.current) {
      renderD3Tree();
    }
  }, [miningTree, activeTab]);

  // Function to render the D3 tree visualization
  const renderD3Tree = () => {
    if (!treeContainer.current || !miningTree) return;

    // Clear any existing SVG
    d3.select(treeContainer.current).selectAll("*").remove();

    const width = 800;
    const height = 600;
    const margin = { top: 40, right: 90, bottom: 50, left: 90 };
    const innerWidth = width - margin.left - margin.right;
    const innerHeight = height - margin.top - margin.bottom;

    // Create the SVG container
    const svg = d3.select(treeContainer.current)
      .append("svg")
      .attr("width", width)
      .attr("height", height)
      .append("g")
      .attr("transform", `translate(${margin.left},${margin.top})`);

    // Create the tree layout
    const treeLayout = d3.tree()
      .size([innerHeight, innerWidth]);

    // Create the root node using d3.hierarchy
    const root = d3.hierarchy(miningTree);

    // Assign the data to the tree layout
    const treeData = treeLayout(root);

    // Add links between nodes
    svg.selectAll(".link")
      .data(treeData.links())
      .enter()
      .append("path")
      .attr("class", "link")
      .attr("d", d3.linkHorizontal()
        .x(d => d.y)  // Swap x and y for horizontal layout
        .y(d => d.x))
      .style("fill", "none")
      .style("stroke", "#ccc")
      .style("stroke-width", 2);

    // Create a group for each node
    const node = svg.selectAll(".node")
      .data(treeData.descendants())
      .enter()
      .append("g")
      .attr("class", d => `node ${d.children ? "node--internal" : "node--leaf"}`)
      .attr("transform", d => `translate(${d.y},${d.x})`);

    // Add circles for each node
    node.append("circle")
      .attr("r", d => {
        // Size the circle based on count (with minimum and maximum sizes)
        const minSize = 5;
        const maxSize = 20;
        return d.data.count ? minSize + Math.min((d.data.count / 5), maxSize) : minSize;
      })
      .style("fill", d => {
        // Color based on depth
        const colors = ["#4682b4", "#7b68ee", "#3cb371", "#ff7f50", "#da70d6"];
        return colors[d.depth % colors.length];
      })
      .style("stroke", "#fff")
      .style("stroke-width", 2);

    // Add labels for each node
    node.append("text")
      .attr("dy", ".35em")
      .attr("x", d => d.children ? -13 : 13)
      .style("text-anchor", d => d.children ? "end" : "start")
      .text(d => `${d.data.name} (${d.data.count || 0})`)
      .style("font-size", "12px")
      .style("font-family", "Arial");

    // Add title for the tree visualization
    svg.append("text")
      .attr("x", innerWidth / 2)
      .attr("y", -20)
      .attr("text-anchor", "middle")
      .style("font-size", "16px")
      .style("font-weight", "bold")
      .text("FP Growth Mining Tree");
  };

  // Function to generate a more complete FP Growth tree
  const generateFPGrowthTree = (combinationData) => {
    if (!combinationData || combinationData.length === 0) {
      setMiningTree(null);
      return;
    }

    // Step 1: Create transactions from header combinations
    const transactions = combinationData
      .filter(item => item.header_group)
      .map(item => item.header_group.split('|'));

    // Step 2: Count item frequencies
    const itemCounts = {};
    transactions.forEach(transaction => {
      transaction.forEach(item => {
        itemCounts[item] = (itemCounts[item] || 0) + 1;
      });
    });

    // Step 3: Sort by frequency (descending) and filter low frequency items
    const minSupport = Math.max(2, Math.floor(transactions.length * 0.1)); // At least 10% support
    const frequentItems = Object.keys(itemCounts)
      .filter(item => itemCounts[item] >= minSupport)
      .sort((a, b) => itemCounts[b] - itemCounts[a]);

    // Step 4: Reorder transactions based on frequency
    const orderedTransactions = transactions.map(transaction => {
      return transaction
        .filter(item => frequentItems.includes(item))
        .sort((a, b) => itemCounts[b] - itemCounts[a]);
    }).filter(transaction => transaction.length > 0);

    // Step 5: Build the FP Tree
    const root = { 
      name: "Root", 
      children: [], 
      count: transactions.length,
      itemCounts: {}
    };

    // Helper function to insert a transaction into the tree
    const insertPath = (node, path, index = 0) => {
      if (index >= path.length) return;

      const item = path[index];
      let child = node.children.find(c => c.name === item);

      if (!child) {
        child = { 
          name: item, 
          count: 1,
          children: [] 
        };
        node.children.push(child);
      } else {
        child.count++;
      }

      insertPath(child, path, index + 1);
    };

    // Insert all transactions into the tree
    orderedTransactions.forEach(transaction => {
      insertPath(root, transaction);
    });

    // Step 6: Prune the tree to avoid overwhelming visualization
    const pruneTree = (node, maxDepth = 4, currentDepth = 0) => {
      if (currentDepth >= maxDepth) {
        node.children = [];
        return;
      }

      // Sort children by count
      node.children.sort((a, b) => b.count - a.count);

      // Limit number of children per node for visualization clarity
      if (node.children.length > 7) {
        node.children = node.children.slice(0, 7);
      }

      // Recursively prune children
      node.children.forEach(child => pruneTree(child, maxDepth, currentDepth + 1));
    };

    pruneTree(root);

    setMiningTree(root);
  };

  const handleCombinationClick = (combination) => {
    if (onCombinationSelect) {
      onCombinationSelect(combination);
    }
  };

  // Function to render header_group as tags
  const renderHeaderGroupAsTags = (headerGroup) => {
    if (!headerGroup) return null;

    const headers = headerGroup.split('|');
    return (
      <div className="header-tags">
        {headers.map((header, index) => (
          <span key={index} className="similarity-tag">{header}</span>
        ))}
      </div>
    );
  };

  const toggleTestMode = () => {
    const newTestMode = !testMode;
    setTestMode(newTestMode);

    // Log the test mode state change
    console.log(`Test mode ${newTestMode ? 'enabled' : 'disabled'}`);

    // Clear combination data when turning off test mode if no channel/header selected
    if (!newTestMode && (!channel || !selectedHeader)) {
      setCombinationHeaders([]);
      setMiningTree(null);
    }
  };

  return (
    <div className="column-container">
      <div className="column-title-container">
        <h2 className="column-title">
          {!testMode && selectedHeader 
            ? `헤더명 조합 목록: ${selectedHeader}` 
            : '헤더명 조합 목록'}
        </h2>
        <div className="test-mode-toggle">
          <label className="toggle-label">
            <span>Test Mode {testMode ? 'ON' : 'OFF'}</span>
            <input 
              type="checkbox" 
              checked={testMode}
              onChange={toggleTestMode}
            />
            <span className="toggle-slider"></span>
          </label>
        </div>
      </div>

      <div className="tabs">
        <button 
          className={`tab ${activeTab === 'combination' ? 'active' : ''}`}
          onClick={() => setActiveTab('combination')}
        >
          조합뷰
        </button>
        <button 
          className={`tab ${activeTab === 'mining' ? 'active' : ''}`}
          onClick={() => setActiveTab('mining')}
        >
          마이닝뷰
        </button>
      </div>

      {activeTab === 'combination' ? (
        <div className="tab-content">
          {loading ? (
            <div className="loading">로딩 중...</div>
          ) : error ? (
            <div className="error">에러: {error}</div>
          ) : combinationHeaders.length > 0 ? (
            <div className="column-content">
              {combinationHeaders.map((item, index) => (
                <div
                  key={index}
                  className={`column-item ${selectedCombination === item.header_group ? 'selected' : ''}`}
                  onClick={() => handleCombinationClick(item.header_group)}
                >
                  {renderHeaderGroupAsTags(item.header_group)}
                </div>
              ))}
            </div>
          ) : (
            <div className="column-empty">
              {testMode 
                ? "테스트 모드가 활성화되었습니다." 
                : "채널과 헤더를 선택해주세요."}
            </div>
          )}
        </div>
      ) : (
        <div className="tab-content">
          {loading ? (
            <div className="loading">로딩 중...</div>
          ) : error ? (
            <div className="error">에러: {error}</div>
          ) : miningTree ? (
            <div className="mining-view">
              <div 
                className="mining-tree-container" 
                ref={treeContainer}
                style={{ width: '100%', height: '500px', overflowX: 'auto' }}
              >
                {/* D3 tree will be rendered here */}
              </div>
              <div className="mining-explanation">
                <p>이 트리는 FP Growth 알고리즘 기반으로 헤더 조합 패턴을 시각화한 것입니다.</p>
                <ul>
                  <li>원의 크기는 해당 헤더의 빈도를 나타냅니다.</li>
                  <li>색상은 트리의 깊이를 구분합니다.</li>
                  <li>경로는 자주 함께 나타나는 헤더 패턴을 보여줍니다.</li>
                </ul>
              </div>
            </div>
          ) : (
            <div className="column-empty">
              {testMode 
                ? "마이닝 데이터를 로딩 중입니다..." 
                : "채널과 헤더를 선택하면 FP Growth 마이닝 트리가 표시됩니다."}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default HeaderCombinationList;
