import React, { useState } from 'react';
import './App.css';
import ChannelList from './components/ChannelList';
import HeaderList from './components/HeaderList';
import HeaderSimilarityList from './components/HeaderSimilarityList';

function App() {
  const [selectedChannel, setSelectedChannel] = useState(null);
  const [selectedHeader, setSelectedHeader] = useState(null);

  const handleChannelSelect = (channel) => {
    setSelectedChannel(channel);
    setSelectedHeader(null); // Reset header selection when changing channel
  };

  const handleHeaderSelect = (header) => {
    setSelectedHeader(header);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>채널 및 헤더 리스트</h1>
      </header>
      <main className="App-content">
        <div className="content-wrapper">
          <div className="column">
            <ChannelList onChannelSelect={handleChannelSelect} />
          </div>
          <div className="column">
            <HeaderList channel={selectedChannel} onHeaderSelect={handleHeaderSelect} />
          </div>
          <div className="column">
            {selectedHeader ? (
              <HeaderSimilarityList channel={selectedChannel} selectedHeader={selectedHeader} />
            ) : (
              <div className="placeholder-container">
                <div className="no-selection">헤더를 선택해주세요.</div>
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
