import React from 'react';
import './SeparationClassifier.css';

const DataDisplay = ({ processedData, flattenData }) => {
  if (!processedData) {
    return <div className="no-data">처리할 데이터가 없습니다</div>;
  }

  // Check if the text is a duplicated string (contains itself twice)
  const isDuplicatedText = (text) => {
    if (typeof text !== 'string' || text.length === 0) return false;
    const halfLength = Math.floor(text.length / 2);
    const firstHalf = text.substring(0, halfLength);
    return text.includes(firstHalf + firstHalf);
  };

  // Remove duplicated text if needed
  const cleanDuplicatedText = (text) => {
    if (!isDuplicatedText(text)) return text;
    const halfLength = Math.floor(text.length / 2);
    return text.substring(0, halfLength);
  };

  // Helper function to render a tag with appropriate styling
  const renderTag = (value, source, key) => {
    const className = `value-tag ${
      source === 'bracket' ? 'bracket-source' : 
      source === 'clean-text' ? 'text-source' : 'delimiter-source'
    }`;

    return (
      <span key={key} className={className}>
        {typeof value === 'string' ? value : value.value}
      </span>
    );
  };

  // Check if a value is in any group from the substring grouping
  const isValueInGroups = (value, groupData) => {
    if (!groupData || !groupData.groups) return false;

    // Check each group
    for (const substring in groupData.groups) {
      const group = groupData.groups[substring];
      if (group.includes(value)) {
        return true;
      }
    }

    return false;
  };

  // Render the enhanced view that shows processed and unprocessed parts in sequence
  const renderEnhancedView = (row) => {
    // Handle substring grouping data
    if (row.type === 'substring-grouping') {
      return renderSubstringGroupingView(row);
    }

    // Clean up potentially duplicated original text
    let original = row.original;
    if (isDuplicatedText(original)) {
      original = cleanDuplicatedText(original);
    }

    if (!original || typeof original !== 'string') {
      return renderSeparatedValues(row);
    }

    // If no separation has happened yet
    if (!row.separated || row.separated.length === 0) {
      return <span className="unprocessed-tag">{original}</span>;
    }

    if (row.type === 'bracket-separation') {
      // For bracket separation, highlight brackets and content
      let result = [];
      let lastIndex = 0;

      // Sort match details by start index if available
      const matchDetails = row.matchDetails || [];
      const sortedMatches = [...matchDetails].sort((a, b) => a.startIndex - b.startIndex);

      sortedMatches.forEach((match, idx) => {
        // Add unprocessed text before this match
        if (match.startIndex > lastIndex) {
          const unprocessedText = original.substring(lastIndex, match.startIndex);
          result.push(
            <span key={`unproc-${idx}`} className="unprocessed-tag">{unprocessedText}</span>
          );
        }

        // Add the processed bracket content
        result.push(
          <span key={`proc-${idx}`} className="value-tag bracket-source">{match.value}</span>
        );

        lastIndex = match.endIndex;
      });

      // Add any remaining text
      if (lastIndex < original.length) {
        result.push(
          <span key="unproc-last" className="unprocessed-tag">
            {original.substring(lastIndex)}
          </span>
        );
      }

      return result;
    } else if (row.type === 'delimiter-separation') {
      // For delimiter separation, we should preserve bracket positions from previous step
      const elements = [];

      // First, separate out the bracket parts (they have position info)
      const bracketParts = row.separated
        .filter(part => part.source === 'bracket' && part.bracketInfo)
        .sort((a, b) => a.bracketInfo.startIndex - b.bracketInfo.startIndex);

      // Non-bracket parts go in between or around bracket parts
      const otherParts = row.separated.filter(part => part.source !== 'bracket');

      // Use a simple approach: put brackets in their exact positions, other parts elsewhere
      let lastEnd = 0;
      let otherIndex = 0;

      // First, place any non-bracket parts that come before the first bracket
      if (bracketParts.length > 0) {
        const firstBracketStart = bracketParts[0].bracketInfo.startIndex;

        // Add some non-bracket parts at the beginning if there's space
        const beforeBracketText = original.substring(0, firstBracketStart).trim();
        if (beforeBracketText.length > 0) {
          // Look for matching non-bracket parts to put here
          let foundMatchingPart = false;

          // Try to find a matching part
          for (let i = 0; i < otherParts.length; i++) {
            const part = otherParts[i];
            if (beforeBracketText.includes(part.value)) {
              elements.push(
                <span key={`other-${otherIndex}`} className={`value-tag ${part.source === 'clean-text' ? 'text-source' : 'delimiter-source'}`}>
                  {part.value}
                </span>
              );
              otherIndex++;
              otherParts.splice(i, 1); // Remove this part
              foundMatchingPart = true;
              i--; // Adjust index for removed item
            }
          }

          // If no matching part found, show as unprocessed
          if (!foundMatchingPart) {
            elements.push(
              <span key={`unproc-start`} className="unprocessed-tag">{beforeBracketText}</span>
            );
          }
        }
      }

      // Place bracket parts in their exact positions
      bracketParts.forEach((bracketPart, idx) => {
        const bracketInfo = bracketPart.bracketInfo;

        // Add the bracket content
        elements.push(
          <span key={`bracket-${idx}`} className="value-tag bracket-source">
            {bracketPart.value}
          </span>
        );

        // Update the last position
        lastEnd = bracketInfo.endIndex;

        // If there's another bracket coming, check if we can fit other parts in between
        if (idx < bracketParts.length - 1) {
          const nextBracketStart = bracketParts[idx + 1].bracketInfo.startIndex;

          // If there's space between brackets, add some non-bracket parts
          if (nextBracketStart > lastEnd) {
            const betweenText = original.substring(lastEnd, nextBracketStart).trim();

            if (betweenText.length > 0) {
              // Try to find matching parts for this space
              let foundMatchingPart = false;

              // Look for parts that might fit here
              for (let i = 0; i < otherParts.length && i < 2; i++) {  // Limit to 2 parts
                const part = otherParts[i];
                if (betweenText.includes(part.value)) {
                  elements.push(
                    <span key={`between-${idx}-${i}`} className={`value-tag ${part.source === 'clean-text' ? 'text-source' : 'delimiter-source'}`}>
                      {part.value}
                    </span>
                  );
                  otherParts.splice(i, 1); // Remove this part
                  foundMatchingPart = true;
                  i--; // Adjust index for removed item
                }
              }

              // If no matching part found, show as unprocessed
              if (!foundMatchingPart) {
                elements.push(
                  <span key={`unproc-${idx}`} className="unprocessed-tag">{betweenText}</span>
                );
              }
            }
          }
        }
      });

      // Add any text after the last bracket
      if (lastEnd < original.length) {
        const afterText = original.substring(lastEnd).trim();

        if (afterText.length > 0) {
          // Try to place remaining non-bracket parts
          if (otherParts.length > 0) {
            // Add remaining other parts
            otherParts.forEach((part, idx) => {
              elements.push(
                <span key={`remaining-${idx}`} className={`value-tag ${part.source === 'clean-text' ? 'text-source' : 'delimiter-source'}`}>
                  {part.value}
                </span>
              );
            });
          } else {
            // No parts left, show as unprocessed
            elements.push(
              <span key="unproc-end" className="unprocessed-tag">{afterText}</span>
            );
          }
        }
      }

      return elements.length > 0 ? elements : renderSeparatedValues(row);
    }

    // Fallback to original display method
    return renderSeparatedValues(row);
  };

  // Render substring grouping data with color-coded grouped vs ungrouped items
  const renderSubstringGroupingView = (row) => {
    if (!row.previous || !row.previous.separated || row.previous.separated.length === 0) {
      return <span className="no-values">이전 단계의 값이 없습니다</span>;
    }

    return (
      <div className="substring-grouping-view">
        {row.previous.separated.map((item, idx) => {
          const value = typeof item === 'string' ? item : item.value;
          const isGrouped = isValueInGroups(value, row);
          const groupClass = isGrouped ? 'grouped' : 'ungrouped';

          return (
            <span 
              key={idx} 
              className={`value-tag ${groupClass}`}
              title={isGrouped ? '그룹에 포함됨' : '그룹화되지 않음'}
            >
              {value}
            </span>
          );
        })}
      </div>
    );
  };

  const renderSeparatedValues = (row) => {
    if (!row.separated || row.separated.length === 0) {
      return <span className="no-values">추출된 값 없음</span>;
    }

    if (Array.isArray(row.separated) && row.separated.every(v => typeof v === 'string')) {
      // Old format (just strings)
      return row.separated.map((val, idx) => (
        <span key={idx} className="value-tag">{val}</span>
      ));
    } 

    // New format (with metadata)
    return row.separated.map((item, idx) => (
      <span 
        key={idx} 
        className={`value-tag ${item.source === 'bracket' ? 'bracket-source' : 
                              item.source === 'clean-text' ? 'text-source' : 'delimiter-source'}`}
      >
        {typeof item === 'string' ? item : item.value}
      </span>
    ));
  };

  return (
    <div className="data-display">
      <table className="data-table">
        <tbody>
          {flattenData(processedData).map((row, index) => (
            <tr key={index}>
              <td className="enhanced-view">
                {renderEnhancedView(row)}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DataDisplay;
