import React, { useState, useEffect } from 'react';

const SeparationClassifier = ({ data, onReset }) => {
  const [processedData, setProcessedData] = useState(null);
  const [pipeline, setPipeline] = useState([
    { id: 'bracket-separation', name: '괄호값 분리', active: true },
    { 
      id: 'delimiter-separation', 
      name: '구분자 분리', 
      active: false,
      config: {
        delimiters: [',', ' '] // Default delimiters: comma and whitespace
      }
    }
  ]);
  const [selectedStep, setSelectedStep] = useState('bracket-separation');

  // Process data through pipeline steps
  useEffect(() => {
    if (data) {
      // Process data through all active pipeline steps
      let result = { ...data };

      pipeline.forEach(step => {
        if (step.active) {
          if (step.id === 'bracket-separation') {
            result = processBracketSeparation(result);
          } else if (step.id === 'delimiter-separation') {
            result = processDelimiterSeparation(result, step.config.delimiters);
          }
          // Add more step processors here in the future
        }
      });

      setProcessedData(result);
    } else {
      setProcessedData(null);
    }
  }, [data, pipeline]);

  const processBracketSeparation = (input) => {
    if (typeof input === 'string') {
      const bracketRegex = /\[(.*?)\]|\((.*?)\)|\{(.*?)\}/g;
      const matches = [];
      let match;

      while ((match = bracketRegex.exec(input)) !== null) {
        const value = match[1] || match[2] || match[3];
        if (value) matches.push(value);
      }

      return {
        original: input,
        separated: matches,
        type: 'bracket-separation'
      };
    } else if (Array.isArray(input)) {
      return input.map(item => processBracketSeparation(item));
    } else if (typeof input === 'object' && input !== null) {
      const result = {};
      for (const key in input) {
        result[key] = processBracketSeparation(input[key]);
      }
      return result;
    }

    return input;
  };

  const processDelimiterSeparation = (input, delimiters) => {
    if (typeof input === 'string') {
      // Create a regex pattern from the delimiters
      const delimiterPattern = delimiters.map(d => 
        // Escape special regex characters in delimiters
        d.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')
      ).join('|');

      const regex = new RegExp(`(${delimiterPattern})`, 'g');

      // Split the string by the delimiters and filter out empty strings
      const parts = input.split(regex).filter(part => 
        part.trim() !== '' && !delimiters.includes(part.trim())
      );

      return {
        original: input,
        separated: parts,
        type: 'delimiter-separation'
      };
    } else if (input && input.type === 'bracket-separation') {
      // Process the original text with delimiter separation
      const delimiterPattern = delimiters.map(d => 
        d.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')
      ).join('|');

      const regex = new RegExp(`(${delimiterPattern})`, 'g');

      // Split the original text and filter
      const partsFromOriginal = input.original.split(regex).filter(part => 
        part.trim() !== '' && !delimiters.includes(part.trim())
      );

      // Also process bracket values to get more granular results
      const partsFromBrackets = [];
      input.separated.forEach(val => {
        const parts = val.split(regex).filter(part => 
          part.trim() !== '' && !delimiters.includes(part.trim())
        );
        partsFromBrackets.push(...parts);
      });

      // Combine all unique values
      const allParts = [...new Set([...partsFromOriginal, ...partsFromBrackets])];

      return {
        original: input.original,
        separated: allParts,
        type: 'delimiter-separation',
        previous: {
          type: input.type,
          separated: input.separated
        }
      };
    } else if (Array.isArray(input)) {
      return input.map(item => processDelimiterSeparation(item, delimiters));
    } else if (typeof input === 'object' && input !== null) {
      const result = {};
      for (const key in input) {
        result[key] = processDelimiterSeparation(input[key], delimiters);
      }
      return result;
    }

    return input;
  };

  const toggleStepActive = (stepId) => {
    setPipeline(pipeline.map(step => 
      step.id === stepId ? { ...step, active: !step.active } : step
    ));
  };

  const selectStep = (stepId) => {
    setSelectedStep(stepId);
  };

  // Update delimiter configuration
  const updateDelimiters = (delimiterString) => {
    setPipeline(pipeline.map(step => 
      step.id === 'delimiter-separation' 
        ? { 
            ...step, 
            config: { 
              ...step.config, 
              delimiters: delimiterString.split('').filter(d => d) // Split into characters and filter empty
            } 
          } 
        : step
    ));
  };

  // Flatten data structure to generate simple rows
  const flattenData = (data, path = '', rows = []) => {
    if (!data) return rows;

    if (data.type === 'bracket-separation' || data.type === 'delimiter-separation') {
      rows.push({
        path,
        original: data.original,
        separated: data.separated,
        type: data.type,
        previous: data.previous
      });
    } else if (Array.isArray(data)) {
      data.forEach((item, index) => {
        const itemPath = path ? `${path}[${index}]` : `[${index}]`;
        flattenData(item, itemPath, rows);
      });
    } else if (typeof data === 'object' && data !== null) {
      Object.entries(data).forEach(([key, value]) => {
        const keyPath = path ? `${path}.${key}` : key;
        flattenData(value, keyPath, rows);
      });
    } else if (path) {
      // Simple value
      rows.push({
        path,
        original: data,
        separated: []
      });
    }

    return rows;
  };

  return (
    <div className="separation-classifier">
      <div className="classifier-layout">
        {/* Left side - Data View (wider) */}
        <div className="data-view">
          <h3>처리된 데이터</h3>
          <div className="data-content">
            {processedData ? (
              <table className="data-table">
                <tbody>
                  {flattenData(processedData).map((row, index) => (
                    <tr key={index}>
                      <td className="original-cell">
                        {typeof row.original === 'string' ? (
                          <div dangerouslySetInnerHTML={{ 
                            __html: row.original.replace(
                              /(\[.*?\]|\(.*?\)|\{.*?\})/g, 
                              '<span class="highlight-bracket">$1</span>'
                            )
                          }} />
                        ) : (
                          String(row.original)
                        )}
                      </td>
                      <td className="separated-cell">
                        {row.separated && row.separated.length > 0 ? (
                          <div className="separated-values">
                            {row.separated.map((val, idx) => (
                              <span key={idx} className="value-tag">{val}</span>
                            ))}
                          </div>
                        ) : (
                          <span className="no-values">추출된 값 없음</span>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <div className="no-data">처리할 데이터가 없습니다</div>
            )}
          </div>
        </div>

        {/* Right side - Pipeline (narrower) */}
        <div className="pipeline-panel">
          <h3>파이프라인</h3>
          <div className="pipeline-list">
            {pipeline.map((step, index) => (
              <div 
                key={step.id}
                className={`pipeline-step ${step.active ? 'active' : 'inactive'} ${selectedStep === step.id ? 'selected' : ''}`}
                onClick={() => selectStep(step.id)}
              >
                <div className="step-number">{index + 1}</div>
                <div className="step-details">
                  <div className="step-title">{step.name}</div>
                  <div className="step-control">
                    <label className="toggle">
                      <input 
                        type="checkbox" 
                        checked={step.active}
                        onChange={() => toggleStepActive(step.id)}
                        onClick={(e) => e.stopPropagation()}
                      />
                      <span className="toggle-track"></span>
                    </label>
                  </div>
                </div>
                {selectedStep === step.id && step.id === 'delimiter-separation' && (
                  <div className="step-config">
                    <div className="config-row">
                      <label>구분자:</label>
                      <input 
                        type="text" 
                        value={step.config.delimiters.join('')}
                        onChange={(e) => updateDelimiters(e.target.value)}
                        className="delimiter-input"
                        placeholder="구분자 입력 (예: ,;)"
                        onClick={(e) => e.stopPropagation()}
                      />
                    </div>
                    <div className="delimiter-help">
                      각 문자는 개별 구분자로 처리됩니다. 예: <code>,</code> <code>공백</code>
                    </div>
                  </div>
                )}
              </div>
            ))}

            <button className="add-step">
              + 처리 단계 추가
            </button>
          </div>

          <div className="pipeline-actions">
            <button className="reset-button" onClick={onReset}>
              초기화
            </button>
          </div>
        </div>
      </div>

