import React from 'react';
import './SeparationClassifier.css';

const DataDisplay = ({ processedData, flattenData }) => {
  if (!processedData) {
    return <div className="no-data">처리할 데이터가 없습니다</div>;
  }

  const renderOriginalCell = (row) => {
    if (typeof row.original === 'string') {
      return (
        <div dangerouslySetInnerHTML={{ 
          __html: row.original.replace(
            /(\[.*?\]|\(.*?\)|\{.*?\})/g, 
            '<span class="highlight-bracket">$1</span>'
          )
        }} />
      );
    }
    return String(row.original);
  };

  const renderSeparatedValues = (row) => {
    if (!row.separated || row.separated.length === 0) {
      return <span className="no-values">추출된 값 없음</span>;
    }

    if (Array.isArray(row.separated) && row.separated.every(v => typeof v === 'string')) {
      // Old format (just strings)
      return row.separated.map((val, idx) => (
        <span key={idx} className="value-tag">{val}</span>
      ));
    } 

    // New format (with metadata)
    return row.separated.map((item, idx) => (
      <span 
        key={idx} 
        className={`value-tag ${item.source === 'bracket' ? 'bracket-source' : 
                              item.source === 'clean-text' ? 'text-source' : 'delimiter-source'}`}
      >
        {typeof item === 'string' ? item : item.value}
      </span>
    ));
  };

  const renderPatternGroups = (row) => {
    if (!row.patterns || row.patterns.length === 0) {
      return <span className="no-values">발견된 패턴 없음</span>;
    }

    return (
      <div className="pattern-groups">
        {row.patterns.map((pattern, idx) => (
          <div key={idx} className="pattern-group">
            <div className="pattern-header">
              <span className={`pattern-type ${pattern.type}-pattern`}>
                {pattern.type === 'prefix' ? '접두사' : '접미사'}
              </span>
              <span className="pattern-value">"{pattern.pattern}"</span>
              <span className="pattern-count">빈도: {pattern.count}</span>
            </div>
            <div className="pattern-values">
              {pattern.values.map((value, valueIdx) => (
                <span key={valueIdx} className="value-tag">{value}</span>
              ))}
            </div>
          </div>
        ))}
      </div>
    );
  };

  const renderCellContent = (row) => {
    if (row.type === 'substring-grouping') {
      return renderPatternGroups(row);
    } else {
      return renderSeparatedValues(row);
    }
  };

  return (
    <table className="data-table">
      <tbody>
        {flattenData(processedData).map((row, index) => (
          <tr key={index}>
            <td className="original-cell">
              {renderOriginalCell(row)}
            </td>
            <td className="separated-cell">
              <div className="separated-values">
                {renderCellContent(row)}
              </div>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default DataDisplay;
