import React, { useState, useEffect } from 'react';
import { fetchHeaderSimilarityList } from '../services/api';
import './HeaderSimilarityList.css';

const HeaderSimilarityList = ({ channel, selectedHeader }) => {
  const [similarHeaders, setSimilarHeaders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getSimilarHeaders = async () => {
      if (!channel || !selectedHeader) return;

      setLoading(true);
      try {
        const response = await fetchHeaderSimilarityList(channel, selectedHeader);
        console.log('Similar headers response:', response);
        setSimilarHeaders(response.list || []);
        setError(null);
      } catch (err) {
        setError(err.message);
        setSimilarHeaders([]);
      } finally {
        setLoading(false);
      }
    };

    getSimilarHeaders();
  }, [channel, selectedHeader]);

  if (!channel || !selectedHeader) return <div className="no-selection">채널과 헤더를 선택해주세요.</div>;
  if (loading) return <div className="loading">유사 헤더 목록 로딩 중...</div>;
  if (error) return <div className="error">에러: {error}</div>;

  return (
    <div className="similarity-list-container">
      <h2>헤더명 유사군 목록: {selectedHeader}</h2>
      <div className="similarity-list">
        {similarHeaders && similarHeaders.length > 0 ? (
          similarHeaders.map((item, index) => (
            <div key={index} className="similar-header-item">
              {item.header}
            </div>
          ))
        ) : (
          <div className="no-similar-headers">유사 헤더 정보가 없습니다.</div>
        )}
      </div>
    </div>
  );
};

export default HeaderSimilarityList;
