import React, { useState, useEffect } from 'react';
import './GroupingSheet.css';

const GroupingSheet = ({ segmentValues = [] }) => {
  // 그룹 상태 관리
  const [groups, setGroups] = useState([
    { id: 'unprocessed', name: '미처리', items: [], isDefault: true }
  ]);

  // 값들의 그룹 할당 상태 관리
  const [valueGroups, setValueGroups] = useState({});

  // 새 그룹명 입력 상태
  const [newGroupName, setNewGroupName] = useState('');

  // 선택된 그룹 관리
  const [selectedGroupId, setSelectedGroupId] = useState(null);

  // 컴포넌트 마운트시 미처리 그룹에 모든 분절값 추가
  useEffect(() => {
    if (segmentValues.length > 0) {
      // 초기 상태에서 모든 값은 미처리 그룹에 할당
      const initialValueGroups = {};
      segmentValues.forEach(value => {
        initialValueGroups[value] = 'unprocessed';
      });

      setValueGroups(initialValueGroups);

      // 미처리 그룹에 값 할당
      setGroups(prevGroups => {
        const updatedGroups = [...prevGroups];
        const unprocessedGroup = updatedGroups.find(g => g.id === 'unprocessed');
        if (unprocessedGroup) {
          unprocessedGroup.items = [...segmentValues];
        }
        return updatedGroups;
      });
    }
  }, [segmentValues]);

  // 새 그룹 추가 함수
  const addNewGroup = () => {
    if (!newGroupName.trim()) return;

    const newGroupId = `group-${Date.now()}`;
    const newGroup = {
      id: newGroupId,
      name: newGroupName.trim(),
      items: [],
      isDefault: false
    };

    setGroups([...groups, newGroup]);
    setNewGroupName('');

    // 새 그룹 추가 후 자동으로 선택
    setSelectedGroupId(newGroupId);
  };

  // 그룹 선택 함수
  const selectGroup = (groupId) => {
    setSelectedGroupId(groupId === selectedGroupId ? null : groupId);
  };

  // 값을 그룹에 할당하는 함수
  const assignToGroup = (value, groupId) => {
    // 같은 그룹으로 이동이면 무시
    if (valueGroups[value] === groupId) return;

    // 현재 할당된 그룹에서 제거
    const currentGroupId = valueGroups[value];
    if (currentGroupId) {
      setGroups(prevGroups => {
        return prevGroups.map(group => {
          if (group.id === currentGroupId) {
            return {
              ...group,
              items: group.items.filter(item => item !== value)
            };
          }
          return group;
        });
      });
    }

    // 새 그룹에 추가
    setGroups(prevGroups => {
      return prevGroups.map(group => {
        if (group.id === groupId) {
          return {
            ...group,
            items: [...group.items, value]
          };
        }
        return group;
      });
    });

    // 값의 그룹 매핑 업데이트
    setValueGroups({
      ...valueGroups,
      [value]: groupId
    });
  };

  // 아이템 클릭 핸들러
  const handleItemClick = (value, currentGroupId) => {
    // 선택된 그룹이 없으면 무시 (미처리 그룹이 아닌 다른 그룹의 아이템은 클릭할 수 없음)
    if (!selectedGroupId || (currentGroupId !== 'unprocessed' && selectedGroupId !== 'unprocessed')) return;

    // 미처리 항목을 클릭한 경우, 선택된 그룹으로 이동
    if (currentGroupId === 'unprocessed' && selectedGroupId !== 'unprocessed') {
      assignToGroup(value, selectedGroupId);
    }
    // 그룹 항목을 클릭한 경우, 미처리로 이동 (미처리가 선택된 경우에만)
    else if (currentGroupId !== 'unprocessed' && selectedGroupId === 'unprocessed') {
      assignToGroup(value, 'unprocessed');
    }
  };

  // 그룹 삭제 함수
  const deleteGroup = (groupId, e) => {
    // 이벤트 전파 방지
    e.stopPropagation();

    if (groupId === 'unprocessed') return; // 미처리 그룹은 삭제 불가

    // 해당 그룹에 있던 모든 값을 미처리로 이동
    const groupToDelete = groups.find(g => g.id === groupId);
    if (groupToDelete) {
      groupToDelete.items.forEach(value => {
        assignToGroup(value, 'unprocessed');
      });
    }

    // 그룹 삭제
    setGroups(groups.filter(g => g.id !== groupId));

    // 삭제된 그룹이 선택된 그룹이었으면 선택 해제
    if (selectedGroupId === groupId) {
      setSelectedGroupId(null);
    }
  };

  // 그룹명 변경 함수
  const [editingGroupId, setEditingGroupId] = useState(null);
  const [editingGroupName, setEditingGroupName] = useState('');

  const startEditingGroupName = (group, e) => {
    // 이벤트 전파 방지 (그룹 선택과 충돌방지)
    e.stopPropagation();

    if (group.isDefault) return; // 미처리 그룹명은 변경 불가
    setEditingGroupId(group.id);
    setEditingGroupName(group.name);
  };

  const saveGroupName = () => {
    if (!editingGroupName.trim()) return;

    setGroups(prevGroups => {
      return prevGroups.map(group => {
        if (group.id === editingGroupId) {
          return {
            ...group,
            name: editingGroupName.trim()
          };
        }
        return group;
      });
    });

    setEditingGroupId(null);
    setEditingGroupName('');
  };

  // 새 그룹 추가 키 핸들러
  const handleAddGroupKeyDown = (e) => {
    if (e.key === 'Enter' && newGroupName.trim()) {
      addNewGroup();
    }
  };

  return (
    <div className="grouping-sheet">
      {/* 도움말 */}
      <div className="grouping-help">
        <p>
          1. 그룹 헤더를 클릭하여 그룹을 선택하세요. 2. 미처리 항목을 클릭하면 선택된 그룹에 추가됩니다.
        </p>
      </div>

      {/* 엑셀 시트 스타일의 테이블 */}
      <div className="sheet-container">
        <table className="grouping-table">
          <thead>
            <tr>
              {groups.map(group => (
                <th 
                  key={group.id} 
                  className={`group-header ${selectedGroupId === group.id ? 'selected-group-header' : ''}`}
                  onClick={() => selectGroup(group.id)}
                >
                  {editingGroupId === group.id ? (
                    <div className="group-name-edit" onClick={(e) => e.stopPropagation()}>
                      <input
                        type="text"
                        value={editingGroupName}
                        onChange={(e) => setEditingGroupName(e.target.value)}
                        onBlur={saveGroupName}
                        onKeyDown={(e) => e.key === 'Enter' && saveGroupName()}
                        autoFocus
                      />
                    </div>
                  ) : (
                    <div className="group-title">
                      <span 
                        className="group-name"
                        onClick={(e) => startEditingGroupName(group, e)}
                        title={group.isDefault ? "미처리 그룹명은 변경할 수 없습니다" : "클릭하여 그룹명 변경"}
                      >
                        {group.name} ({group.items.length})
                      </span>
                      {!group.isDefault && (
                        <button 
                          className="delete-group-btn"
                          onClick={(e) => deleteGroup(group.id, e)}
                          title="그룹 삭제"
                        >
                          ✕
                        </button>
                      )}
                    </div>
                  )}
                </th>
              ))}

              {/* 새 그룹 추가 헤더 */}
              <th className="add-group-header">
                <div className="add-group-header-content">
                  <input
                    type="text"
                    value={newGroupName}
                    onChange={(e) => setNewGroupName(e.target.value)}
                    placeholder="새 그룹 이름"
                    className="header-new-group-input"
                    onKeyDown={handleAddGroupKeyDown}
                  />
                  <button 
                    className="header-add-group-btn"
                    onClick={addNewGroup}
                    disabled={!newGroupName.trim()}
                  >
                    +
                  </button>
                </div>
              </th>
            </tr>
          </thead>
          <tbody>
            <tr>
              {groups.map(group => (
                <td 
                  key={group.id}
                  className={`group-column ${selectedGroupId === group.id ? 'selected-group' : ''}`}
                >
                  <div className="group-items">
                    {group.items.map(value => (
                      <div 
                        key={`${group.id}-${value}`}
                        className={`group-item ${(selectedGroupId === 'unprocessed' && group.id !== 'unprocessed') || (selectedGroupId !== 'unprocessed' && group.id === 'unprocessed') ? 'clickable' : ''}`}
                        onClick={() => handleItemClick(value, group.id)}
                      >
                        {value}
                      </div>
                    ))}
                    {group.items.length === 0 && (
                      <div className="empty-group-message">
                        {group.id === 'unprocessed' ? 
                          '미처리 항목이 없습니다' : 
                          '미처리 항목을 클릭하여 추가하세요'}
                      </div>
                    )}
                  </div>
                </td>
              ))}

              {/* 새 그룹 추가에 상응하는 빈 셀 */}
              <td className="add-group-column">
                <div className="empty-group-placeholder">
                  새 그룹을 추가하면 여기에 나타납니다
                </div>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default GroupingSheet;
