import React, { useState, useEffect } from 'react';
import { fetchHeaderList } from '../services/api';
import Column from './Column';

const HeaderList = ({ channel, onHeaderSelect, selectedHeader }) => {
  const [headers, setHeaders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getHeaders = async () => {
      if (!channel) return;

      setLoading(true);
      try {
        const response = await fetchHeaderList(channel);
        console.log('Header list response:', response);
        setHeaders(response.list || []);
        setError(null);
      } catch (err) {
        setError(err.message);
        setHeaders([]);
      } finally {
        setLoading(false);
      }
    };

    getHeaders();
  }, [channel]);

  if (!channel) {
    return (
      <div className="column-container">
        <div className="column-empty">채널을 선택해주세요.</div>
      </div>
    );
  }

  return (
    <Column
      title={`헤더 목록: ${channel}`}
      items={headers}
      onItemClick={onHeaderSelect}
      renderItem={{
        display: item => item.header,
        value: item => item.header
      }}
      selectedItem={selectedHeader}
      loading={loading}
      error={error}
      emptyMessage="헤더 정보가 없습니다."
    />
  );
};

export default HeaderList;
