import React, { useState, useEffect } from 'react';
import { fetchHeaderCombinationTextList } from '../services/api';
import Column from './Column';

const HeaderCombinationTextList = ({ channel, selectedCombination, onCustomSectionsUpdate }) => {
  const [combinationTexts, setCombinationTexts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [customSections, setCustomSections] = useState([]);
  const [newSectionName, setNewSectionName] = useState('');

  useEffect(() => {
    const getCombinationTexts = async () => {
      if (!channel || !selectedCombination) return;

      setLoading(true);
      try {
        const response = await fetchHeaderCombinationTextList(channel, [selectedCombination]);
        console.log('Combination texts response:', response);
        setCombinationTexts(response.list || []);
        setError(null);
      } catch (err) {
        setError(err.message);
        setCombinationTexts([]);
      } finally {
        setLoading(false);
      }
    };

    getCombinationTexts();
  }, [channel, selectedCombination]);

  // Update parent component when customSections change
  useEffect(() => {
    if (onCustomSectionsUpdate) {
      onCustomSectionsUpdate(customSections);
    }
  }, [customSections, onCustomSectionsUpdate]);

  const handleCreateSection = () => {
    if (newSectionName.trim()) {
      const updatedSections = [...customSections, newSectionName.trim()];
      setCustomSections(updatedSections);
      setNewSectionName('');
    }
  };

  if (!channel || !selectedCombination) {
    return (
      <div className="column-container">
        <div className="column-empty">채널과 헤더 조합을 선택해주세요.</div>
      </div>
    );
  }

  const combinationCount = selectedCombination ? selectedCombination.split(',').length : 0;

  return (
    <div>
      <div className="section-creation-area">
        <h3>섹션 생성</h3>
        <div className="combination-info">
          선택된 조합 개수: {combinationCount}
        </div>
        <div className="section-input-container">
          <input
            type="text"
            value={newSectionName}
            onChange={(e) => setNewSectionName(e.target.value)}
            placeholder="새 섹션 이름 입력"
            className="section-input"
          />
          <button onClick={handleCreateSection} className="section-button">
            섹션 생성
          </button>
        </div>
        {customSections.length > 0 && (
          <div className="custom-sections-list">
            <h4>생성된 섹션 목록:</h4>
            <ul>
              {customSections.map((section, index) => (
                <li key={index}>{section}</li>
              ))}
            </ul>
          </div>
        )}
      </div>

      <Column
        title={`섹션 생성: ${selectedCombination}`}
        items={combinationTexts}
        renderItem={{
          display: item => (
            <div>
              <div className="item-text">{item.text}</div>
              <div className="item-count">({item.count})</div>
            </div>
          ),
          value: item => item.text
        }}
        loading={loading}
        error={error}
        emptyMessage="헤더 조합 텍스트 정보가 없습니다."
      />
    </div>
  );
};

export default HeaderCombinationTextList;
