import React, { useState, useEffect } from 'react';
import { fetchHeaderList } from '../services/api';
import './Column.css';

const HeaderList = ({ channel, onHeaderSelect, selectedHeader, similarityGroups }) => {
  const [headers, setHeaders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getHeaders = async () => {
      if (!channel) return;

      setLoading(true);
      try {
        const response = await fetchHeaderList(channel);
        console.log('Header list response:', response);
        setHeaders(response.list || []);
        setError(null);
      } catch (err) {
        setError(err.message);
        setHeaders([]);
      } finally {
        setLoading(false);
      }
    };

    getHeaders();
  }, [channel]);

  // 유사군에 포함된 모든 헤더 추출
  const groupedHeaders = similarityGroups && similarityGroups.length > 0
    ? similarityGroups.flatMap(group => group.items.map(item => item.header))
    : [];

  // 각 헤더별로 유사군 매핑 생성
  const headerToGroupMap = {};
  if (similarityGroups && similarityGroups.length > 0) {
    similarityGroups.forEach(group => {
      group.items.forEach(item => {
        // 대표어가 아닌 헤더만 매핑 (대표어는 indicator가 필요 없음)
        if (item.header !== group.representative) {
          headerToGroupMap[item.header] = {
            representative: group.representative,
            count: group.items.length
          };
        }
      });
    });
  }

  // 유사군에 속하지 않은 헤더만 필터링
  const filteredHeaders = headers.filter(item => !groupedHeaders.includes(item.header));

  if (!channel) {
    return (
      <div className="column-container">
        <div className="column-empty">채널을 선택해주세요.</div>
      </div>
    );
  }

  return (
    <div className="column-container">
      <h2 className="column-title">
        헤더 목록: {channel}
      </h2>
      <div className="column-content">
        {loading ? (
          <div className="loading">로딩 중...</div>
        ) : error ? (
          <div className="error">에러: {error}</div>
        ) : filteredHeaders.length > 0 ? (
          filteredHeaders.map((item, index) => (
            <div
              key={index}
              className={`column-item ${selectedHeader === item.header ? 'selected' : ''}`}
              onClick={() => onHeaderSelect(item.header)}
            >
              {item.header}
              {headerToGroupMap[item.header] && (
                <span className="similarity-tag" title={`대표어: ${headerToGroupMap[item.header].representative}`}>
                  유사군
                </span>
              )}
            </div>
          ))
        ) : (
          <div className="column-empty">헤더 정보가 없습니다.</div>
        )}
      </div>
    </div>
  );
};

export default HeaderList;
