import React, { useState } from 'react';
import './SeparationClassifier.css';

const GroupPanel = ({ groupingData }) => {
  const [selectedTab, setSelectedTab] = useState('values');

  if (!groupingData) {
    return <div className="no-data">그룹핑 데이터가 없습니다</div>;
  }

  const { 
    uniqueValues, 
    groups, 
    onAddGroup, 
    onUpdateGroupName, 
    onAddValueToGroup,
    onRemoveValueFromGroup
  } = groupingData;

  const handleAddGroup = () => {
    if (onAddGroup) {
      onAddGroup();
    }
  };

  const handleUpdateGroupName = (groupId, newName) => {
    if (onUpdateGroupName) {
      onUpdateGroupName(groupId, newName);
    }
  };

  const handleAddValueToGroup = (groupId, value) => {
    if (onAddValueToGroup) {
      onAddValueToGroup(groupId, value);
    }
  };

  const handleRemoveValueFromGroup = (groupId, value) => {
    if (onRemoveValueFromGroup) {
      onRemoveValueFromGroup(groupId, value);
    }
  };

  // Find which group contains a specific value
  const findGroupForValue = (value) => {
    return groups?.find(group => group.items.includes(value));
  };

  return (
    <div className="group-panel">
      <div className="tab-navigation">
        <button 
          className={`tab-button ${selectedTab === 'values' ? 'active' : ''}`}
          onClick={() => setSelectedTab('values')}
        >
          분절값 목록 ({uniqueValues?.length || 0})
        </button>
        <button 
          className={`tab-button ${selectedTab === 'groups' ? 'active' : ''}`}
          onClick={() => setSelectedTab('groups')}
        >
          그룹 ({groups?.length || 0})
        </button>
      </div>

      {/* Debug display */}
      <div style={{fontSize: '10px', padding: '5px', background: '#f5f5f5', margin: '5px 0'}}>
        uniqueValues length: {uniqueValues?.length || 0}
        {uniqueValues && uniqueValues.length > 0 && (
          <div>First few values: {uniqueValues.slice(0, 5).join(', ')}</div>
        )}
      </div>

      <div className="grouping-layout">
        {selectedTab === 'values' && (
          <div className="segmentation-values-container">
            <div className="values-header">
              <h4>분절값 목록</h4>
              <p className="values-description">
                분절값을 그룹으로 드래그하거나 드롭다운에서 그룹을 선택하세요
              </p>
            </div>

            <div className="values-list">
              {uniqueValues?.map((value, index) => {
                const group = findGroupForValue(value);

                return (
                  <div key={index} className="segmentation-value-item" draggable>
                    <span className="value-text">{value}</span>
                    <div className="value-group-selector">
                      <select 
                        value={group?.id || ""}
                        onChange={(e) => {
                          // Remove from current group if any
                          if (group) {
                            handleRemoveValueFromGroup(group.id, value);
                          }

                          // Add to new group if not empty
                          if (e.target.value) {
                            handleAddValueToGroup(e.target.value, value);
                          }
                        }}
                      >
                        <option value="">그룹 선택</option>
                        {groups?.map(g => (
                          <option key={g.id} value={g.id}>{g.name}</option>
                        ))}
                      </select>
                    </div>
                  </div>
                );
              })}

              {(!uniqueValues || uniqueValues.length === 0) && (
                <div className="no-values">추출된 분절값이 없습니다</div>
              )}
            </div>
          </div>
        )}

        {selectedTab === 'groups' && (
          <div className="group-boxes-container">
            <div className="groups-header">
              <h4>그룹 목록</h4>
              <button className="add-group-button" onClick={handleAddGroup}>
                + 새 그룹 추가
              </button>
            </div>

            <div className="groups-list">
              {groups?.map((group, index) => (
                <div key={group.id} className="group-box">
                  <div className="group-header">
                    <input 
                      type="text" 
                      value={group.name} 
                      onChange={(e) => handleUpdateGroupName(group.id, e.target.value)}
                      className="group-name-input"
                    />
                    <div className="group-actions">
                      <button className="group-edit-button">
                        설정
                      </button>
                    </div>
                  </div>

                  <div className="group-items">
                    {group.items.map((item, itemIndex) => (
                      <div key={itemIndex} className="group-item">
                        <span className="item-text">{item}</span>
                        <button 
                          className="remove-item-button"
                          onClick={() => handleRemoveValueFromGroup(group.id, item)}
                        >
                          ×
                        </button>
                      </div>
                    ))}

                    {(!group.items || group.items.length === 0) && (
                      <div className="empty-group">비어있는 그룹</div>
                    )}
                  </div>
                </div>
              ))}

              {(!groups || groups.length === 0) && (
                <div className="no-groups">
                  아직 생성된 그룹이 없습니다.
                  <button className="add-first-group-button" onClick={handleAddGroup}>
                    첫 그룹 추가하기
                  </button>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default GroupPanel;
