import React, { useState, useEffect } from 'react';
import { fetchHeaderSimilarityList } from '../services/api';
import Column from './Column';
import './Column.css';

const HeaderSimilarityList = ({ channel, selectedHeader, onSimilarHeadersGroup }) => {
  const [similarHeaders, setSimilarHeaders] = useState([]);
  const [selectedSimilars, setSelectedSimilars] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getSimilarHeaders = async () => {
      if (!channel || !selectedHeader) return;

      setLoading(true);
      try {
        const response = await fetchHeaderSimilarityList(channel, selectedHeader);
        console.log('Similar headers response:', response);
        setSimilarHeaders(response.list || []);
        setSelectedSimilars([]);
        setError(null);
      } catch (err) {
        setError(err.message);
        setSimilarHeaders([]);
      } finally {
        setLoading(false);
      }
    };

    getSimilarHeaders();
  }, [channel, selectedHeader]);

  const handleSelectSimilar = (headerItem) => {
    const header = headerItem.header;
    if (header === selectedHeader) return; // 자기 자신(메인 헤더)은 선택 불가

    setSelectedSimilars(prev => {
      // 이미 선택되어 있으면 제거, 아니면 추가
      if (prev.includes(header)) {
        const newSelected = prev.filter(item => item !== header);
        // 선택된 유사어 변경 시 콜백 호출
        if (onSimilarHeadersGroup) {
          onSimilarHeadersGroup(selectedHeader, newSelected);
        }
        return newSelected;
      } else {
        const newSelected = [...prev, header];
        // 선택된 유사어 변경 시 콜백 호출
        if (onSimilarHeadersGroup) {
          onSimilarHeadersGroup(selectedHeader, newSelected);
        }
        return newSelected;
      }
    });
  };

  const renderHeaderItem = (item) => {
    const isMain = item.header === selectedHeader;
    const isSelected = selectedSimilars.includes(item.header);

    return (
      <div className={`similar-header-item ${isMain ? 'main-header' : ''} ${isSelected ? 'selected' : ''}`}>
        <span className="header-name">{item.header}</span>
        {!isMain && (
          <button 
            className="toggle-similar-btn"
            onClick={(e) => {
              e.stopPropagation();
              handleSelectSimilar(item);
            }}
          >
            {isSelected ? '제거' : '선택'}
          </button>
        )}
      </div>
    );
  };

  // 트리 뷰로 유사어 표시
  const renderTreeView = () => {
    if (!similarHeaders.length) return null;

    // 메인 헤더 (맨 위에 표시)
    const mainHeader = similarHeaders.find(item => item.header === selectedHeader);

    return (
      <div className="similarity-tree-view">
        <div className="main-header-container">
          {mainHeader && (
            <div className="main-header-item">
              <span className="header-name">{mainHeader.header}</span>
              {selectedSimilars.length > 0 && (
                <span className="similar-count">유사어: {selectedSimilars.length}개</span>
              )}
            </div>
          )}
        </div>

        {selectedSimilars.length > 0 && (
          <div className="similar-headers-container">
            {selectedSimilars.map((similarHeader, idx) => {
              const headerItem = similarHeaders.find(item => item.header === similarHeader);
              return (
                <div key={idx} className="similar-header-tree-item">
                  <span className="tree-connector">└─</span>
                  <span className="header-name">{headerItem?.header}</span>
                  <button 
                    className="remove-similar-btn"
                    onClick={() => handleSelectSimilar(headerItem)}
                  >
                    제거
                  </button>
                </div>
              );
            })}
          </div>
        )}
      </div>
    );
  };

  if (!channel || !selectedHeader) {
    return (
      <div className="column-container">
        <div className="column-empty">채널과 헤더를 선택해주세요.</div>
      </div>
    );
  }

  return (
    <div className="column-container similarity-list-container">
      <h2 className="column-title">{`헤더명 유사군 목록: ${selectedHeader}`}</h2>

      {/* 트리 뷰 표시 */}
      {selectedSimilars.length > 0 && renderTreeView()}

      {/* 유사군 목록 */}
      <div className="column-content">
        {loading && <div className="loading">로딩 중...</div>}
        {error && <div className="error">에러: {error}</div>}

        {!loading && !error && similarHeaders.length > 0 ? (
          similarHeaders.map((item, index) => (
            <div
              key={index}
              className={`column-item similarity-item ${selectedSimilars.includes(item.header) ? 'grouped' : ''}`}
              onClick={() => handleSelectSimilar(item)}
            >
              {renderHeaderItem(item)}
            </div>
          ))
        ) : (
          !loading && !error && <div className="column-empty">유사 헤더 정보가 없습니다.</div>
        )}
      </div>
    </div>
  );
};

export default HeaderSimilarityList;
