import React, { useState, useEffect, useRef } from 'react';

const MiningView = ({ combinationHeaders, loading, error, testMode }) => {
  const [miningData, setMiningData] = useState(null);
  const [expandedNodes, setExpandedNodes] = useState({});
  const [highlightedPattern, setHighlightedPattern] = useState(null);
  const [algorithm, setAlgorithm] = useState('eclat'); // 'eclat' or 'fpgrowth'
  const [minSupport, setMinSupport] = useState(0.1); // 10% default
  const [minConfidence, setMinConfidence] = useState(0.5); // 50% default
  const [maxDepth, setMaxDepth] = useState(15); // Increased default depth for deeper patterns
  const svgRef = useRef(null);

  // 데이터가 변경될 때마다 마이닝 실행
  useEffect(() => {
    if (combinationHeaders && combinationHeaders.length > 0) {
      generateMiningData(combinationHeaders, algorithm);
    } else {
      setMiningData(null);
    }
  }, [combinationHeaders, algorithm, minSupport, minConfidence, maxDepth]);

  // Effect to update SVG visualization when mining data changes
  useEffect(() => {
    if (miningData && svgRef.current) {
      renderMiningTree();
    }
  }, [miningData, expandedNodes, highlightedPattern]);

  // Function to render a unified hierarchical tree visualization
  const renderMiningTree = () => {
    if (!svgRef.current || !miningData) return;

    const svg = svgRef.current;
    // Clear previous content
    while (svg.firstChild) {
      svg.removeChild(svg.firstChild);
    }

    const CONTAINER_WIDTH = svg.clientWidth || 300;
    const NODE_HEIGHT = 30; // Increased height for better spacing
    const NODE_SPACING = 8; // Increased spacing between nodes
    const LEVEL_INDENT = 25; // Increased indent for better hierarchy visualization

    // Calculate tree structure based on expanded nodes
    const treeNodes = [];
    let yOffset = 20;  // Initial top padding

    // Add root node first
    treeNodes.push({
      id: 'root',
      name: '전체 패턴',
      support: miningData.reduce((sum, item) => sum + item.support, 0),
      confidence: 1,
      level: 0,
      x: 10,
      y: yOffset,
      isExpanded: true,
      parent: null
    });

    yOffset += NODE_HEIGHT + NODE_SPACING;

    // Recursive function to add child nodes
    const addNodesRecursively = (nodes, parentId, level, isVisible) => {
      if (!nodes || nodes.length === 0) return;

      // Sort nodes by support for better visualization
      const sortedNodes = [...nodes].sort((a, b) => b.support - a.support);

      sortedNodes.forEach(node => {
        // Skip if parent is collapsed
        if (!isVisible) return;

        const isExpanded = expandedNodes[node.id] || false;

        // Add current node
        treeNodes.push({
          ...node,
          level,
          x: 10 + (level * LEVEL_INDENT),
          y: yOffset,
          isExpanded,
          parent: parentId
        });

        // Increment y-position for next node
        yOffset += NODE_HEIGHT + NODE_SPACING;

        // Process children if any
        if (node.children && node.children.length > 0) {
          addNodesRecursively(node.children, node.id, level + 1, isVisible && isExpanded);
        }
      });
    };

    // Add all nodes from mining data starting from the root
    addNodesRecursively(miningData, 'root', 1, true);

    // Set SVG height based on content
    svg.setAttribute('height', `${yOffset + 20}px`);  // Add bottom padding

    // Create a container for the SVG elements
    const container = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    svg.appendChild(container);

    // Draw connections between nodes
    treeNodes.forEach(node => {
      if (node.parent) {
        const parent = treeNodes.find(n => n.id === node.parent);
        if (parent) {
          const path = document.createElementNS('http://www.w3.org/2000/svg', 'path');

          const startX = parent.x + 8;  // Start from parent indentation + offset
          const startY = parent.y + (NODE_HEIGHT / 2);
          const endX = node.x;
          const endY = node.y + (NODE_HEIGHT / 2);

          // Create an elbow connector (straight lines with 90-degree turn)
          const d = `M ${startX} ${startY} L ${startX + 5} ${startY} L ${startX + 5} ${endY} L ${endX} ${endY}`;

          path.setAttribute('d', d);
          path.setAttribute('fill', 'none');

          // Highlight path if node is part of highlighted pattern
          const isHighlighted = highlightedPattern && (
            highlightedPattern.includes(node.id) && highlightedPattern.includes(parent.id)
          );

          path.setAttribute('stroke', isHighlighted ? '#999' : '#eee');
          path.setAttribute('stroke-width', isHighlighted ? '1.5' : '1');

          container.appendChild(path);
        }
      }
    });

    // Draw nodes
    treeNodes.forEach(node => {
      const nodeGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
      nodeGroup.setAttribute('transform', `translate(${node.x}, ${node.y})`);

      // Node background (rectangle)
      const rect = document.createElementNS('http://www.w3.org/2000/svg', 'rect');

      // Calculate width based on content and level
      const baseWidth = CONTAINER_WIDTH - node.x - 30;
      const width = Math.min(baseWidth, 220);  // Limit max width

      rect.setAttribute('width', width);
      rect.setAttribute('height', NODE_HEIGHT);
      rect.setAttribute('rx', 4);  // Rounded corners

      // Determine fill color based on level and highlight state
      const isHighlighted = highlightedPattern && highlightedPattern.includes(node.id);

      // Use a more monotone color scheme
      let fillColor = '#f7f7f7'; // Single base color for all nodes

      if (isHighlighted) {
        fillColor = '#f0f0f0'; // Slightly different shade for highlighted nodes
      }

      rect.setAttribute('fill', fillColor);
      rect.setAttribute('stroke', isHighlighted ? '#999' : '#ddd');
      rect.setAttribute('stroke-width', isHighlighted ? '1.5' : '1');

      // Make nodes interactive
      if ((node.children && node.children.length > 0) || node.id === 'root') {
        nodeGroup.style.cursor = 'pointer';

        // Toggle expansion on click
        nodeGroup.addEventListener('click', () => {
          toggleNode(node.id);
        });
      }

      // Hover effect for highlighting patterns
      nodeGroup.addEventListener('mouseenter', () => {
        // Find all ancestors and highlight the path
        const pattern = [node.id];
        let currentNode = node;

        while (currentNode.parent) {
          pattern.push(currentNode.parent);
          currentNode = treeNodes.find(n => n.id === currentNode.parent);
          if (!currentNode) break;
        }

        setHighlightedPattern(pattern);
      });

      nodeGroup.addEventListener('mouseleave', () => {
        setHighlightedPattern(null);
      });

      // Add expansion indicator if node has children
      if ((node.children && node.children.length > 0) || node.id === 'root') {
        const indicator = document.createElementNS('http://www.w3.org/2000/svg', 'text');
        indicator.textContent = node.isExpanded ? '−' : '+';
        indicator.setAttribute('x', width - 15);
        indicator.setAttribute('y', NODE_HEIGHT / 2 + 4);
        indicator.setAttribute('font-size', '12px');
        indicator.setAttribute('font-weight', 'bold');
        indicator.setAttribute('fill', '#888');
        nodeGroup.appendChild(indicator);
      }

      // Node label - truncate if needed
      const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      text.setAttribute('x', 8);
      text.setAttribute('y', NODE_HEIGHT / 2 + 4);
      text.setAttribute('font-size', node.id === 'root' ? '12px' : '11px');
      text.setAttribute('font-weight', node.id === 'root' ? 'bold' : 'normal');
      text.setAttribute('fill', '#333');

      // For root, show the name directly
      if (node.id === 'root') {
        text.textContent = node.name;
      } else {
        // For regular nodes, make sure text fits
        const maxTextWidth = width - 60;  // Leave room for support and indicator
        let displayText = node.name;

        if (displayText.length > 20) {
          displayText = displayText.substring(0, 18) + '...';
        }

        text.textContent = displayText;
      }

      // Support count
      const supportText = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      supportText.setAttribute('x', width - 30);
      supportText.setAttribute('y', NODE_HEIGHT / 2 + 4);
      supportText.setAttribute('font-size', '11px');
      supportText.setAttribute('text-anchor', 'end');
      supportText.setAttribute('fill', '#777');
      supportText.textContent = node.support;

      nodeGroup.appendChild(rect);
      nodeGroup.appendChild(text);
      nodeGroup.appendChild(supportText);
      container.appendChild(nodeGroup);
    });
  };

  // Function to generate mining data using Eclat or FP Growth algorithm
  const generateMiningData = (combinationData, algo = 'eclat') => {
    if (!combinationData || combinationData.length === 0) {
      setMiningData(null);
      return;
    }

    // Step 1: Create transactions from header combinations
    const transactions = combinationData
      .filter(item => item.header_group)
      .map(item => item.header_group.split('|'));

    // Step 2: Count item frequencies for both algorithms
    const itemCounts = {};
    const totalTransactions = transactions.length;

    transactions.forEach(transaction => {
      transaction.forEach(item => {
        itemCounts[item] = (itemCounts[item] || 0) + 1;
      });
    });

    // Calculate minimum support threshold - use absolute minimum of 2
    const minSupportCount = Math.max(2, Math.floor(totalTransactions * minSupport));

    // Get frequent items sorted by frequency
    const frequentItems = Object.keys(itemCounts)
      .filter(item => itemCounts[item] >= minSupportCount)
      .sort((a, b) => itemCounts[b] - itemCounts[a]);

    if (algo === 'eclat') {
      // ECLAT ALGORITHM IMPLEMENTATION

      // Step 1: Build vertical tid-lists (transaction ID lists) for each item
      const tidLists = {};
      frequentItems.forEach(item => {
        tidLists[item] = new Set();
      });

      transactions.forEach((transaction, tid) => {
        transaction.forEach(item => {
          if (frequentItems.includes(item)) {
            tidLists[item].add(tid);
          }
        });
      });

      // Step 2: Find frequent itemsets using ECLAT recursion
      const frequentItemsets = {};

      // Initialize with single items
      frequentItems.forEach(item => {
        const support = tidLists[item].size;
        frequentItemsets[item] = {
          support,
          confidence: support / totalTransactions,
          tids: tidLists[item],
          children: {}
        };
      });

      // Recursive function to find frequent itemsets (allowing deeper patterns)
      const eclat = (prefix, tidList, level, parent) => {
        if (level > maxDepth) return; // Increased depth limit

        // Find extensions with all items that come after the prefix's last item
        const lastItem = prefix[prefix.length - 1];
        const lastItemIndex = frequentItems.indexOf(lastItem);

        for (let i = lastItemIndex + 1; i < frequentItems.length; i++) {
          const item = frequentItems[i];
          const itemTids = tidLists[item];

          // Compute intersection
          const intersection = new Set([...tidList].filter(tid => itemTids.has(tid)));
          const support = intersection.size;

          // If support is high enough, add to frequent itemsets
          // Modified to use absolute minimum count of 2 rather than percentage-based
          if (support >= Math.max(2, minSupportCount)) {
            const newPrefix = [...prefix, item];
            const newPrefixStr = item; // Just use the extension item as key

            // Calculate confidence (support of extended pattern / support of prefix)
            const confidence = support / tidList.size;

            // Modified confidence check to be lower for deeper patterns
            if (confidence >= (level > 3 ? minConfidence/2 : minConfidence)) {
              parent.children[newPrefixStr] = {
                support,
                confidence,
                tids: intersection,
                children: {}
              };

              // Recursively find extensions of this new prefix
              eclat(newPrefix, intersection, level + 1, parent.children[newPrefixStr]);
            }
          }
        }
      };

      // Start ECLAT recursion for each single item
      frequentItems.forEach(item => {
        eclat([item], tidLists[item], 1, frequentItemsets[item]);
      });

      // Convert to array format for visualization
      const buildDeepNestedTree = (itemsets) => {
        const result = [];

        frequentItems.forEach(item => {
          const itemData = {
            id: item,
            name: item,
            support: tidLists[item].size,
            confidence: tidLists[item].size / totalTransactions,
            children: []
          };

          // 재귀적으로 모든 자식 노드를 추가하는 함수
          const addChildrenRecursively = (parentId, parentNode, currentItemset) => {
            const children = currentItemset.children || {};

            Object.entries(children).forEach(([childKey, childValue]) => {
              const childId = `${parentId}-${childKey}`;
              const childNode = {
                id: childId,
                name: childKey,
                support: childValue.support,
                confidence: childValue.confidence,
                children: []
              };

              parentNode.children.push(childNode);

              // 재귀적으로 손자 노드 처리
              addChildrenRecursively(childId, childNode, childValue);
            });
          };

          // 첫 레벨 자식 노드 추가
          addChildrenRecursively(item, itemData, frequentItemsets[item]);

          result.push(itemData);
        });

        return result;
      };

      const result = buildDeepNestedTree(frequentItemsets);
      setMiningData(result);

    } else {
      // FP GROWTH ALGORITHM - UNIFIED TREE IMPLEMENTATION

      // Step 3: Reorder transactions based on frequency
      const orderedTransactions = transactions.map(transaction => {
        return transaction
          .filter(item => frequentItems.includes(item))
          .sort((a, b) => itemCounts[b] - itemCounts[a]);
      }).filter(transaction => transaction.length > 0);

      // Step 4: Build unified FP-Tree
      const fpTree = { 
        name: "root", 
        count: 0,
        children: {},
        nodeLinks: {} // Header table node links
      };

      // Initialize header table
      const headerTable = {};
      frequentItems.forEach(item => {
        headerTable[item] = {
          count: itemCounts[item],
          nodeLink: null
        };
      });

      // Insert a transaction into the FP-Tree
      const insertTransaction = (transaction, tree, count = 1) => {
        if (transaction.length === 0) return;

        const item = transaction[0];

        // Create node if it doesn't exist
        if (!tree.children[item]) {
          tree.children[item] = {
            name: item,
            count: 0,
            children: {},
            parent: tree,
            nextSameItem: null
          };

          // Update header table node links
          if (!headerTable[item].nodeLink) {
            headerTable[item].nodeLink = tree.children[item];
          } else {
            let current = headerTable[item].nodeLink;
            while (current.nextSameItem) {
              current = current.nextSameItem;
            }
            current.nextSameItem = tree.children[item];
          }
        }

        // Increment count
        tree.children[item].count += count;

        // Insert remaining items
        if (transaction.length > 1) {
          insertTransaction(transaction.slice(1), tree.children[item], count);
        }
      };

      // Insert all transactions
      orderedTransactions.forEach(transaction => {
        insertTransaction(transaction, fpTree);
      });

      // Step 5: Mine patterns using FP-Growth
      const patterns = {};

      // Mine patterns with a suffix
      const minePatterns = (headerTable, conditionalBase, suffix = [], parentSupport = totalTransactions) => {
        // Get items in descending order of frequency
        const items = Object.keys(headerTable).sort((a, b) => headerTable[b].count - headerTable[a].count);

        for (const item of items) {
          // New frequent itemset found
          const newSuffix = [item, ...suffix];
          const supportCount = headerTable[item].count;

          // Calculate confidence for patterns with len > 1
          const confidence = newSuffix.length > 1 ? supportCount / parentSupport : supportCount / totalTransactions;

          // Only include if confidence meets threshold
          if (confidence >= minConfidence) {
            // Store pattern
            const patternKey = newSuffix.join('-');
            patterns[patternKey] = {
              items: newSuffix,
              support: supportCount,
              confidence: confidence
            };

            // Build conditional pattern base
            const conditionalPatternBase = [];
            let node = headerTable[item].nodeLink;

            while (node) {
              // Traverse upward to get the path
              const path = [];
              let prefixSupport = node.count;
              let parent = node.parent;

              while (parent && parent.name !== "root") {
                path.unshift(parent.name);
                parent = parent.parent;
              }

              if (path.length > 0) {
                conditionalPatternBase.push({
                  path,
                  count: prefixSupport
                });
              }

              node = node.nextSameItem;
            }

            // Build conditional FP-Tree
            if (conditionalPatternBase.length > 0) {
              const newConditionalBase = {};

              // Count items in the conditional pattern base
              conditionalPatternBase.forEach(({path, count}) => {
                path.forEach(pathItem => {
                  newConditionalBase[pathItem] = (newConditionalBase[pathItem] || 0) + count;
                });
              });

              // Filter items by minimum support
              const newHeaderTable = {};
              Object.keys(newConditionalBase).forEach(item => {
                // Use a fixed threshold of 2 for deeper patterns
                if (newConditionalBase[item] >= Math.max(2, minSupportCount)) {
                  newHeaderTable[item] = {
                    count: newConditionalBase[item],
                    nodeLink: null
                  };
                }
              });

              // Recursively mine with the new conditional tree, up to more depth
              if (Object.keys(newHeaderTable).length > 0 && newSuffix.length < maxDepth) {
                minePatterns(newHeaderTable, conditionalPatternBase, newSuffix, supportCount);
              }
            }
          }
        }
      };

      // Start mining
      minePatterns(headerTable, []);

      // Build tree from patterns
      const buildTree = (patterns) => {
        const result = [];
        const itemMap = {};

        // Group by first item
        Object.values(patterns).forEach(pattern => {
          if (pattern.items.length > 0) {
            const firstItem = pattern.items[0];
            if (!itemMap[firstItem]) {
              itemMap[firstItem] = {
                id: firstItem,
                name: firstItem,
                support: itemCounts[firstItem],
                confidence: itemCounts[firstItem] / totalTransactions,
                children: []
              };
              result.push(itemMap[firstItem]);
            }

            // Add subsequent items as children
            if (pattern.items.length > 1) {
              let currentNode = itemMap[firstItem];
              let currentPath = firstItem;

              for (let i = 1; i < pattern.items.length; i++) {
                const currentItem = pattern.items[i];
                currentPath = `${currentPath}-${currentItem}`;

                // Check if child already exists
                let childNode = currentNode.children.find(child => child.name === currentItem);

                if (!childNode) {
                  childNode = {
                    id: currentPath,
                    name: currentItem,
                    support: pattern.support,
                    confidence: pattern.confidence,
                    children: []
                  };
                  currentNode.children.push(childNode);
                }

                currentNode = childNode;
              }
            }
          }
        });

        return result;
      };

      const result = buildTree(patterns);
      setMiningData(result);
    }

    // Initialize expanded state for visualization
    const initialExpanded = { 'root': true };
    // Auto-expand some top nodes for better initial view
    if (miningData && miningData.length > 0) {
      // Expand first 3 level 1 nodes
      miningData.slice(0, 3).forEach(node => {
        initialExpanded[node.id] = true;
      });
    }
    setExpandedNodes(initialExpanded);
  };

  // Switch between algorithms
  const switchAlgorithm = (algo) => {
    setAlgorithm(algo);
  };

  // Change minimum support threshold
  const handleSupportChange = (e) => {
    const value = parseFloat(e.target.value);
    if (!isNaN(value) && value > 0 && value <= 1) {
      setMinSupport(value);
    }
  };

  // Change minimum confidence threshold
  const handleConfidenceChange = (e) => {
    const value = parseFloat(e.target.value);
    if (!isNaN(value) && value > 0 && value <= 1) {
      setMinConfidence(value);
    }
  };

  // Change maximum depth
  const handleDepthChange = (e) => {
    const value = parseInt(e.target.value);
    if (!isNaN(value) && value > 0) {
      setMaxDepth(value);
    }
  };

  // Function to toggle node expansion
  const toggleNode = (nodeId) => {
    setExpandedNodes(prev => ({
      ...prev,
      [nodeId]: !prev[nodeId]
    }));
  };

  return (
    <div className="tab-content">
      {loading ? (
        <div className="loading">로딩 중...</div>
      ) : error ? (
        <div className="error">에러: {error}</div>
      ) : combinationHeaders && combinationHeaders.length > 0 ? (
        <div className="mining-view">
          <div className="mining-controls">
            <div className="algorithm-selector">
              <button 
                className={`algorithm-btn ${algorithm === 'eclat' ? 'active' : ''}`} 
                onClick={() => switchAlgorithm('eclat')}
              >
                Eclat 알고리즘
              </button>
              <button 
                className={`algorithm-btn ${algorithm === 'fpgrowth' ? 'active' : ''}`} 
                onClick={() => switchAlgorithm('fpgrowth')}
              >
                FP-Growth 알고리즘
              </button>
            </div>
            <div className="thresholds-control">
              <div className="threshold-item">
                <label>최소 지지도: {(minSupport * 100).toFixed(0)}%</label>
                <input 
                  type="range" 
                  min="0.01" 
                  max="0.5" 
                  step="0.05" 
                  value={minSupport}
                  onChange={handleSupportChange}
                />
              </div>
              <div className="threshold-item">
                <label>최소 신뢰도: {(minConfidence * 100).toFixed(0)}%</label>
                <input 
                  type="range" 
                  min="0.01" 
                  max="0.9" 
                  step="0.1" 
                  value={minConfidence}
                  onChange={handleConfidenceChange}
                />
              </div>
              <div className="threshold-item">
                <label>최대 패턴 깊이: {maxDepth}</label>
                <input 
                  type="range" 
                  min="3" 
                  max="20" 
                  step="1" 
                  value={maxDepth}
                  onChange={handleDepthChange}
                />
              </div>
            </div>
          </div>

          <div className="mining-tree-container">
            <svg 
              className="mining-tree-svg" 
              ref={svgRef} 
              width="100%" 
              height="400"
            ></svg>
          </div>

          <div className="mining-explanation">
            <p>{algorithm === 'eclat' ? 'Eclat' : 'FP-Growth'} 알고리즘 패턴 시각화:</p>
            <ul>
              <li>패턴 클릭: 하위 패턴 확장/축소</li>
              <li>마우스 오버: 패턴 경로 하이라이트</li>
              <li>숫자: 패턴 발생 빈도 (최소 2회 이상)</li>
            </ul>
          </div>
        </div>
      ) : (
        <div className="column-empty">
          {testMode 
            ? "마이닝 데이터를 로딩 중입니다..." 
            : "채널과 헤더를 선택하면 마이닝 결과가 표시됩니다."}
        </div>
      )}

      <style jsx>{`
        .mining-view {
          height: 100%;
          display: flex;
          flex-direction: column;
        }

        .mining-controls {
          display: flex;
          flex-direction: column;
