import React, { useState, useEffect } from 'react';
import { fetchHeaderCombinationList } from '../services/api';
import Column from './Column';
import './Column.css';

const HeaderCombinationList = ({ channel, selectedHeader, onCombinationSelect, selectedCombination }) => {
  const [combinationHeaders, setCombinationHeaders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('combination'); // 'combination' or 'network'

  useEffect(() => {
    const getCombinationHeaders = async () => {
      if (!channel || !selectedHeader) return;

      setLoading(true);
      try {
        const headers = Array.isArray(selectedHeader) ? selectedHeader : [selectedHeader];
        const response = await fetchHeaderCombinationList(channel, headers);
        console.log('Combination headers response:', response);
        setCombinationHeaders(response.list || []);
        setError(null);
      } catch (err) {
        setError(err.message);
        setCombinationHeaders([]);
      } finally {
        setLoading(false);
      }
    };

    getCombinationHeaders();
  }, [channel, selectedHeader]);

  const handleCombinationClick = (combination) => {
    if (onCombinationSelect) {
      onCombinationSelect(combination);
    }
  };

  // Function to render a header group as tags
  const renderHeaderGroupAsTags = (headerGroup) => {
    if (!headerGroup) return null;

    const tags = headerGroup.split('|').map((tag, index) => (
      <span key={index} className="selected-tag">
        <span className="tag-text">{tag.trim()}</span>
      </span>
    ));

    return (
      <div className="selected-items-horizontal">
        {tags}
      </div>
    );
  };

  if (!channel || !selectedHeader) {
    return (
      <div className="column-container">
        <div className="column-empty">채널과 헤더를 선택해주세요.</div>
      </div>
    );
  }

  return (
    <div className="column-container">
      <h2 className="column-title">
        헤더명 조합 목록: {selectedHeader}
      </h2>

      <div className="tabs">
        <button 
          className={`tab ${activeTab === 'combination' ? 'active' : ''}`}
          onClick={() => setActiveTab('combination')}
        >
          조합뷰
        </button>
        <button 
          className={`tab ${activeTab === 'network' ? 'active' : ''}`}
          onClick={() => setActiveTab('network')}
        >
          네트워크뷰
        </button>
      </div>

      <div className="tab-content">
        {loading ? (
          <div className="loading">로딩 중...</div>
        ) : error ? (
          <div className="error">에러: {error}</div>
        ) : activeTab === 'combination' ? (
          combinationHeaders.length > 0 ? (
            <div className="combination-view">
              {combinationHeaders.map((item, index) => (
                <div
                  key={index}
                  className={`column-item ${selectedCombination && selectedCombination.header_group === item.header_group ? 'selected' : ''}`}
                  onClick={() => handleCombinationClick(item)}
                >
                  {renderHeaderGroupAsTags(item.header_group)}
                </div>
              ))}
            </div>
          ) : (
            <div className="column-empty">헤더 조합 정보가 없습니다.</div>
          )
        ) : (
          // Network view (placeholder for now)
          <div className="network-view column-empty">
            네트워크 뷰는 개발 중입니다.
          </div>
        )}
      </div>
    </div>
  );
};

export default HeaderCombinationList;
