import React, { useState, useEffect } from 'react';
import './ChannelList.css';
import { fetchChannelList, fetchHeaderList } from '../services/api';

const ChannelList = () => {
  const [channels, setChannels] = useState([]);
  const [headers, setHeaders] = useState([]);
  const [selectedChannel, setSelectedChannel] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getChannels = async () => {
      try {
        const data = await fetchChannelList();
        setChannels(data.list || []);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    getChannels();
  }, []);

  const handleChannelClick = async (channel) => {
    try {
      setSelectedChannel(channel);
      setLoading(true);
      const data = await fetchHeaderList(channel.채널키);
      setHeaders(data.list || []);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  if (loading && !selectedChannel) return <div className="loading">로딩 중...</div>;
  if (error) return <div className="error">에러: {error}</div>;

  return (
    <div className="channel-list-container">
      <h2>채널 목록</h2>
      <div className="channel-list">
        {channels && channels.length > 0 ? (
          channels.map((channel) => (
            <div 
              key={channel._id} 
              className={`channel-item ${selectedChannel && selectedChannel._id === channel._id ? 'selected' : ''}`}
              onClick={() => handleChannelClick(channel)}
            >
              <div className="channel-info">
                <h3>{channel.채널키 || 'Unknown Channel'}</h3>
              </div>
            </div>
          ))
        ) : (
          <div className="no-channels">채널 정보가 없습니다.</div>
        )}
      </div>

      {selectedChannel && (
        <div className="header-list-container">
          <h2>헤더명 목록: {selectedChannel.채널키}</h2>
          {loading ? (
            <div className="loading">헤더 정보 로딩 중...</div>
          ) : (
            <div className="header-list">
              {headers && headers.length > 0 ? (
                headers.map((header, index) => (
                  <div key={index} className="header-item">
                    <div className="header-info">
                      <h3>{header.헤더명 || header.name || 'Unknown Header'}</h3>
                    </div>
                  </div>
                ))
              ) : (
                <div className="no-headers">헤더 정보가 없습니다.</div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ChannelList;
