import React, { useState } from 'react';

const SegmentationGrouping = ({ 
  config, 
  onUpdateConfig, 
  onAddGroup, 
  onUpdateGroupName,
  segmentationData
}) => {
  const [replaceNumbers, setReplaceNumbers] = useState(config.replaceNumbers || false);

  // Define groups handling logic
  const handleAddGroup = () => {
    if (onAddGroup) onAddGroup();
  };

  const handleGroupNameChange = (groupId, newName) => {
    if (onUpdateGroupName) onUpdateGroupName(groupId, newName);
  };

  const handleToggleReplaceNumbers = () => {
    const newValue = !replaceNumbers;
    setReplaceNumbers(newValue);
    onUpdateConfig({ replaceNumbers: newValue });
  };

  return (
    <div className="segmentation-grouping">
      <div className="grouping-options">
        <div className="option-item">
          <label>
            <input
              type="checkbox"
              checked={replaceNumbers}
              onChange={handleToggleReplaceNumbers}
            />
            숫자 패턴 치환 (\d+)
          </label>
          <p className="option-description">숫자값을 \d+ 패턴으로 치환하여 그룹화합니다.</p>
        </div>
      </div>

      <div className="groups-container">
        <h4>그룹 목록</h4>
        {config.groups && config.groups.length > 0 ? (
          <div className="group-list">
            {config.groups.map(group => (
              <div key={group.id} className="group-item">
                <input
                  type="text"
                  value={group.name}
                  onChange={(e) => handleGroupNameChange(group.id, e.target.value)}
                  placeholder="그룹 이름"
                />
                <div className="group-items">
                  {group.items && group.items.map((item, idx) => (
                    <span key={idx} className="group-item-tag">{item}</span>
                  ))}
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p>정의된 그룹이 없습니다.</p>
        )}
        <button className="add-group-btn" onClick={handleAddGroup}>
          + 그룹 추가
        </button>
      </div>
    </div>
  );
};

export default SegmentationGrouping;
