import React, { useState } from 'react';
import './ControlColumn.css';

// Function for bracket value separation
const separateBracketValues = (data) => {
  // If data is a string, extract values inside brackets
  if (typeof data === 'string') {
    const regex = /\[(.*?)\]|\((.*?)\)|\{(.*?)\}/g;
    const matches = [];
    let match;

    while ((match = regex.exec(data)) !== null) {
      // Find which capturing group has content (brackets, parentheses, or braces)
      const content = match[1] || match[2] || match[3];
      matches.push(content);
    }

    return matches.length > 0 ? matches : [data];
  }

  // If data is an array, process each item
  if (Array.isArray(data)) {
    return data.flatMap(item => separateBracketValues(item));
  }

  // If data is an object, process each value
  if (typeof data === 'object' && data !== null) {
    const result = [];
    for (const key in data) {
      const separated = separateBracketValues(data[key]);
      // Create objects with the original key and the separated values
      if (Array.isArray(separated)) {
        separated.forEach(value => {
          result.push({ [key]: value });
        });
      } else {
        result.push({ [key]: separated });
      }
    }
    return result;
  }

  // Return as is for other data types
  return [data];
};

const ControlColumn = ({ data, onProcessedDataChange }) => {
  const [chatInput, setChatInput] = useState('');
  const [chatMessages, setChatMessages] = useState([
    { role: 'system', content: 'How can I help you with your data processing today?' }
  ]);
  const [activeTab, setActiveTab] = useState('options');
  const [pipeline, setPipeline] = useState([
    { id: 'bracketSeparation', name: '괄호값 분리', enabled: true }
  ]);

  // Process data through the pipeline
  const processData = () => {
    if (!data) return null;

    let processedData = data;

    // Apply each enabled pipeline step
    pipeline.forEach(step => {
      if (step.enabled) {
        if (step.id === 'bracketSeparation') {
          processedData = separateBracketValues(processedData);
        }
        // Add more pipeline steps here in the future
      }
    });

    // Update the parent component with processed data
    if (onProcessedDataChange) {
      onProcessedDataChange(processedData);
    }

    return processedData;
  };

  // Toggle pipeline step
  const togglePipelineStep = (stepId) => {
    setPipeline(pipeline.map(step => 
      step.id === stepId ? { ...step, enabled: !step.enabled } : step
    ));
  };

  const handleSendMessage = () => {
    if (chatInput.trim() === '') return;

    // Add user message
    const newMessages = [...chatMessages, { role: 'user', content: chatInput }];
    setChatMessages(newMessages);

    // Simulate LLM response
    setTimeout(() => {
      setChatMessages([
        ...newMessages,
        { 
          role: 'system', 
          content: 'I understand you want to work with this data. Would you like to split it by item properties or analyze it as is?' 
        }
      ]);
    }, 1000);

    setChatInput('');
  };

  const handleInputChange = (e) => {
    setChatInput(e.target.value);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Process data whenever pipeline changes
  React.useEffect(() => {
    processData();
  }, [pipeline, data]);

  return (
    <div className="column control-column">
      <div className="control-tabs">
        <button 
          className={`control-tab ${activeTab === 'llm' ? 'active' : ''}`}
          onClick={() => setActiveTab('llm')}
        >
          LLM Co-working
        </button>
        <button 
          className={`control-tab ${activeTab === 'options' ? 'active' : ''}`}
          onClick={() => setActiveTab('options')}
        >
          Process Options
        </button>
      </div>

      {activeTab === 'llm' && (
        <div className="llm-interface">
          <div className="chat-messages">
            {chatMessages.map((msg, index) => (
              <div key={index} className={`message ${msg.role}`}>
                <div className="message-content">{msg.content}</div>
              </div>
            ))}
          </div>

          <div className="chat-input-container">
            <textarea 
              className="chat-input"
              value={chatInput}
              onChange={handleInputChange}
              onKeyPress={handleKeyPress}
              placeholder="Ask about your data..."
              rows={3}
            />
            <button 
              className="send-button"
              onClick={handleSendMessage}
              disabled={chatInput.trim() === ''}
            >
              Send
            </button>
          </div>
        </div>
      )}

      {activeTab === 'options' && (
        <div className="process-options">
          <h4>Pipeline Steps</h4>

          <div className="pipeline-steps">
            {pipeline.map(step => (
              <div key={step.id} className="pipeline-step">
                <div className="step-header">
                  <input
                    type="checkbox"
                    id={`step-${step.id}`}
                    checked={step.enabled}
                    onChange={() => togglePipelineStep(step.id)}
                  />
                  <label htmlFor={`step-${step.id}`}>{step.name}</label>
                </div>
                {step.id === 'bracketSeparation' && (
                  <div className="step-description">
                    Separates values enclosed in brackets, parentheses, and braces into individual items.
                  </div>
                )}
              </div>
            ))}
          </div>

          <div className="settings-section">
            <h5>Advanced Settings</h5>

            <div className="setting-item">
              <label>Key path delimiter:</label>
              <input type="text" defaultValue="." />
            </div>
            <div className="setting-item">
              <label>Max depth:</label>
              <input type="number" defaultValue={3} />
            </div>
            <div className="setting-item checkbox">
              <input type="checkbox" id="preserveStruc" defaultChecked />
              <label htmlFor="preserveStruc">Preserve structure</label>
            </div>
          </div>

          <div className="settings-section">
            <h5>Segment Options</h5>
            <div className="setting-item">
              <label>Segment size limit:</label>
              <input type="number" defaultValue={1024} />
            </div>
            <div className="setting-item">
              <label>Overlap strategy:</label>
              <select>
                <option>None</option>
                <option>Fixed size</option>
                <option>Percentage</option>
              </select>
            </div>
          </div>

          <div className="settings-section">
            <h5>Classification Options</h5>
            <div className="setting-item">
              <label>Confidence threshold:</label>
              <input type="range" min="0" max="100" defaultValue={75} />
              <span>75%</span>
            </div>
            <div className="setting-item checkbox">
              <input type="checkbox" id="automerge" />
              <label htmlFor="automerge">Auto-merge similar categories</label>
            </div>
          </div>

          <button 
            className="apply-pipeline-button"
            onClick={processData}
          >
            Apply Pipeline
          </button>
        </div>
      )}
    </div>
  );
};

export default ControlColumn;
