import React, { useState, useEffect } from 'react';
import './FileExplorerColumn.css';

const FileExplorerColumn = () => {
  // Initialize with sample data from the src/browse_mode_sample_data directory
  const [files, setFiles] = useState([
    {
      id: 'root',
      name: 'browse_mode_sample_data',
      type: 'folder',
      isOpen: true,
      children: [
        {
          id: 'product',
          name: 'ÏÉÅÌíà',
          type: 'folder',
          isOpen: false,
          children: [
            { id: 'coupang', name: 'Ïø†Ìå°.xlsx', type: 'file' }
          ]
        }
      ]
    }
  ]);

  const [searchTerm, setSearchTerm] = useState('');

  // Mock function to simulate fetching files from a real filesystem
  // In a real app, this would use an API call to get the actual directory structure
  const fetchFileStructure = () => {
    // The initial files state already contains our mock structure
    // In a real implementation, this would be an API call to the backend
    console.log("Fetched file structure from src/browse_mode_sample_data");
  };

  useEffect(() => {
    // Simulate fetching the file structure when component mounts
    fetchFileStructure();
  }, []);

  const toggleFolder = (id) => {
    setFiles(prevFiles => {
      const updateNode = (nodes) => {
        return nodes.map(node => {
          if (node.id === id) {
            return { ...node, isOpen: !node.isOpen };
          }
          if (node.children) {
            return { ...node, children: updateNode(node.children) };
          }
          return node;
        });
      };
      return updateNode(prevFiles);
    });
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const filterFilesBySearch = (nodes, term) => {
    if (!term) return nodes;

    const filterNodes = (nodeList) => {
      return nodeList.filter(node => {
        const nameMatch = node.name.toLowerCase().includes(term.toLowerCase());

        if (node.children && node.children.length > 0) {
          const filteredChildren = filterNodes(node.children);
          if (filteredChildren.length > 0) {
            node.children = filteredChildren;
            node.isOpen = true; // Open folders that have matching children
            return true;
          }
        }

        return nameMatch;
      });
    };

    return filterNodes([...nodes]);
  };

  const renderTree = (nodes) => {
    return nodes.map(node => (
      <div key={node.id} className="tree-node">
        <div 
          className={`tree-item ${node.type}`}
          onClick={() => node.type === 'folder' && toggleFolder(node.id)}
        >
          <span className="icon">
            {node.type === 'folder' ? (node.isOpen ? 'üìÇ' : 'üìÅ') : getFileIcon(node.name)}
          </span>
          <span className="name">{node.name}</span>
        </div>
        {node.type === 'folder' && node.isOpen && node.children && (
          <div className="tree-children">
            {renderTree(node.children)}
          </div>
        )}
      </div>
    ));
  };

  // Helper function to get appropriate icon based on file extension
  const getFileIcon = (filename) => {
    const extension = filename.split('.').pop().toLowerCase();

    switch (extension) {
      case 'pdf':
        return 'üìï';
      case 'doc':
      case 'docx':
        return 'üìò';
      case 'xls':
      case 'xlsx':
        return 'üìó';
      case 'jpg':
      case 'jpeg':
      case 'png':
      case 'gif':
        return 'üñºÔ∏è';
      case 'txt':
        return 'üìÑ';
      case 'json':
        return 'üìã';
      default:
        return 'üìÑ';
    }
  };

  const filteredFiles = searchTerm 
    ? filterFilesBySearch(files, searchTerm) 
    : files;

  return (
    <div className="column file-explorer-column">
      <h3>File Explorer</h3>
      <div className="search-box">
        <input 
          type="text" 
          placeholder="Search files..." 
          value={searchTerm}
          onChange={handleSearchChange}
        />
      </div>
      <div className="file-path">
        <span>src/browse_mode_sample_data</span>
      </div>
      <div className="tree-view">
        {renderTree(filteredFiles)}
      </div>
    </div>
  );
};

export default FileExplorerColumn;
