import axios from 'axios';

const API_BASE_URL = 'https://api-backend.nventure.co.kr/solution';
const AUTH_TOKEN = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..BGbPFVbUSF04rnPA7p23kB1uaIY_ouX9KOVxGYyxnxE';

// API 요청에 사용할 기본 설정
const apiClient = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
    'Authorization': AUTH_TOKEN
  },
  maxBodyLength: Infinity
});

// 채널 목록 조회 API
export const fetchChannelList = async () => {
  try {
    const response = await apiClient.get('/crawlChannelList');
    return response.data;
  } catch (error) {
    console.error('채널 목록 조회 실패:', error);
    throw error;
  }
};

// 헤더명 목록 조회 API
export const fetchHeaderList = async (channel, size = 100, afterKey = '') => {
  try {
    const data = {
      channel: channel,
      size: size,
      afterKey: afterKey
    };

    const response = await apiClient.post('/headerList', data);
    return response.data;
  } catch (error) {
    console.error('헤더명 목록 조회 실패:', error);
    throw error;
  }
};
