import React, { useState, useEffect } from 'react';
import { fetchHeaderCombinationList } from '../services/api';
import axios from 'axios';
import Column from './Column';
import './Column.css';

const HeaderCombinationList = ({ channel, selectedHeader, onCombinationSelect, selectedCombination }) => {
  const [combinationHeaders, setCombinationHeaders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('combination'); // 'combination' or 'network'

  // Test function to directly call the API with hardcoded values
  const testDirectApiCall = async () => {
    console.log('Testing direct API call...');
    try {
      let data = JSON.stringify({
        "channel": "금융감독원",
        "headers": [
          "지분율",
          "주주명"
        ]
      });

      let config = {
        method: 'post',
        maxBodyLength: Infinity,
        url: 'https://api-backend.nventure.co.kr/solution/headerCombinationList',
        headers: { 
          'Content-Type': 'application/json', 
          'Authorization': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..BGbPFVbUSF04rnPA7p23kB1uaIY_ouX9KOVxGYyxnxE'
        },
        data : data
      };

      const response = await axios.request(config);
      console.log('Direct API call successful');
      console.log('Direct API response:', JSON.stringify(response.data, null, 2));
    } catch (error) {
      console.error('Direct API call error:', error);
    }
  };

  useEffect(() => {
    const getCombinationHeaders = async () => {
      if (!channel) return;

      setLoading(true);
      try {
        // Try with selectedHeader first if available
        if (selectedHeader) {
          const headers = Array.isArray(selectedHeader) ? selectedHeader : [selectedHeader];
          console.log('Trying with selected header:', { channel, headers });

          const response = await fetchHeaderCombinationList(channel, headers);
          console.log('Response with selected header:', response);

          if (response.list && response.list.length > 0) {
            setCombinationHeaders(response.list);
            setError(null);
            return;
          } else {
            console.log('No results with selected header, trying hardcoded values...');
          }
        }

        // If no selectedHeader or no results, try with hardcoded values
        const hardcodedHeaders = ["지분율", "주주명"];
        console.log('Trying with hardcoded headers:', { channel, headers: hardcodedHeaders });

        const response = await fetchHeaderCombinationList(channel, hardcodedHeaders);
        console.log('Response with hardcoded headers:', response);

        setCombinationHeaders(response.list || []);
        setError(null);
      } catch (err) {
        console.error('Error in fetchHeaderCombinationList:', err);
        setError(err.message);
        setCombinationHeaders([]);
      } finally {
        setLoading(false);
      }
    };

    getCombinationHeaders();
  }, [channel, selectedHeader]);

  const handleCombinationClick = (combination) => {
    if (onCombinationSelect) {
      onCombinationSelect(combination);
    }
  };

  // Function to render header_group as tags
  const renderHeaderGroupAsTags = (headerGroup) => {
    if (!headerGroup) return null;

    const headers = headerGroup.split('|');
    return (
      <div className="header-tags">
        {headers.map((header, index) => (
          <span key={index} className="similarity-tag">{header}</span>
        ))}
      </div>
    );
  };

  if (!channel || !selectedHeader) {
    return (
      <div className="column-container">
        <div className="column-empty">채널과 헤더를 선택해주세요.</div>
      </div>
    );
  }

  return (
    <div className="column-container">
      <h2 className="column-title">{`헤더명 조합 목록: ${selectedHeader}`}</h2>

      <div className="tabs">
        <button 
          className={`tab ${activeTab === 'combination' ? 'active' : ''}`}
          onClick={() => setActiveTab('combination')}
        >
          조합뷰
        </button>
        <button 
          className={`tab ${activeTab === 'network' ? 'active' : ''}`}
          onClick={() => setActiveTab('network')}
        >
          네트워크뷰
        </button>
      </div>

      {activeTab === 'combination' ? (
        <div className="tab-content">
          {loading ? (
            <div className="loading">로딩 중...</div>
          ) : error ? (
            <div className="error">에러: {error}</div>
          ) : combinationHeaders.length > 0 ? (
            <div className="column-content">
              {combinationHeaders.map((item, index) => (
                <div
                  key={index}
                  className={`column-item ${selectedCombination === item.header_group ? 'selected' : ''}`}
                  onClick={() => handleCombinationClick(item.header_group)}
                >
                  {renderHeaderGroupAsTags(item.header_group)}
                </div>
              ))}
            </div>
          ) : (
            <div className="column-empty">헤더 조합 정보가 없습니다.</div>
          )}
        </div>
      ) : (
        <div className="tab-content">
          <div className="column-empty">네트워크뷰 구현 예정입니다.</div>
        </div>
      )}
    </div>
  );
};

export default HeaderCombinationList;
