import React, { useState, useEffect } from 'react';
import { fetchHeaderList } from '../services/api';
import Column from './Column';
import './Column.css';

const HeaderList = ({ channel, onHeaderSelect, selectedHeader, headerGroups, filterGroupedHeaders }) => {
  const [headers, setHeaders] = useState([]);
  const [filteredHeaders, setFilteredHeaders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const getHeaders = async () => {
      if (!channel) return;

      setLoading(true);
      try {
        const response = await fetchHeaderList(channel);
        console.log('Header list response:', response);
        setHeaders(response.list || []);
        setError(null);
      } catch (err) {
        setError(err.message);
        setHeaders([]);
      } finally {
        setLoading(false);
      }
    };

    getHeaders();
  }, [channel]);

  // 헤더 그룹이 변경될 때마다 필터링된 헤더 목록 업데이트
  useEffect(() => {
    if (headers.length > 0 && filterGroupedHeaders) {
      // 헤더 객체 배열을 헤더 이름 배열로 변환
      const headerNames = headers.map(h => h.header);
      // 유사어로 지정된 헤더 필터링
      const filteredNames = filterGroupedHeaders(headerNames);
      // 필터링된 이름에 해당하는 원본 헤더 객체 찾아서 설정
      setFilteredHeaders(headers.filter(h => filteredNames.includes(h.header)));
    } else {
      setFilteredHeaders(headers);
    }
  }, [headers, headerGroups, filterGroupedHeaders]);

  // 헤더 항목 렌더링 (메인 헤더에 유사어 그룹 정보 표시)
  const renderHeaderItem = (item) => {
    const headerName = item.header;
    const isMainHeader = headerGroups && headerGroups[headerName];
    const similarCount = isMainHeader ? headerGroups[headerName].length : 0;

    return (
      <div className="header-item">
        <span className="header-name">{headerName}</span>
        {isMainHeader && similarCount > 0 && (
          <span className="similar-count">유사어: {similarCount}개</span>
        )}
      </div>
    );
  };

  if (!channel) {
    return (
      <div className="column-container">
        <div className="column-empty">채널을 선택해주세요.</div>
      </div>
    );
  }

  return (
    <div className="column-container header-list-container">
      <h2 className="column-title">{`헤더 목록: ${channel}`}</h2>
      <div className="column-content">
        {loading && <div className="loading">로딩 중...</div>}
        {error && <div className="error">에러: {error}</div>}

        {!loading && !error && filteredHeaders.length > 0 ? (
          filteredHeaders.map((item, index) => (
            <div
              key={index}
              className={`column-item header-item ${selectedHeader === item.header ? 'selected' : ''}`}
              onClick={() => onHeaderSelect(item.header)}
            >
              {renderHeaderItem(item)}
            </div>
          ))
        ) : (
          !loading && !error && <div className="column-empty">헤더 정보가 없습니다.</div>
        )}
      </div>
    </div>
  );
};

export default HeaderList;
