import React, { useState } from 'react';
import './SeparationClassifier.css';

const GroupsPanel = ({ groupingData, selectedGroupId, setSelectedGroupId }) => {
  const [editingGroupId, setEditingGroupId] = useState(null);
  const [groupNameInput, setGroupNameInput] = useState('');

  if (!groupingData || !groupingData.groups) {
    return <div className="no-data">그룹 데이터가 없습니다</div>;
  }

  const { groups } = groupingData;

  return (
    <div className="groups-panel">
      <h4>그룹</h4>
      <div className="group-boxes-list">
        {groups && groups.map((group) => (
          <div 
            key={group.id} 
            className={`group-box ${selectedGroupId === group.id ? 'selected-group' : ''}`}
            onClick={() => setSelectedGroupId(group.id)}
          >
            {editingGroupId === group.id ? (
              <div className="group-name-edit">
                <input 
                  type="text" 
                  value={groupNameInput} 
                  onChange={(e) => setGroupNameInput(e.target.value)}
                  onBlur={() => {
                    if (groupingData && groupingData.onUpdateGroupName) {
                      groupingData.onUpdateGroupName(group.id, groupNameInput);
                    }
                    setEditingGroupId(null);
                  }}
                  autoFocus
                  onClick={(e) => e.stopPropagation()} // Prevent group selection when editing name
                />
              </div>
            ) : (
              <div 
                className="group-box-name"
                onClick={(e) => {
                  e.stopPropagation(); // Prevent group selection when clicking on name to edit
                  setEditingGroupId(group.id);
                  setGroupNameInput(group.name);
                }}
              >
                {group.name}
              </div>
            )}
            <div className="group-box-content">
              {group.items && group.items.length > 0 ? (
                group.items.map((item, idx) => (
                  <div 
                    key={idx} 
                    className="group-item"
                    onClick={(e) => {
                      e.stopPropagation(); // Prevent group selection when clicking item
                      if (groupingData && groupingData.onRemoveValueFromGroup) {
                        groupingData.onRemoveValueFromGroup(group.id, item);
                      }
                    }}
                    title="클릭하여 그룹에서 제거"
                  >
                    {item}
                  </div>
                ))
              ) : (
                <div className="empty-group">빈 그룹</div>
              )}
            </div>
          </div>
        ))}

        {/* Add new group button */}
        <button 
          className="add-group-button"
          onClick={() => {
            if (groupingData && groupingData.onAddGroup) {
              groupingData.onAddGroup();
            }
          }}
        >
          + 새 그룹 추가
        </button>
      </div>
    </div>
  );
};

export default GroupsPanel;
