import React, { useState } from 'react';
import './SeparationClassifier.css';

const GroupPanel = ({ groupingData }) => {
  const [editingGroupId, setEditingGroupId] = useState(null);
  const [groupNameInput, setGroupNameInput] = useState('');
  const [selectedGroupId, setSelectedGroupId] = useState(null);

  if (!groupingData || !groupingData.uniqueValues || !Array.isArray(groupingData.uniqueValues)) {
    return <div className="no-data">그룹 데이터가 없습니다</div>;
  }

  const { uniqueValues, groups } = groupingData;

  // Handle segmentation value click to add to selected group
  const handleValueClick = (value) => {
    if (!selectedGroupId) {
      alert('먼저 그룹을 선택해주세요');
      return;
    }

    if (groupingData && groupingData.onAddValueToGroup) {
      groupingData.onAddValueToGroup(selectedGroupId, value);
    }
  };

  return (
    <div className="group-panel">
      <h3>분절값 그룹핑</h3>
      <div className="grouping-layout">
        {/* Left side - Segmentation values */}
        <div className="segmentation-values-container">
          <h4>분절값 목록</h4>
          <div className="segmentation-values-list">
            {uniqueValues.map((value, index) => {
              // Skip values that are already in any group
              const isInAnyGroup = groups && groups.some(group => 
                group.items && group.items.includes(value)
              );

              if (isInAnyGroup) {
                return null;
              }

              return (
                <div 
                  key={index} 
                  className="segmentation-value-item clickable"
                  onClick={() => handleValueClick(value)}
                  title="클릭하여 선택된 그룹에 추가"
                >
                  {value}
                </div>
              );
            })}
          </div>
        </div>

        {/* Right side - Group boxes */}
        <div className="group-boxes-container">
          <h4>그룹</h4>
          <div className="group-boxes-list">
            {groups && groups.map((group) => (
              <div 
                key={group.id} 
                className={`group-box ${selectedGroupId === group.id ? 'selected-group' : ''}`}
                onClick={() => setSelectedGroupId(group.id)}
              >
                {editingGroupId === group.id ? (
                  <div className="group-name-edit">
                    <input 
                      type="text" 
                      value={groupNameInput} 
                      onChange={(e) => setGroupNameInput(e.target.value)}
                      onBlur={() => {
                        if (groupingData && groupingData.onUpdateGroupName) {
                          groupingData.onUpdateGroupName(group.id, groupNameInput);
                        }
                        setEditingGroupId(null);
                      }}
                      autoFocus
                      onClick={(e) => e.stopPropagation()} // Prevent group selection when editing name
                    />
                  </div>
                ) : (
                  <div 
                    className="group-box-name"
                    onClick={(e) => {
                      e.stopPropagation(); // Prevent group selection when clicking on name to edit
                      setEditingGroupId(group.id);
                      setGroupNameInput(group.name);
                    }}
                  >
                    {group.name}
                  </div>
                )}
                <div className="group-box-content">
                  {group.items && group.items.length > 0 ? (
                    group.items.map((item, idx) => (
                      <div 
                        key={idx} 
                        className="group-item"
                        onClick={(e) => {
                          e.stopPropagation(); // Prevent group selection when clicking item
                          if (groupingData && groupingData.onRemoveValueFromGroup) {
                            groupingData.onRemoveValueFromGroup(group.id, item);
                          }
                        }}
                        title="클릭하여 그룹에서 제거"
                      >
                        {item}
                      </div>
                    ))
                  ) : (
                    <div className="empty-group">빈 그룹</div>
                  )}
                </div>
              </div>
            ))}

            {/* Add new group button */}
            <button 
              className="add-group-button"
              onClick={() => {
                if (groupingData && groupingData.onAddGroup) {
                  groupingData.onAddGroup();
                }
              }}
            >
              + 새 그룹 추가
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GroupPanel;
