import React from 'react';
import './SeparationClassifier.css';

// Utility functions for text processing
const textUtils = {
  // String reversal utility
  reverseString: (str) => str.split('').reverse().join(''),

  // Extract tokens from data rows
  extractTokens: (dataRows) => {
    const bracketSet = new Set();
    const valueSet = new Set();

    dataRows.forEach(row => {
      const cellValue = row[0];

      // Skip empty/null values
      if (cellValue === null || cellValue === undefined || String(cellValue).trim() === '') {
        return;
      }

      const cellValueStr = String(cellValue);

      // 1. Handle bracket content with regex
      const bracketContentRegex = /\(([^)]*)\)|\[([^\]]*)\]|\{([^}]*)\}/g;
      let textWithoutBrackets = cellValueStr.replace(bracketContentRegex, (match, p1, p2, p3) => {
        const content = p1 || p2 || p3; 
        if (content && content.trim()) {
          bracketSet.add(content.trim());
        }
        return ' '; 
      });

      // 2. Split by spaces and commas
      const separatedValues = textWithoutBrackets
        .split(/[\s,]+/)
        .map(value => value.trim())
        .filter(value => value.length > 0);

      separatedValues.forEach(value => {
        if (value && value.trim()) {
          valueSet.add(value.trim());
        }
      });
    });

    return {
      bracketValues: Array.from(bracketSet).map(val => `[괄호] ${val}`),
      normalValues: Array.from(valueSet),
    };
  },

  // Find common substring patterns
  findCommonSubstrings: (tokens, minLength = 1, minCount = 2, direction = 'any') => {
    // Track substring frequencies
    const substringFreq = {};

    // Analyze all tokens
    tokens.forEach(token => {
      const cleanToken = token.replace('[괄호] ', '');

      // Handle prefixes
      if (direction === 'prefix' || direction === 'any') {
        for (let len = minLength; len <= cleanToken.length; len++) {
          const substr = cleanToken.substring(0, len);
          if (substr.length >= minLength) {
            if (!substringFreq[substr]) {
              substringFreq[substr] = { 
                count: 1, 
                tokens: [token],
                direction: 'prefix'
              };
            } else if (!substringFreq[substr].tokens.includes(token)) {
              substringFreq[substr].count++;
              substringFreq[substr].tokens.push(token);
              if (substringFreq[substr].direction !== 'prefix') {
                substringFreq[substr].direction = 'both';
              }
            }
          }
        }
      }

      // Handle suffixes
      if (direction === 'suffix' || direction === 'any') {
        for (let len = minLength; len <= cleanToken.length; len++) {
          const substr = cleanToken.substring(cleanToken.length - len);
          if (substr.length >= minLength) {
            if (!substringFreq[substr]) {
              substringFreq[substr] = { 
                count: 1, 
                tokens: [token],
                direction: 'suffix'
              };
            } else if (!substringFreq[substr].tokens.includes(token)) {
              substringFreq[substr].count++;
              substringFreq[substr].tokens.push(token);
              if (substringFreq[substr].direction !== 'suffix') {
                substringFreq[substr].direction = 'both';
              }
            }
          }
        }
      }

      // Handle middle substrings
      if (direction === 'any') {
        for (let len = minLength; len <= cleanToken.length; len++) {
          for (let i = 1; i < cleanToken.length - len; i++) {
            const substr = cleanToken.substring(i, i + len);
            if (substr.length >= minLength) {
              if (!substringFreq[substr]) {
                substringFreq[substr] = { 
                  count: 1, 
                  tokens: [token],
                  direction: 'middle'
                };
              } else if (!substringFreq[substr].tokens.includes(token)) {
                substringFreq[substr].count++;
                substringFreq[substr].tokens.push(token);
                substringFreq[substr].direction = 'mixed';
              }
            }
          }
        }
      }
    });

    // Filter and sort results
    return Object.entries(substringFreq)
      .filter(([substr, data]) => {
        // Base filter: meet minimum count
        const baseFilter = data.count >= minCount;

        // Special handling for single characters
        if (substr.length === 1) {
          const coverage = (data.tokens.length / tokens.length) * 100;
          return baseFilter && (coverage >= 30 || data.count >= 5);
        }

        return baseFilter;
      })
      .sort((a, b) => {
        // Sort by token count (descending)
        const countDiff = b[1].count - a[1].count;
        if (countDiff !== 0) return countDiff;

        // Then by length (descending)
        const lengthDiff = b[0].length - a[0].length;
        if (lengthDiff !== 0) return lengthDiff;

        // Finally by alphabetical order
        return a[0].localeCompare(b[0]);
      })
      .map(([substr, data]) => ({
        substring: substr,
        count: data.count,
        tokens: data.tokens,
        direction: data.direction,
        percentCoverage: (data.tokens.length / tokens.length) * 100
      }));
  },

  // Analyze compound word relationships
  analyzeCompoundRelationships: (tokens) => {
    const relationships = [];

    // Compare all token pairs
    for (let i = 0; i < tokens.length; i++) {
      const token1 = tokens[i].replace('[괄호] ', '');

      for (let j = 0; j < tokens.length; j++) {
        if (i === j) continue;

        const token2 = tokens[j].replace('[괄호] ', '');

        // Check if token1 is part of token2
        if (token2.includes(token1)) {
          const position = token2.indexOf(token1);
          const beforeChar = position > 0 ? token2[position - 1] : '';
          const afterChar = position + token1.length < token2.length ? token2[position + token1.length] : '';

          // Check if it's at a word boundary
          const isWordBoundary = (position === 0 || beforeChar === ' ' || beforeChar === '-') &&
                                (position + token1.length === token2.length || afterChar === ' ' || afterChar === '-');

          if (isWordBoundary || token1.length >= 2) {
            relationships.push({
              parent: tokens[j],   // Compound
              child: tokens[i],    // Component
              position: position   // Position
            });
          }
        }
      }
    }

    return relationships;
  },

  // Select optimal substrings for grouping
  selectOptimalSubstrings: (substrings, tokens, compoundRel, maxGroups = 10) => {
    // Sort by coverage and importance
    const sortedSubstrings = [...substrings]
      .sort((a, b) => {
        // Prioritize substrings that have compound relationships
        const hasCompoundA = compoundRel.some(rel => 
          rel.parent.includes(a.substring) || rel.child.includes(a.substring));
        const hasCompoundB = compoundRel.some(rel => 
          rel.parent.includes(b.substring) || rel.child.includes(b.substring));

        if (hasCompoundA && !hasCompoundB) return -1;
        if (!hasCompoundA && hasCompoundB) return 1;

        // Score based on count × length^2 for more balanced selection
        const scoreA = a.count * Math.pow(a.substring.length, 2);
        const scoreB = b.count * Math.pow(b.substring.length, 2);
        return scoreB - scoreA;
      });

    const selectedSubstrings = [];
    const coveredTokens = new Set();

    // Greedy selection
    for (const substr of sortedSubstrings) {
      // Check if this covers new tokens
      const newCoverage = substr.tokens.some(token => !coveredTokens.has(token));

      // More lenient selection criteria
      // For Korean characters that often have semantic meaning, we should be more lenient
      const isImportantSubstr = (
        // Either has more than 3 occurrences
        substr.count >= 3 || 
        // Or it's 2+ characters with at least 2 occurrences
        (substr.substring.length >= 2 && substr.count >= 2) ||
        // Or it's a Korean character (or other non-ASCII) with at least 2 occurrences
        (/[^\x00-\x7F]/.test(substr.substring) && substr.count >= 2)
      );

      const isHighSupportSingleChar = (
        substr.substring.length === 1 && 
        substr.count >= Math.max(3, tokens.length * 0.15) // Lowered threshold
      );

      if (newCoverage || isImportantSubstr || isHighSupportSingleChar) {
        selectedSubstrings.push(substr);

        // Update covered tokens
        substr.tokens.forEach(token => coveredTokens.add(token));
      }

      // Limit groups but increased to allow more meaningful patterns
      if (selectedSubstrings.length >= maxGroups + 2) break;
    }

    return selectedSubstrings;
  },

  // Form compound word relationship groups
  formCompoundGroups: (relationships) => {
    // Group by parent token
    const compoundGroups = {};

    relationships.forEach(rel => {
      const parentToken = rel.parent;
      if (!compoundGroups[parentToken]) {
        compoundGroups[parentToken] = {
          parent: parentToken,
          children: [rel.child],
          positions: [rel.position]
        };
      } else if (!compoundGroups[parentToken].children.includes(rel.child)) {
        compoundGroups[parentToken].children.push(rel.child);
        compoundGroups[parentToken].positions.push(rel.position);
      }
    });

    // Sort by number of children (descending)
    return Object.values(compoundGroups)
      .sort((a, b) => b.children.length - a.children.length)
      .filter(group => group.children.length >= 2) // At least 2 children
      .slice(0, 5); // Top 5 only
  }
};

// Main component for classification results
const SeparationClassifier = ({ data, onReset }) => {
  // Empty data handling
  if (!data || !data.rows || data.rows.length === 0) {
    return <div className="separation-empty">No data to classify</div>;
  }

  // Process and classify the data
  const processData = () => {
    const { bracketValues, normalValues } = textUtils.extractTokens(data.rows);

    // No results case
    if (bracketValues.length === 0 && normalValues.length === 0) {
      return {
        headers: ["Group", "Token"],
        rows: [["", "분리된 값이 없습니다"]]
      };
    }

    // All tokens
    const allMinTokens = [...bracketValues, ...normalValues];

    // Analyze compound relationships
    const compoundRelationships = textUtils.analyzeCompoundRelationships(allMinTokens);

    // Get directional substrings
    const prefixSubstrings = textUtils.findCommonSubstrings(allMinTokens, 1, 2, 'prefix');
    const suffixSubstrings = textUtils.findCommonSubstrings(allMinTokens, 1, 2, 'suffix');

    // Select optimal substrings
    const optimalPrefixSubstrings = textUtils.selectOptimalSubstrings(
      prefixSubstrings, allMinTokens, compoundRelationships, 8
    );
    const optimalSuffixSubstrings = textUtils.selectOptimalSubstrings(
      suffixSubstrings, allMinTokens, compoundRelationships, 8
    );

    // Build compound groups
    const compoundGroups = textUtils.formCompoundGroups(compoundRelationships);

    // Build results data structure
    const finalRows = [];

    // 1. Prefix groups
    finalRows.push(["🌲 접두사 기준 그룹 (앞글자순)", ""]);

    optimalPrefixSubstrings.forEach(group => {
      finalRows.push([`📌 앞부분: ${group.substring} (${group.count})`, ""]);

      const groupTokens = group.tokens.sort((a, b) => {
        const aIsBracket = a.startsWith('[괄호]');
        const bIsBracket = b.startsWith('[괄호]');

        if (aIsBracket && !bIsBracket) return -1;
        if (!aIsBracket && bIsBracket) return 1;

        return a.localeCompare(b);
      });

      groupTokens.forEach(token => {
        finalRows.push(["└─", token]);
      });

      finalRows.push(["", ""]);
    });

    // 2. Suffix groups
    finalRows.push(["🌲 접미사 기준 그룹 (뒷글자순)", ""]);

    optimalSuffixSubstrings.forEach(group => {
      finalRows.push([`📌 뒷부분: ${group.substring} (${group.count})`, ""]);

      const groupTokens = group.tokens.sort((a, b) => {
        const aIsBracket = a.startsWith('[괄호]');
        const bIsBracket = b.startsWith('[괄호]');

        if (aIsBracket && !bIsBracket) return -1;
        if (!aIsBracket && bIsBracket) return 1;

        return textUtils.reverseString(a.replace('[괄호] ', '')).localeCompare(
          textUtils.reverseString(b.replace('[괄호] ', ''))
        );
      });

      groupTokens.forEach(token => {
        finalRows.push(["└─", token]);
      });

      finalRows.push(["", ""]);
    });

    // 3. Compound relationships
    if (compoundGroups.length > 0) {
      finalRows.push(["🌲 복합어 관계 그룹", ""]);

      compoundGroups.forEach(group => {
        finalRows.push([
          `📌 복합어: ${group.parent.replace('[괄호] ', '')} (${group.children.length})`, 
          group.parent
        ]);

        group.children.forEach((child, idx) => {
          const position = group.positions[idx];
          const parentClean = group.parent.replace('[괄호] ', '');
          const childClean = child.replace('[괄호] ', '');

          let positionMarker = '';
          if (position === 0) {
            positionMarker = '⬅️ 시작 부분';
          } else if (position + childClean.length === parentClean.length) {
            positionMarker = '➡️ 끝 부분';
          } else {
            positionMarker = '⬇️ 중간 부분';
          }

          finalRows.push([`└─ [${positionMarker}]`, child]);
        });

        finalRows.push(["", ""]);
      });
    }

    // 4. Ungrouped tokens
    const allGroupedTokens = new Set();

    optimalPrefixSubstrings.forEach(group => 
      group.tokens.forEach(token => allGroupedTokens.add(token)));

    optimalSuffixSubstrings.forEach(group => 
      group.tokens.forEach(token => allGroupedTokens.add(token)));

    compoundGroups.forEach(group => {
      allGroupedTokens.add(group.parent);
      group.children.forEach(child => allGroupedTokens.add(child));
    });

    const ungroupedTokens = allMinTokens
      .filter(token => !allGroupedTokens.has(token))
      .sort();

    if (ungroupedTokens.length > 0) {
      finalRows.push(["🌲 미분류 토큰", ""]);
      ungroupedTokens.forEach(token => {
        finalRows.push(["└─", token]);
      });
    }

    return {
      prefixGroups: optimalPrefixSubstrings.map(group => ({
        substring: group.substring,
        count: group.count,
        tokens: group.tokens.sort((a, b) => {
          const aIsBracket = a.startsWith('[괄호]');
          const bIsBracket = b.startsWith('[괄호]');

          if (aIsBracket && !bIsBracket) return -1;
          if (!aIsBracket && bIsBracket) return 1;

          return a.localeCompare(b);
        })
      })),
      suffixGroups: optimalSuffixSubstrings.map(group => ({
        substring: group.substring,
        count: group.count,
        tokens: group.tokens.sort((a, b) => {
          const aIsBracket = a.startsWith('[괄호]');
          const bIsBracket = b.startsWith('[괄호]');

          if (aIsBracket && !bIsBracket) return -1;
          if (!aIsBracket && bIsBracket) return 1;

          return textUtils.reverseString(a.replace('[괄호] ', '')).localeCompare(
            textUtils.reverseString(b.replace('[괄호] ', ''))
          );
        })
      })),
      compoundGroups,
      ungroupedTokens
    };
  };

  // Processed classification data
  const classificationData = processData();

  // Render tree node with children
  const renderTreeNode = (label, children, nodeType, nodeSubtype = '') => (
    <div className={`tree-node ${nodeType} ${nodeSubtype}`}>
      <div className="tree-node-label">{label}</div>
      {children && children.length > 0 && (
        <div className="tree-node-children">
          {children}
        </div>
      )}
    </div>
  );

  // Render token node (leaf)
  const renderTokenNode = (token) => {
    const isBracketContent = String(token).startsWith('[괄호]');
    let displayContent = token;

    if (isBracketContent) {
      displayContent = (
        <>
          <span className="bracket-indicator">⊂⊃</span>
          {String(token).replace('[괄호] ', '')}
        </>
      );
    }

    return (
      <div className={`token-node ${isBracketContent ? 'bracket-content' : ''}`}>
        {displayContent}
      </div>
    );
  };

  // Render position indicator for compound words
  const renderPositionIndicator = (position, parentLength, childLength) => {
    let positionMarker = '';
    let positionClass = '';

    if (position === 0) {
      positionMarker = '⬅️ 시작 부분';
      positionClass = 'position-start';
    } else if (position + childLength === parentLength) {
      positionMarker = '➡️ 끝 부분';
      positionClass = 'position-end';
    } else {
      positionMarker = '⬇️ 중간 부분';
      positionClass = 'position-middle';
    }

    return <span className={`position-indicator ${positionClass}`}>{positionMarker}</span>;
  };

  // Render the classification results with dendrogram-style visualization
  return (
    <div className="separation-classifier">
      <div className="classifier-header">
        <h3>분리분류 결과</h3>
        <button className="reset-button" onClick={onReset} title="원본 데이터로 복원">
          <span>🔄 원본으로 복원</span>
        </button>
      </div>

      <div className="dendrogram-container">
        {/* Prefix Groups */}
        <div className="tree-category">
          <div className="tree-category-header">
            <h4>🌲 접두사 기준 그룹 (앞글자순)</h4>
          </div>
          <div className="tree-content">
            {classificationData.prefixGroups.map((group, idx) => (
              <div key={`prefix-${idx}`} className="group-tree">
                {renderTreeNode(
                  <><span className="group-marker">📌</span> 앞부분: {group.substring} <span className="count-badge">({group.count})</span></>,
                  group.tokens.map((token, tokenIdx) => (
                    <div key={`prefix-${idx}-token-${tokenIdx}`} className="tree-branch">
                      <div className="branch-line"></div>
                      {renderTokenNode(token)}
                    </div>
                  )),
                  'group-node',
                  'prefix-group'
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Suffix Groups */}
        <div className="tree-category">
          <div className="tree-category-header">
            <h4>🌲 접미사 기준 그룹 (뒷글자순)</h4>
          </div>
          <div className="tree-content">
            {classificationData.suffixGroups.map((group, idx) => (
              <div key={`suffix-${idx}`} className="group-tree">
                {renderTreeNode(
                  <><span className="group-marker">📌</span> 뒷부분: {group.substring} <span className="count-badge">({group.count})</span></>,
                  group.tokens.map((token, tokenIdx) => (
                    <div key={`suffix-${idx}-token-${tokenIdx}`} className="tree-branch">
                      <div className="branch-line"></div>
                      {renderTokenNode(token)}
                    </div>
                  )),
                  'group-node',
                  'suffix-group'
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Compound Groups */}
        {classificationData.compoundGroups && classificationData.compoundGroups.length > 0 && (
          <div className="tree-category">
            <div className="tree-category-header">
              <h4>🌲 복합어 관계 그룹</h4>
            </div>
            <div className="tree-content">
              {classificationData.compoundGroups.map((group, idx) => {
                const parentClean = group.parent.replace('[괄호] ', '');

                return (
                  <div key={`compound-${idx}`} className="group-tree">
                    {renderTreeNode(
                      <><span className="group-marker">📌</span> 복합어: {parentClean} <span className="count-badge">({group.children.length})</span></>,
                      group.children.map((child, childIdx) => {
                        const position = group.positions[childIdx];
                        const childClean = child.replace('[괄호] ', '');

                        return (
                          <div key={`compound-${idx}-child-${childIdx}`} className="tree-branch with-position">
                            <div className="branch-line"></div>
                            <div className="branch-content">
                              {renderPositionIndicator(position, parentClean.length, childClean.length)}
                              {renderTokenNode(child)}
                            </div>
                          </div>
                        );
                      }),
                      'group-node',
                      'compound-group'
                    )}
                  </div>
                );
              })}
            </div>
          </div>
        )}

        {/* Ungrouped Tokens */}
        {classificationData.ungroupedTokens && classificationData.ungroupedTokens.length > 0 && (
          <div className="tree-category">
            <div className="tree-category-header">
              <h4>🌲 미분류 토큰</h4>
            </div>
            <div className="tree-content ungrouped-content">
              {classificationData.ungroupedTokens.map((token, idx) => (
                <div key={`ungrouped-${idx}`} className="ungrouped-token">
                  {renderTokenNode(token)}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default SeparationClassifier;
