import React from 'react';
import './SeparationClassifier.css';

// Utility functions for text processing
const textUtils = {
  // String reversal utility
  reverseString: (str) => str.split('').reverse().join(''),

  // Extract tokens from data rows
  extractTokens: (dataRows) => {
    const bracketSet = new Set();
    const valueSet = new Set();

    dataRows.forEach(row => {
      const cellValue = row[0];

      // Skip empty/null values
      if (cellValue === null || cellValue === undefined || String(cellValue).trim() === '') {
        return;
      }

      const cellValueStr = String(cellValue);

      // 1. Handle bracket content with regex
      const bracketContentRegex = /\(([^)]*)\)|\[([^\]]*)\]|\{([^}]*)\}/g;
      let textWithoutBrackets = cellValueStr.replace(bracketContentRegex, (match, p1, p2, p3) => {
        const content = p1 || p2 || p3; 
        if (content && content.trim()) {
          bracketSet.add(content.trim());
        }
        return ' '; 
      });

      // 2. Split by spaces and commas
      const separatedValues = textWithoutBrackets
        .split(/[\s,]+/)
        .map(value => value.trim())
        .filter(value => value.length > 0);

      separatedValues.forEach(value => {
        if (value && value.trim()) {
          valueSet.add(value.trim());
        }
      });
    });

    return {
      bracketValues: Array.from(bracketSet).map(val => `[괄호] ${val}`),
      normalValues: Array.from(valueSet),
    };
  },

  // Find common substring patterns
  findCommonSubstrings: (tokens, minLength = 1, minCount = 2, direction = 'any') => {
    // Track substring frequencies
    const substringFreq = {};

    // Analyze all tokens
    tokens.forEach(token => {
      const cleanToken = token.replace('[괄호] ', '');

      // Handle prefixes
      if (direction === 'prefix' || direction === 'any') {
        for (let len = minLength; len <= cleanToken.length; len++) {
          const substr = cleanToken.substring(0, len);
          if (substr.length >= minLength) {
            if (!substringFreq[substr]) {
              substringFreq[substr] = { 
                count: 1, 
                tokens: [token],
                direction: 'prefix'
              };
            } else if (!substringFreq[substr].tokens.includes(token)) {
              substringFreq[substr].count++;
              substringFreq[substr].tokens.push(token);
              if (substringFreq[substr].direction !== 'prefix') {
                substringFreq[substr].direction = 'both';
              }
            }
          }
        }
      }

      // Handle suffixes
      if (direction === 'suffix' || direction === 'any') {
        for (let len = minLength; len <= cleanToken.length; len++) {
          const substr = cleanToken.substring(cleanToken.length - len);
          if (substr.length >= minLength) {
            if (!substringFreq[substr]) {
              substringFreq[substr] = { 
                count: 1, 
                tokens: [token],
                direction: 'suffix'
              };
            } else if (!substringFreq[substr].tokens.includes(token)) {
              substringFreq[substr].count++;
              substringFreq[substr].tokens.push(token);
              if (substringFreq[substr].direction !== 'suffix') {
                substringFreq[substr].direction = 'both';
              }
            }
          }
        }
      }

      // Remove middle substring handling as requested - only prefix and suffix are allowed
    });

    // Filter and sort results - improved to better capture meaningful patterns
    return Object.entries(substringFreq)
      .filter(([substr, data]) => {
        // Base filter: meet minimum count
        const baseFilter = data.count >= minCount;

        // Give priority to Korean character patterns and units
        const isKorean = /[\uAC00-\uD7A3]/.test(substr);
        const isUnit = /^[a-zA-Z]+$/.test(substr) && substr.length <= 3;

        // Special handling for single characters
        if (substr.length === 1) {
          // For Korean single characters, they are more likely meaningful
          if (isKorean) {
            return baseFilter && (data.count >= 2);
          }
          // For non-Korean, require higher frequency
          const coverage = (data.tokens.length / tokens.length) * 100;
          return baseFilter && (coverage >= 25 || data.count >= 3);
        }

        // Give more weight to units (like kg, g, etc.)
        if (isUnit && data.count >= 2 && data.direction === 'suffix') {
          return true;
        }

        // Give more weight to substrings with Korean characters
        if (isKorean && substr.length >= 2) {
          return baseFilter && data.count >= 2;
        }

        return baseFilter;
      })
      .sort((a, b) => {
        // First prioritize units like kg, g
        const aIsUnit = /^[a-zA-Z]+$/.test(a[0]) && a[0].length <= 3 && a[1].direction === 'suffix';
        const bIsUnit = /^[a-zA-Z]+$/.test(b[0]) && b[0].length <= 3 && b[1].direction === 'suffix';

        if (aIsUnit && !bIsUnit) return -1;
        if (!aIsUnit && bIsUnit) return 1;

        // Then prioritize Korean patterns
        const aIsKorean = /[\uAC00-\uD7A3]/.test(a[0]);
        const bIsKorean = /[\uAC00-\uD7A3]/.test(b[0]);

        if (aIsKorean && !bIsKorean) return -1;
        if (!aIsKorean && bIsKorean) return 1;

        // Then by token count (descending)
        const countDiff = b[1].count - a[1].count;
        if (countDiff !== 0) return countDiff;

        // Then by length (descending)
        const lengthDiff = b[0].length - a[0].length;
        if (lengthDiff !== 0) return lengthDiff;

        // Finally by alphabetical order
        return a[0].localeCompare(b[0]);
      })
      .map(([substr, data]) => ({
        substring: substr,
        count: data.count,
        tokens: data.tokens,
        direction: data.direction,
        percentCoverage: (data.tokens.length / tokens.length) * 100,
        isKorean: /[\uAC00-\uD7A3]/.test(substr),
        isUnit: /^[a-zA-Z]+$/.test(substr) && substr.length <= 3 && data.direction === 'suffix'
      }));
  },

  // Analyze compound word relationships
  analyzeCompoundRelationships: (tokens) => {
    const relationships = [];

    // Compare all token pairs
    for (let i = 0; i < tokens.length; i++) {
      const token1 = tokens[i].replace('[괄호] ', '');

      for (let j = 0; j < tokens.length; j++) {
        if (i === j) continue;

        const token2 = tokens[j].replace('[괄호] ', '');

        // Check if token1 is part of token2
        if (token2.includes(token1)) {
          const position = token2.indexOf(token1);
          const beforeChar = position > 0 ? token2[position - 1] : '';
          const afterChar = position + token1.length < token2.length ? token2[position + token1.length] : '';

          // Check if it's at a word boundary or Korean characters (more flexible for Korean)
          const isKorean = /[\uAC00-\uD7A3]/.test(token1);
          const isWordBoundary = (position === 0 || beforeChar === ' ' || beforeChar === '-') &&
                                (position + token1.length === token2.length || afterChar === ' ' || afterChar === '-');

          // For Korean patterns, be more lenient
          if (isWordBoundary || (isKorean && token1.length >= 1) || (!isKorean && token1.length >= 2)) {
            relationships.push({
              parent: tokens[j],   // Compound
              child: tokens[i],    // Component
              position: position   // Position
            });
          }
        }
      }
    }

    return relationships;
  },

  // Find common words in tokens
  findCommonWords: (tokens) => {
    // This function specifically finds common Korean words and units
    const wordFreq = {};
    const wordRelations = [];

    // Find measurement units (g, kg, etc.)
    const unitRegex = /(\d+(?:\.\d+)?)\s*([a-zA-Z]+)$/;

    // First pass: extract all units and words - ONLY at prefix or suffix positions
    tokens.forEach(token => {
      const cleanToken = token.replace('[괄호] ', '');

      // Check for measurement units
      const unitMatch = cleanToken.match(unitRegex);
      if (unitMatch) {
        const [_, value, unit] = unitMatch;

        if (!wordFreq[unit]) {
          wordFreq[unit] = { count: 1, tokens: [token], isUnit: true };
        } else if (!wordFreq[unit].tokens.includes(token)) {
          wordFreq[unit].count++;
          wordFreq[unit].tokens.push(token);

          // Record relationship
          wordRelations.push({
            word: unit,
            token: token,
            position: cleanToken.indexOf(unit),
            isUnit: true
          });
        }
      }

      // Look for Korean character sequences - expand max length to 4 and improve detection
      const MAX_KOREAN_WORD_LENGTH = 4;

      // Check for known important Korean words specifically
      const importantKoreanWords = ['자연', '등급', '농장', '한우', '한돈', '돼지', '소고기', '돼지고기'];

      for (const koreanWord of importantKoreanWords) {
        // Check prefix position (start of string)
        if (cleanToken.startsWith(koreanWord)) {
          if (!wordFreq[koreanWord]) {
            wordFreq[koreanWord] = { count: 1, tokens: [token], isUnit: false, direction: 'prefix' };
          } else if (!wordFreq[koreanWord].tokens.includes(token)) {
            wordFreq[koreanWord].count++;
            wordFreq[koreanWord].tokens.push(token);

            // Record relationship
            wordRelations.push({
              word: koreanWord,
              token: token,
              position: 0,
              isUnit: false,
              direction: 'prefix'
            });
          }
        }

        // Check suffix position (end of string)
        if (cleanToken.endsWith(koreanWord)) {
          if (!wordFreq[koreanWord]) {
            wordFreq[koreanWord] = { count: 1, tokens: [token], isUnit: false, direction: 'suffix' };
          } else if (!wordFreq[koreanWord].tokens.includes(token)) {
            wordFreq[koreanWord].count++;
            wordFreq[koreanWord].tokens.push(token);

            // Record relationship
            wordRelations.push({
              word: koreanWord,
              token: token,
              position: cleanToken.length - koreanWord.length,
              isUnit: false,
              direction: 'suffix'
            });
          }
        }
      }

      // General approach - look for any Korean words of various lengths
      for (let len = 2; len <= MAX_KOREAN_WORD_LENGTH; len++) {
        if (len <= cleanToken.length) {
          // Prefix (start position)
          const prefixWord = cleanToken.substring(0, len);
          if (/^[\uAC00-\uD7A3]+$/.test(prefixWord)) {
            if (!wordFreq[prefixWord]) {
              wordFreq[prefixWord] = { count: 1, tokens: [token], isUnit: false, direction: 'prefix' };
            } else if (!wordFreq[prefixWord].tokens.includes(token)) {
              wordFreq[prefixWord].count++;
              wordFreq[prefixWord].tokens.push(token);

              // Record relationship
              wordRelations.push({
                word: prefixWord,
                token: token,
                position: 0, // Always at start
                isUnit: false,
                direction: 'prefix'
              });
            }
          }

          // Suffix (end position)
          const suffixWord = cleanToken.substring(cleanToken.length - len);
          if (/^[\uAC00-\uD7A3]+$/.test(suffixWord)) {
            if (!wordFreq[suffixWord]) {
              wordFreq[suffixWord] = { count: 1, tokens: [token], isUnit: false, direction: 'suffix' };
            } else if (!wordFreq[suffixWord].tokens.includes(token)) {
              wordFreq[suffixWord].count++;
              wordFreq[suffixWord].tokens.push(token);

              // Record relationship
              wordRelations.push({
                word: suffixWord,
                token: token,
                position: cleanToken.length - len, // Position at end
                isUnit: false,
                direction: 'suffix'
              });
            }
          }
        }
      }
    });

    // Filter to only keep words that appear in multiple tokens
    const commonWords = Object.entries(wordFreq)
      .filter(([word, data]) => data.count >= 2)
      .sort((a, b) => {
        // Prioritize units
        if (a[1].isUnit && !b[1].isUnit) return -1;
        if (!a[1].isUnit && b[1].isUnit) return 1;

        // Prioritize important Korean words
        const importantA = ['자연', '등급', '농장', '한우', '한돈'].includes(a[0]);
        const importantB = ['자연', '등급', '농장', '한우', '한돈'].includes(b[0]);
        if (importantA && !importantB) return -1;
        if (!importantA && importantB) return 1;

        // Sort by frequency first
        const countDiff = b[1].count - a[1].count;
        if (countDiff !== 0) return countDiff;

        // Then by length (prefer longer words)
        return b[0].length - a[0].length;
      })
      .map(([word, data]) => ({
        word,
        count: data.count,
        tokens: data.tokens,
        isUnit: data.isUnit,
        direction: data.direction
      }));

    // Group the relationships by common word
    const wordGroups = [];

    commonWords.forEach(commonWord => {
      const relatedTokens = wordRelations
        .filter(rel => rel.word === commonWord.word)
        .map(rel => ({
          token: rel.token,
          position: rel.position,
          isUnit: rel.isUnit,
          direction: rel.direction
        }));

      wordGroups.push({
        word: commonWord.word,
        count: commonWord.count,
        tokens: commonWord.tokens,
        relations: relatedTokens,
        isUnit: commonWord.isUnit,
        direction: commonWord.direction
      });
    });

    return wordGroups;
  },

  // Select optimal substrings for grouping
  selectOptimalSubstrings: (substrings, tokens, compoundRel, maxGroups = 15) => {
    // First, identify important Korean substrings
    const koreanSubstrings = substrings.filter(substr => 
      substr.isKorean && substr.count >= 2);

    // Sort by coverage and importance
    const sortedSubstrings = [...substrings]
      .sort((a, b) => {
        // Prioritize Korean patterns
        if (a.isKorean && !b.isKorean) return -1;
        if (!a.isKorean && b.isKorean) return 1;

        // Prioritize substrings that have compound relationships
        const hasCompoundA = compoundRel.some(rel => 
          rel.parent.includes(a.substring) || rel.child.includes(a.substring));
        const hasCompoundB = compoundRel.some(rel => 
          rel.parent.includes(b.substring) || rel.child.includes(b.substring));

        if (hasCompoundA && !hasCompoundB) return -1;
        if (!hasCompoundA && hasCompoundB) return 1;

        // Score based on count and length
        // Modified to give more weight to meaningful substrings (e.g., "등급", "자연")
        const scoreA = a.count * (a.isKorean ? Math.pow(a.substring.length, 1.5) : a.substring.length);
        const scoreB = b.count * (b.isKorean ? Math.pow(b.substring.length, 1.5) : b.substring.length);
        return scoreB - scoreA;
      });

    const selectedSubstrings = [];
    const coveredTokens = new Set();

    // First, include important Korean substrings
    for (const substr of koreanSubstrings) {
      if (substr.substring.length >= 1 && substr.count >= 2) {
        // Check if this is a meaningful Korean term
        if (/[가-힣]{1,}/.test(substr.substring)) {
          selectedSubstrings.push(substr);

          // Update covered tokens
          substr.tokens.forEach(token => coveredTokens.add(token));

          // Limit Korean groups
          if (selectedSubstrings.length >= Math.ceil(maxGroups * 0.6)) break;
        }
      }
    }

    // Then add other important substrings
    for (const substr of sortedSubstrings) {
      // Skip if already selected
      if (selectedSubstrings.some(s => s.substring === substr.substring)) {
        continue;
      }

      // Check if this covers new tokens
      const newCoverage = substr.tokens.some(token => !coveredTokens.has(token));

      // Selection criteria - relaxed for Korean patterns
      const isImportantSubstr = 
        (substr.isKorean && substr.count >= 2) || 
        (substr.substring.length >= 2 && substr.count >= 3);

      const isHighSupportSingleChar = 
        substr.substring.length === 1 && 
        substr.count >= Math.max(3, tokens.length * 0.15);

      if (newCoverage || isImportantSubstr || isHighSupportSingleChar) {
        selectedSubstrings.push(substr);

        // Update covered tokens
        substr.tokens.forEach(token => coveredTokens.add(token));
      }

      // Limit groups
      if (selectedSubstrings.length >= maxGroups) break;
    }

    return selectedSubstrings;
  },

  // Form compound word relationship groups
  formCompoundGroups: (relationships) => {
    // Group by parent token
    const compoundGroups = {};

    relationships.forEach(rel => {
      const parentToken = rel.parent;
      if (!compoundGroups[parentToken]) {
        compoundGroups[parentToken] = {
          parent: parentToken,
          children: [rel.child],
          positions: [rel.position]
        };
      } else if (!compoundGroups[parentToken].children.includes(rel.child)) {
        compoundGroups[parentToken].children.push(rel.child);
        compoundGroups[parentToken].positions.push(rel.position);
      }
    });

    // Sort by number of children (descending)
    return Object.values(compoundGroups)
      .sort((a, b) => b.children.length - a.children.length)
      .filter(group => group.children.length >= 2) // At least 2 children
      .slice(0, 5); // Top 5 only
  }
};

// Main component for classification results
const SeparationClassifier = ({ data, onReset }) => {
  // Empty data handling
  if (!data || !data.rows || data.rows.length === 0) {
    return <div className="separation-empty">No data to classify</div>;
  }

  // Process and classify the data
  const processData = () => {
    const { bracketValues, normalValues } = textUtils.extractTokens(data.rows);

    // No results case
    if (bracketValues.length === 0 && normalValues.length === 0) {
      return {
        headers: ["Group", "Token"],
        rows: [["", "분리된 값이 없습니다"]]
      };
    }

    // All tokens
    const allMinTokens = [...bracketValues, ...normalValues];

    // Find common Korean words in tokens
    const commonWordGroups = textUtils.findCommonWords(allMinTokens);

    // Analyze compound relationships
    const compoundRelationships = textUtils.analyzeCompoundRelationships(allMinTokens);

    // Get directional substrings
    const prefixSubstrings = textUtils.findCommonSubstrings(allMinTokens, 1, 2, 'prefix');
    const suffixSubstrings = textUtils.findCommonSubstrings(allMinTokens, 1, 2, 'suffix');

    // Select optimal substrings
    const optimalPrefixSubstrings = textUtils.selectOptimalSubstrings(
      prefixSubstrings, allMinTokens, compoundRelationships, 8
    );
    const optimalSuffixSubstrings = textUtils.selectOptimalSubstrings(
      suffixSubstrings, allMinTokens, compoundRelationships, 8
    );

    // Build compound groups
    const compoundGroups = textUtils.formCompoundGroups(compoundRelationships);

    // Build results data structure
    const finalRows = [];

    // 1. Prefix groups
    finalRows.push(["🌲 접두사 기준 그룹 (앞글자순)", ""]);

    optimalPrefixSubstrings.forEach(group => {
      finalRows.push([`📌 앞부분: ${group.substring} (${group.count})`, ""]);

      const groupTokens = group.tokens.sort((a, b) => {
        const aIsBracket = a.startsWith('[괄호]');
        const bIsBracket = b.startsWith('[괄호]');

        if (aIsBracket && !bIsBracket) return -1;
        if (!aIsBracket && bIsBracket) return 1;

        return a.localeCompare(b);
      });

      groupTokens.forEach(token => {
        finalRows.push(["└─", token]);
      });

      finalRows.push(["", ""]);
    });

    // 2. Suffix groups
    finalRows.push(["🌲 접미사 기준 그룹 (뒷글자순)", ""]);

    optimalSuffixSubstrings.forEach(group => {
      finalRows.push([`📌 뒷부분: ${group.substring} (${group.count})`, ""]);

      const groupTokens = group.tokens.sort((a, b) => {
        const aIsBracket = a.startsWith('[괄호]');
        const bIsBracket = b.startsWith('[괄호]');

        if (aIsBracket && !bIsBracket) return -1;
        if (!aIsBracket && bIsBracket) return 1;

        return textUtils.reverseString(a.replace('[괄호] ', '')).localeCompare(
          textUtils.reverseString(b.replace('[괄호] ', ''))
        );
      });

      groupTokens.forEach(token => {
        finalRows.push(["└─", token]);
      });

      finalRows.push(["", ""]);
    });

    // 3. Common Korean word groups
    if (commonWordGroups.length > 0) {
      finalRows.push(["🌲 단어 기준 그룹", ""]);

      commonWordGroups.forEach(group => {
        // Skip if too few tokens or already covered well by other groups
        if (group.count < 2 || group.word.length < 1) return;

        finalRows.push([
          `📌 단어: ${group.word} (${group.count})`, 
          ""
        ]);

        // Sort tokens
        const sortedTokens = [...group.tokens].sort((a, b) => {
          const aIsBracket = a.startsWith('[괄호]');
          const bIsBracket = b.startsWith('[괄호]');

          if (aIsBracket && !bIsBracket) return -1;
          if (!aIsBracket && bIsBracket) return 1;

          return a.localeCompare(b);
        });

        sortedTokens.forEach(token => {
          // Find position
          const relation = group.relations.find(r => r.token === token);
          const position = relation ? relation.position : -1;
          const cleanToken = token.replace('[괄호] ', '');

          let positionMarker = '';
          if (position === 0) {
            positionMarker = '⬅️ 처음';
          } else if (position + group.word.length === cleanToken.length) {
            positionMarker = '➡️ 끝';
          } else if (position > 0) {
            positionMarker = '⬇️ 중간';
          }

          finalRows.push([`└─ ${positionMarker.length > 0 ? `[${positionMarker}]` : ''}`, token]);
        });

        finalRows.push(["", ""]);
      });
    }

    // 4. Compound relationships
    if (compoundGroups.length > 0) {
      finalRows.push(["🌲 복합어 관계 그룹", ""]);

      compoundGroups.forEach(group => {
        finalRows.push([
          `📌 복합어: ${group.parent.replace('[괄호] ', '')} (${group.children.length})`, 
          group.parent
        ]);

        group.children.forEach((child, idx) => {
          const position = group.positions[idx];
          const parentClean = group.parent.replace('[괄호] ', '');
          const childClean = child.replace('[괄호] ', '');

          let positionMarker = '';
          if (position === 0) {
            positionMarker = '⬅️ 시작 부분';
          } else if (position + childClean.length === parentClean.length) {
            positionMarker = '➡️ 끝 부분';
          } else {
            positionMarker = '⬇️ 중간 부분';
          }

          finalRows.push([`└─ [${positionMarker}]`, child]);
        });

        finalRows.push(["", ""]);
      });
    }

    // 5. Ungrouped tokens
    const allGroupedTokens = new Set();

    optimalPrefixSubstrings.forEach(group => 
      group.tokens.forEach(token => allGroupedTokens.add(token)));

    optimalSuffixSubstrings.forEach(group => 
      group.tokens.forEach(token => allGroupedTokens.add(token)));

    commonWordGroups.forEach(group => 
      group.tokens.forEach(token => allGroupedTokens.add(token)));

    compoundGroups.forEach(group => {
      allGroupedTokens.add(group.parent);
      group.children.forEach(child => allGroupedTokens.add(child));
    });

    const ungroupedTokens = allMinTokens
      .filter(token => !allGroupedTokens.has(token))
      .sort();

    if (ungroupedTokens.length > 0) {
      finalRows.push(["🌲 미분류 토큰", ""]);
      ungroupedTokens.forEach(token => {
        finalRows.push(["└─", token]);
      });
    }

    return {
      prefixGroups: optimalPrefixSubstrings.map(group => ({
        substring: group.substring,
        count: group.count,
        tokens: group.tokens.sort((a, b) => {
          const aIsBracket = a.startsWith('[괄호]');
          const bIsBracket = b.startsWith('[괄호]');

          if (aIsBracket && !bIsBracket) return -1;
          if (!aIsBracket && bIsBracket) return 1;

          return a.localeCompare(b);
        })
      })),
      suffixGroups: optimalSuffixSubstrings.map(group => ({
        substring: group.substring,
        count: group.count,
        tokens: group.tokens.sort((a, b) => {
          const aIsBracket = a.startsWith('[괄호]');
          const bIsBracket = b.startsWith('[괄호]');

          if (aIsBracket && !bIsBracket) return -1;
          if (!aIsBracket && bIsBracket) return 1;

          return textUtils.reverseString(a.replace('[괄호] ', '')).localeCompare(
            textUtils.reverseString(b.replace('[괄호] ', ''))
          );
        })
      })),
      wordGroups: commonWordGroups
        .filter(group => group.count >= 2 && group.word.length >= 1)
        .map(group => ({
          word: group.word,
          count: group.count,
          tokens: group.tokens.sort((a, b) => {
            const aIsBracket = a.startsWith('[괄호]');
            const bIsBracket = b.startsWith('[괄호]');

            if (aIsBracket && !bIsBracket) return -1;
            if (!aIsBracket && bIsBracket) return 1;

            return a.localeCompare(b);
          }),
          relations: group.relations
        })),
      compoundGroups,
      ungroupedTokens
    };
  };

  // Processed classification data
  const classificationData = processData();

  // Render tree node with children
  const renderTreeNode = (label, children, nodeType, nodeSubtype = '') => (
    <div className={`tree-node ${nodeType} ${nodeSubtype}`}>
      <div className="tree-node-label">{label}</div>
      {children && children.length > 0 && (
        <div className="tree-node-children">
          {children}
        </div>
      )}
    </div>
  );

  // Render token node (leaf)
  const renderTokenNode = (token) => {
    const isBracketContent = String(token).startsWith('[괄호]');
    let displayContent = token;

    if (isBracketContent) {
      displayContent = (
        <>
          <span className="bracket-indicator">⊂⊃</span>
          {String(token).replace('[괄호] ', '')}
        </>
      );
    }

    return (
      <div className={`token-node ${isBracketContent ? 'bracket-content' : ''}`}>
        {displayContent}
      </div>
    );
  };

  // Render position indicator for tokens
  const renderPositionIndicator = (position, direction) => {
    if (direction === 'prefix' || position === 0) {
      return <span className="position-indicator position-start">⬅️ 처음</span>;
    } else if (direction === 'suffix') {
      return <span className="position-indicator position-end">➡️ 끝</span>;
    }
    return null;
  };

  // Render the classification results with dendrogram-style visualization
  return (
    <div className="separation-classifier">
      <div className="classifier-header">
        <h3>분리분류 결과</h3>
        <button className="reset-button" onClick={onReset} title="원본 데이터로 복원">
          <span>🔄 원본으로 복원</span>
        </button>
      </div>

      <div className="dendrogram-container">
        {/* Word Groups - NEW SECTION - Moved to top for importance */}
        {classificationData.wordGroups && classificationData.wordGroups.length > 0 && (
          <div className="tree-category">
            <div className="tree-category-header">
              <h4>🌲 단어 기준 그룹</h4>
            </div>
            <div className="tree-content">
              {classificationData.wordGroups.map((group, idx) => {
                // Skip groups where the word is not at start or end of all tokens
                if (!group.isUnit && !group.direction) {
                  return null;
                }

                return (
                  <div key={`word-${idx}`} className="group-tree">
                    {renderTreeNode(
                      <><span className="group-marker">📌</span> {group.isUnit ? `단위: ${group.word}` : 
                        (group.direction === 'prefix' ? `앞부분 단어: ${group.word}` : 
                         group.direction === 'suffix' ? `뒷부분 단어: ${group.word}` : `단어: ${group.word}`)} 
                         <span className="count-badge">({group.count})</span></>,
                      group.tokens.map((token, tokenIdx) => {
                        // Find position info
                        const relation = group.relations.find(r => r.token === token);

                        return (
                          <div key={`word-${idx}-token-${tokenIdx}`} className="tree-branch with-position">
                            <div className="branch-line"></div>
                            <div className="branch-content">
                              {!group.isUnit && relation && renderPositionIndicator(relation.position, relation.direction)}
                              {renderTokenNode(token)}
                            </div>
                          </div>
                        );
                      }),
                      'group-node',
                      group.isUnit ? 'unit-group' : 
                        (group.direction === 'prefix' ? 'prefix-word-group' : 
                         group.direction === 'suffix' ? 'suffix-word-group' : 'word-group')
                    )}
                  </div>
                );
              })}
            </div>
          </div>
        )}

        {/* Prefix Groups */}
        <div className="tree-category">
          <div className="tree-category-header">
            <h4>🌲 접두사 기준 그룹 (앞글자순)</h4>
          </div>
          <div className="tree-content">
            {classificationData.prefixGroups.map((group, idx) => (
              <div key={`prefix-${idx}`} className="group-tree">
                {renderTreeNode(
                  <><span className="group-marker">📌</span> 앞부분: {group.substring} <span className="count-badge">({group.count})</span></>,
                  group.tokens.map((token, tokenIdx) => (
                    <div key={`prefix-${idx}-token-${tokenIdx}`} className="tree-branch">
                      <div className="branch-line"></div>
                      {renderTokenNode(token)}
                    </div>
                  )),
                  'group-node',
                  'prefix-group'
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Suffix Groups */}
        <div className="tree-category">
          <div className="tree-category-header">
            <h4>🌲 접미사 기준 그룹 (뒷글자순)</h4>
          </div>
          <div className="tree-content">
            {classificationData.suffixGroups.map((group, idx) => (
              <div key={`suffix-${idx}`} className="group-tree">
                {renderTreeNode(
                  <><span className="group-marker">📌</span> 뒷부분: {group.substring} <span className="count-badge">({group.count})</span></>,
                  group.tokens.map((token, tokenIdx) => (
                    <div key={`suffix-${idx}-token-${tokenIdx}`} className="tree-branch">
                      <div className="branch-line"></div>
                      {renderTokenNode(token)}
                    </div>
                  )),
                  'group-node',
                  'suffix-group'
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Compound Groups */}
        {classificationData.compoundGroups && classificationData.compoundGroups.length > 0 && (
          <div className="tree-category">
            <div className="tree-category-header">
              <h4>🌲 복합어 관계 그룹</h4>
            </div>
            <div className="tree-content">
              {classificationData.compoundGroups.map((group, idx) => {
                const parentClean = group.parent.replace('[괄호] ', '');

                return (
                  <div key={`compound-${idx}`} className="group-tree">
                    {renderTreeNode(
                      <><span className="group-marker">📌</span> 복합어: {parentClean} <span className="count-badge">({group.children.length})</span></>,
                      group.children.map((child, childIdx) => {
                        const position = group.positions[childIdx];
                        const childClean = child.replace('[괄호] ', '');

                        return (
                          <div key={`compound-${idx}-child-${childIdx}`} className="tree-branch with-position">
                            <div className="branch-line"></div>
                            <div className="branch-content">
                              {position === 0 ? (
                                <span className="position-indicator position-start">⬅️ 처음</span>
                              ) : position + childClean.length === parentClean.length ? (
                                <span className="position-indicator position-end">➡️ 끝</span>
                              ) : null}
                              {renderTokenNode(child)}
                            </div>
                          </div>
                        );
                      }),
                      'group-node',
                      'compound-group'
                    )}
                  </div>
                );
              })}
            </div>
          </div>
        )}

        {/* Ungrouped Tokens */}
        {classificationData.ungroupedTokens && classificationData.ungroupedTokens.length > 0 && (
          <div className="tree-category">
            <div className="tree-category-header">
              <h4>🌲 미분류 토큰</h4>
            </div>
            <div className="tree-content ungrouped-content">
              {classificationData.ungroupedTokens.map((token, idx) => (
                <div key={`ungrouped-${idx}`} className="ungrouped-token">
                  {renderTokenNode(token)}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default SeparationClassifier;
