import React, { useState, useEffect } from 'react';
import { fetchHeaderSimilarityList } from '../services/api';
import Column from './Column';
import './HeaderSimilarityList.css';

const HeaderSimilarityList = ({ channel, selectedHeader }) => {
  const [similarHeaders, setSimilarHeaders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedSimilarGroups, setSelectedSimilarGroups] = useState([]);
  const [currentGroup, setCurrentGroup] = useState([]);
  const [remainingHeaders, setRemainingHeaders] = useState([]);

  useEffect(() => {
    const getSimilarHeaders = async () => {
      if (!channel || !selectedHeader) return;

      setLoading(true);
      try {
        const response = await fetchHeaderSimilarityList(channel, selectedHeader);
        console.log('Similar headers response:', response);
        setSimilarHeaders(response.list || []);
        setRemainingHeaders(response.list || []);
        setError(null);
        // Reset selections when header changes
        setSelectedSimilarGroups([]);
        setCurrentGroup([]);
      } catch (err) {
        setError(err.message);
        setSimilarHeaders([]);
        setRemainingHeaders([]);
      } finally {
        setLoading(false);
      }
    };

    getSimilarHeaders();
  }, [channel, selectedHeader]);

  const handleAddToCurrentGroup = (item) => {
    // Add to current group
    setCurrentGroup([...currentGroup, item]);
    // Remove from remaining headers
    setRemainingHeaders(remainingHeaders.filter(h => h.header !== item.header));
  };

  const handleRemoveFromCurrentGroup = (item) => {
    // Remove from current group
    setCurrentGroup(currentGroup.filter(h => h.header !== item.header));
    // Add back to remaining headers
    setRemainingHeaders([...remainingHeaders, item]);
  };

  const handleSaveCurrentGroup = () => {
    if (currentGroup.length > 0) {
      // Add current group to selected groups
      setSelectedSimilarGroups([...selectedSimilarGroups, {
        items: currentGroup,
        representative: currentGroup[0] // Default representative is the first item
      }]);
      // Reset current group
      setCurrentGroup([]);
    }
  };

  const handleSetRepresentative = (groupIndex, itemIndex) => {
    setSelectedSimilarGroups(selectedSimilarGroups.map((group, idx) => {
      if (idx === groupIndex) {
        // Move the selected item to the first position to mark as representative
        const newItems = [...group.items];
        const [movedItem] = newItems.splice(itemIndex, 1);
        newItems.unshift(movedItem);
        return { ...group, items: newItems, representative: movedItem };
      }
      return group;
    }));
  };

  const handleRemoveGroup = (groupIndex) => {
    // Get items from the group being removed
    const itemsToRestore = selectedSimilarGroups[groupIndex].items;

    // Remove the group
    setSelectedSimilarGroups(selectedSimilarGroups.filter((_, idx) => idx !== groupIndex));

    // Restore items to remaining headers
    setRemainingHeaders([...remainingHeaders, ...itemsToRestore]);
  };

  if (!channel || !selectedHeader) {
    return (
      <div className="column-container">
        <div className="column-empty">채널과 헤더를 선택해주세요.</div>
      </div>
    );
  }

  return (
    <div className="similarity-container">
      <h2 className="column-title">{`헤더명 유사군 목록: ${selectedHeader}`}</h2>

      {/* Selected Similar Groups */}
      <div className="selected-groups">
        <h3>선택된 유사군</h3>
        {selectedSimilarGroups.length > 0 ? (
          <div className="groups-list">
            {selectedSimilarGroups.map((group, groupIndex) => (
              <div key={groupIndex} className="similar-group">
                <div className="group-header">
                  <span className="group-title">유사군 {groupIndex + 1}</span>
                  <button 
                    className="icon-button remove-group" 
                    onClick={() => handleRemoveGroup(groupIndex)}
                    title="그룹 제거"
                  >
                    ✕
                  </button>
                </div>
                <div className="group-items">
                  {group.items.map((item, itemIndex) => (
                    <div key={itemIndex} className="group-item">
                      <span className={`item-text ${itemIndex === 0 ? 'representative' : ''}`}>
                        {item.header}
                        {itemIndex === 0 && <span className="rep-badge">대표어</span>}
                      </span>
                      {itemIndex !== 0 && (
                        <button 
                          className="icon-button set-rep" 
                          onClick={() => handleSetRepresentative(groupIndex, itemIndex)}
                          title="대표어로 설정"
                        >
                          ★
                        </button>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="empty-message">선택된 유사군이 없습니다.</div>
        )}
      </div>

      {/* Current Working Group */}
      <div className="current-group">
        <h3>현재 작업 중인 유사군</h3>
        {currentGroup.length > 0 ? (
          <>
            <div className="group-items">
              {currentGroup.map((item, index) => (
                <div key={index} className="group-item">
                  <span className="item-text">{item.header}</span>
                  <button 
                    className="icon-button remove-item" 
                    onClick={() => handleRemoveFromCurrentGroup(item)}
                    title="항목 제거"
                  >
                    ✕
                  </button>
                </div>
              ))}
            </div>
            <button 
              className="save-group-button" 
              onClick={handleSaveCurrentGroup}
              disabled={currentGroup.length === 0}
            >
              유사군 저장
            </button>
          </>
        ) : (
          <div className="empty-message">아래 목록에서 유사 헤더를 선택하세요.</div>
        )}
      </div>

      {/* Remaining Similar Headers */}
      <div className="remaining-headers">
        <h3>유사 헤더 목록</h3>
        {loading ? (
          <div className="loading">로딩 중...</div>
        ) : error ? (
          <div className="error">에러: {error}</div>
        ) : remainingHeaders.length > 0 ? (
          <div className="column-content">
            {remainingHeaders.map((item, index) => (
              <div 
                key={index}
                className="column-item"
                onClick={() => handleAddToCurrentGroup(item)}
              >
                {item.header}
              </div>
            ))}
          </div>
        ) : (
          <div className="empty-message">유사 헤더 정보가 없습니다.</div>
        )}
      </div>
    </div>
  );
};

export default HeaderSimilarityList;
