import React, { useState, useEffect } from 'react';
import './WorkspaceColumn.css';

const WorkspaceColumn = ({ selectedFile, fileContent }) => {
  const [selectedTab, setSelectedTab] = useState('view');
  const [content, setContent] = useState('');
  const [tableData, setTableData] = useState({
    headers: [],
    rows: []
  });
  const [originalTableData, setOriginalTableData] = useState(null);

  useEffect(() => {
    // Reset content when file changes
    if (selectedFile) {
      if (selectedFile.type === 'xlsx_header') {
        // Handle XLSX header selection
        const { sheetData, headerIndex } = selectedFile;

        // Extract the column data for this header
        const headerName = sheetData.headers[headerIndex];
        const columnData = sheetData.rows.map(row => row[headerIndex]);

        // Set the table data for display
        const newTableData = {
          headers: [headerName],
          rows: columnData.map(value => [value])
        };

        setTableData(newTableData);
        setOriginalTableData({...newTableData});

        // Switch to view tab
        setSelectedTab('view');

        // Set content to empty to use table view instead
        setContent('');
      } else if (selectedFile.type === 'file') {
        // Regular file selected, show file info
        setContent(JSON.stringify({
          fileName: selectedFile.name,
          path: selectedFile.path,
          type: selectedFile.type
        }, null, 2));

        // Clear table data
        setTableData({
          headers: [],
          rows: []
        });
        setOriginalTableData(null);
      } else if (selectedFile.type === 'xlsx_sheet') {
        // Sheet selected, show all data from the sheet
        const { sheetData } = selectedFile;

        const newTableData = {
          headers: sheetData.headers,
          rows: sheetData.rows
        };

        setTableData(newTableData);
        setOriginalTableData({...newTableData});

        // Switch to view tab
        setSelectedTab('view');

        // Set content to empty to use table view instead
        setContent('');
      }
    } else {
      // No file selected
      setContent(JSON.stringify({
        message: "No file selected",
        instruction: "Select a file or header from the file explorer"
      }, null, 2));

      // Clear table data
      setTableData({
        headers: [],
        rows: []
      });
      setOriginalTableData(null);
    }
  }, [selectedFile]);

  const handleTabChange = (tab) => {
    setSelectedTab(tab);
  };

  // Î∂ÑÎ¶¨Î∂ÑÎ•ò Í∏∞Îä• Ï≤òÎ¶¨ Ìï®Ïàò
  const handleSeparationMagic = () => {
    if (!tableData.headers.length || !tableData.rows.length) return;

    // ÏõêÎ≥∏ Îç∞Ïù¥ÌÑ∞Í∞Ä ÏûàÎäî Í≤ΩÏö∞ ÌôúÏö© (Î¶¨ÏÖã Í∞ÄÎä•ÌïòÎèÑÎ°ù)
    const dataToProcess = originalTableData ? [...originalTableData.rows] : [...tableData.rows];

    // Ï§ëÎ≥µ Ï†úÍ±∞Î•º ÏúÑÌïú Set Í∞ùÏ≤¥Îì§
    const bracketSet = new Set();
    const valueSet = new Set();

    // Í∞Å Ìñâ Ï≤òÎ¶¨
    dataToProcess.forEach(row => {
      const cellValue = row[0];

      // null, undefined, Îπà Î¨∏ÏûêÏó¥ Îì± Í±¥ÎÑàÎõ∞Í∏∞
      if (cellValue === null || cellValue === undefined || String(cellValue).trim() === '') {
        return;
      }

      const cellValueStr = String(cellValue);

      // 1. Í¥ÑÌò∏ ÎÇ¥Ïö© Ï≤òÎ¶¨ - Îã§ÏñëÌïú Í¥ÑÌò∏ Ïú†Ìòï Ï≤òÎ¶¨ (ÏÜåÍ¥ÑÌò∏, ÎåÄÍ¥ÑÌò∏, Ï§ëÍ¥ÑÌò∏)
      const bracketContentRegex = /\(([^)]*)\)|\[([^\]]*)\]|\{([^}]*)\}/g;
      let textWithoutBrackets = cellValueStr.replace(bracketContentRegex, (match, p1, p2, p3) => {
        const content = p1 || p2 || p3; // Í¥ÑÌò∏ Ï¢ÖÎ•òÏóê Îî∞Îùº p1, p2, p3 Ï§ë ÌïòÎÇòÏóê Í∞íÏù¥ ÏûàÏùå
        if (content && content.trim()) {
          // Ï§ëÎ≥µ Ï†úÍ±∞Î•º ÏúÑÌï¥ SetÏóê Ï∂îÍ∞Ä
          bracketSet.add(content.trim());
        }
        return ' '; // Í¥ÑÌò∏ÏôÄ ÎÇ¥Ïö©ÏùÑ Í≥µÎ∞±ÏúºÎ°ú ÎåÄÏ≤¥
      });

      // 2. Í≥µÎ∞±Í≥º ÏâºÌëúÎ°ú Î∂ÑÎ¶¨
      const separatedValues = textWithoutBrackets
        .split(/[\s,]+/) // Í≥µÎ∞±Í≥º ÏâºÌëúÎ°ú Î∂ÑÎ¶¨
        .map(value => value.trim())
        .filter(value => value.length > 0); // Îπà Î¨∏ÏûêÏó¥ Ï†úÍ±∞

      // Ï§ëÎ≥µ Ï†úÍ±∞Î•º ÏúÑÌï¥ SetÏóê Ï∂îÍ∞Ä
      separatedValues.forEach(value => {
        if (value && value.trim()) {
          valueSet.add(value.trim());
        }
      });
    });

    // Í≤∞Í≥º Í∞í Î∞∞Ïó¥ ÏÉùÏÑ± (Ï§ëÎ≥µ Ï†úÍ±∞Îêú Í∞íÎì§)
    let bracketValues = Array.from(bracketSet).map(val => `[Í¥ÑÌò∏] ${val}`);
    let normalValues = Array.from(valueSet);

    // Í≤∞Í≥ºÍ∞Ä ÏóÜÏúºÎ©¥ "Î∂ÑÎ¶¨Îêú Í∞íÏù¥ ÏóÜÏäµÎãàÎã§" ÌëúÏãú
    if (bracketValues.length === 0 && normalValues.length === 0) {
      setTableData({
        headers: ["Group", "Token"],
        rows: [["", "Î∂ÑÎ¶¨Îêú Í∞íÏù¥ ÏóÜÏäµÎãàÎã§"]]
      });
      return;
    }

    // Î¨∏ÏûêÏó¥ Îí§ÏßëÍ∏∞ Ìï®Ïàò
    const reverseString = str => str.split('').reverse().join('');

    // Î™®Îì† ÏµúÏÜå ÌÜ†ÌÅ∞ Î∞∞Ïó¥
    const allMinTokens = [...bracketValues, ...normalValues];

    // 3. Î∂ÄÎ∂ÑÎ¨∏ÏûêÏó¥ Í∏∞Î∞ò ÏïÑÏù¥ÌÖúÏÖã ÎßàÏù¥Îãù (Í≥µÌÜµ Î∂ÄÎ∂Ñ Î¨∏ÏûêÏó¥ Ï∂îÏ∂ú)
    // ÏûêÏ£º Îì±Ïû•ÌïòÎäî Î∂ÄÎ∂Ñ Î¨∏ÏûêÏó¥ Ï∞æÍ∏∞
    const findCommonSubstrings = (tokens, minLength = 2, minCount = 2) => {
      // Î∂ÄÎ∂Ñ Î¨∏ÏûêÏó¥ ÎπàÎèÑ Ïπ¥Ïö¥Ìä∏
      const substringFreq = {};

      // Î™®Îì† Í∞ÄÎä•Ìïú Î∂ÄÎ∂Ñ Î¨∏ÏûêÏó¥ Ï∂îÏ∂ú Î∞è Ïπ¥Ïö¥Ìä∏
      tokens.forEach(token => {
        // Í¥ÑÌò∏ ÌÉúÍ∑∏ Ï†úÍ±∞
        const cleanToken = token.replace('[Í¥ÑÌò∏] ', '');

        for (let len = minLength; len <= cleanToken.length; len++) {
          for (let i = 0; i <= cleanToken.length - len; i++) {
            const substr = cleanToken.substring(i, i + len);
            if (!substringFreq[substr]) {
              substringFreq[substr] = { count: 1, tokens: [token] };
            } else if (!substringFreq[substr].tokens.includes(token)) {
              substringFreq[substr].count++;
              substringFreq[substr].tokens.push(token);
            }
          }
        }
      });

      // ÎπàÎèÑÏóê Îî∞Îùº ÌïÑÌÑ∞ÎßÅ Î∞è Ï†ïÎ†¨
      const commonSubstrings = Object.entries(substringFreq)
        .filter(([substr, data]) => data.count >= minCount)
        .sort((a, b) => {
          // 1. ÌÜ†ÌÅ∞ Ïàò ÎÇ¥Î¶ºÏ∞®Ïàú
          const countDiff = b[1].count - a[1].count;
          if (countDiff !== 0) return countDiff;

          // 2. Í∏∏Ïù¥ ÎÇ¥Î¶ºÏ∞®Ïàú
          const lengthDiff = b[0].length - a[0].length;
          if (lengthDiff !== 0) return lengthDiff;

          // 3. ÏïåÌååÎ≤≥ ÏàúÏÑú
          return a[0].localeCompare(b[0]);
        })
        .map(([substr, data]) => ({
          substring: substr,
          count: data.count,
          tokens: data.tokens,
          percentCoverage: (data.tokens.length / tokens.length) * 100
        }));

      return commonSubstrings;
    };

    // ÏµúÏÜå ÌÜ†ÌÅ∞ÏóêÏÑú Í≥µÌÜµ Î∂ÄÎ∂Ñ Î¨∏ÏûêÏó¥ Ï∂îÏ∂ú
    const commonSubstrings = findCommonSubstrings(allMinTokens, 2, 2);

    // Ï§ëÎ≥µ Ï†úÍ±∞ÏôÄ Ïª§Î≤ÑÎ¶¨ÏßÄ ÏµúÏ†ÅÌôîÎ•º ÏúÑÌïú Í∑∏Î¶¨Îîî ÏïåÍ≥†Î¶¨Ï¶ò
    const selectOptimalSubstrings = (substrings, tokens) => {
      // Ïª§Î≤ÑÎ¶¨ÏßÄÍ∞Ä Ï¢ãÏùÄ ÏàúÏÑúÎ°ú Ï†ïÎ†¨
      const sortedSubstrings = [...substrings]
        .sort((a, b) => {
          // ÌÜ†ÌÅ∞Ïàò * Í∏∏Ïù¥Î°ú Ï†êÏàò Í≥ÑÏÇ∞ (Îçî ÎßéÏùÄ ÌÜ†ÌÅ∞ÏùÑ Ïª§Î≤ÑÌïòÍ≥† Îçî Í∏¥ Î∂ÄÎ∂ÑÎ¨∏ÏûêÏó¥ Ïö∞ÏÑ†)
          const scoreA = a.count * a.substring.length;
          const scoreB = b.count * b.substring.length;
          return scoreB - scoreA;
        });

      const selectedSubstrings = [];
      const coveredTokens = new Set();

      // Í∑∏Î¶¨Îîî ÏÑ†ÌÉù
      for (const substr of sortedSubstrings) {
        // ÏÉàÎ°úÏö¥ ÌÜ†ÌÅ∞ÏùÑ Ïª§Î≤ÑÌïòÎäîÏßÄ ÌôïÏù∏
        const newCoverage = substr.tokens.some(token => !coveredTokens.has(token));

        // ÏÉà ÌÜ†ÌÅ∞ÏùÑ Ïª§Î≤ÑÌïòÍ±∞ÎÇò Ï§ëÏöî ÏÑúÎ∏åÏä§Ìä∏ÎßÅÏù∏ Í≤ΩÏö∞Îßå ÏÑ†ÌÉù
        if (newCoverage || (substr.count >= 3 && substr.substring.length >= 3)) {
          selectedSubstrings.push(substr);

          // Ïª§Î≤ÑÎêú ÌÜ†ÌÅ∞ ÏóÖÎç∞Ïù¥Ìä∏
          substr.tokens.forEach(token => coveredTokens.add(token));
        }

        // ÏµúÎåÄ 15Í∞ú Í∑∏Î£πÏúºÎ°ú Ï†úÌïú
        if (selectedSubstrings.length >= 15) break;
      }

      return selectedSubstrings;
    };

    // ÏµúÏ†ÅÏùò Î∂ÄÎ∂Ñ Î¨∏ÏûêÏó¥ ÏÑ†ÌÉù
    const optimalSubstrings = selectOptimalSubstrings(commonSubstrings, allMinTokens);

    // 4. Îç¥ÎìúÎ°úÍ∑∏Îû® Ïä§ÌÉÄÏùºÏùò Îç∞Ïù¥ÌÑ∞ Íµ¨Ï°∞ Íµ¨ÏÑ±
    // Í∞Å ÌÜ†ÌÅ∞Ïù¥ Ïñ¥Îñ§ Í∑∏Î£πÏóê ÏÜçÌïòÎäîÏßÄ Îß§Ìïë
    const tokenToGroupMap = {};

    allMinTokens.forEach(token => {
      const cleanToken = token.replace('[Í¥ÑÌò∏] ', '');
      tokenToGroupMap[token] = optimalSubstrings
        .filter(group => {
          const isBracketContent = token.startsWith('[Í¥ÑÌò∏]');
          // Í¥ÑÌò∏ ÏΩòÌÖêÏ∏†Ïóê ÎåÄÌï¥ Ï†ïÌôïÌûà ÏùºÏπòÌïòÎäî Í∑∏Î£πÎßå ÌóàÏö©
          if (isBracketContent) {
            return group.tokens.includes(token);
          }
          // ÏùºÎ∞ò ÌÜ†ÌÅ∞Ïóê ÎåÄÌï¥ Î∂ÄÎ∂Ñ Î¨∏ÏûêÏó¥ Ìè¨Ìï® Ïó¨Î∂Ä ÌôïÏù∏
          return cleanToken.includes(group.substring);
        })
        .map(group => group.substring);
    });

    // ÌÖåÏù¥Î∏î Ìñâ Íµ¨ÏÑ±
    const finalRows = [];

    // Í∑∏Î£π Ìó§Îçî Ìñâ Ï∂îÍ∞Ä (Î∂ÄÎ∂Ñ Î¨∏ÏûêÏó¥ Í∑∏Î£π)
    optimalSubstrings.forEach(group => {
      finalRows.push([
        `üìä Group (${group.count}): ${group.substring}`, 
        ""
      ]);

      // Ïù¥ Í∑∏Î£πÏóê ÏÜçÌïú Î™®Îì† ÌÜ†ÌÅ∞ Ï∂îÍ∞Ä
      const groupTokens = group.tokens.sort((a, b) => {
        // Í¥ÑÌò∏ ÏΩòÌÖêÏ∏† Î®ºÏ†Ä, Í∑∏ Îã§Ïùå ÏïåÌååÎ≤≥ Ïàú
        const aIsBracket = a.startsWith('[Í¥ÑÌò∏]');
        const bIsBracket = b.startsWith('[Í¥ÑÌò∏]');

        if (aIsBracket && !bIsBracket) return -1;
        if (!aIsBracket && bIsBracket) return 1;

        return a.localeCompare(b);
      });

      groupTokens.forEach(token => {
        finalRows.push(["‚îî‚îÄ", token]);
      });

      // Í∑∏Î£π Í∞Ñ Íµ¨Î∂ÑÏùÑ ÏúÑÌïú Îπà Ìñâ
      finalRows.push(["", ""]);
    });

    // Í∑∏Î£πÏóê ÏÜçÌïòÏßÄ ÏïäÏùÄ ÌÜ†ÌÅ∞ Ï≤òÎ¶¨
    const ungroupedTokens = allMinTokens
      .filter(token => {
        return !optimalSubstrings.some(group => group.tokens.includes(token));
      })
      .sort();

    if (ungroupedTokens.length > 0) {
      finalRows.push(["üìå Ungrouped Tokens", ""]);
      ungroupedTokens.forEach(token => {
        finalRows.push(["‚îî‚îÄ", token]);
      });
    }

    // ÌÖåÏù¥Î∏î Îç∞Ïù¥ÌÑ∞ ÏóÖÎç∞Ïù¥Ìä∏
    setTableData({
      headers: ["Group", "Token"],
      rows: finalRows
    });
  };

  // ÏõêÎ≥∏ Îç∞Ïù¥ÌÑ∞Î°ú Î≥µÏõêÌïòÎäî Ìï®Ïàò
  const resetToOriginal = () => {
    if (originalTableData) {
      setTableData({...originalTableData});
    }
  };

  const renderTable = () => {
    if (!tableData.headers.length) return null;

    return (
      <div className="data-table-container">
        <h4>{selectedFile?.name} - Data View</h4>
        <table className="data-table">
          <thead>
            <tr>
              {tableData.headers.map((header, idx) => (
                <th key={idx}>{header}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {tableData.rows.map((row, rowIdx) => {
              // Í∑∏Î£π Ìñâ Ïä§ÌÉÄÏùºÎßÅ
              const isGroupHeader = String(row[0]).startsWith('üìä') || String(row[0]).startsWith('üìå');
              const isGroupItem = String(row[0]).startsWith('‚îî‚îÄ');

              return (
                <tr 
                  key={rowIdx} 
                  className={`${isGroupHeader ? 'group-header' : ''} ${isGroupItem ? 'group-item' : ''}`}
                >
                  {row.map((cell, cellIdx) => {
                    // Í¥ÑÌò∏ ÎÇ¥Ïö© Ïó¨Î∂Ä ÌôïÏù∏
