import React, { useState, useEffect } from 'react';
import PipelineStep from './PipelineStep';
import DataDisplay from './DataDisplay';
import GroupsPanel from './GroupsPanel';
import { processBracketSeparation, processDelimiterSeparation, processSubstringSegmentation, processSegmentationGrouping, flattenData } from './dataProcessors';
import './SeparationClassifier.css';

const SeparationClassifier = ({ data, onReset }) => {
  const [processedData, setProcessedData] = useState(null);
  const [segmentationData, setSegmentationData] = useState(null);
  const [groupingData, setGroupingData] = useState(null);
  const [pipeline, setPipeline] = useState([
    { id: 'bracket-separation', name: '괄호값 분리', active: true },
    { 
      id: 'delimiter-separation', 
      name: '구분자 분리', 
      active: true,
      config: {
        delimiters: [
          { value: ',', enabled: true, order: 1 },
          { value: ' ', enabled: true, order: 2 }
        ]
      }
    },
    {
      id: 'substring-segmentation',
      name: '상호부분문자열 분절',
      active: true,
      config: {
        minSubstringLength: 2,
        minOccurrence: 2
      }
    },
    {
      id: 'segmentation-grouping',
      name: '분절값 그룹핑',
      active: true,
      config: {
        groups: [],
        replaceNumbers: false
      }
    }
  ]);
  const [selectedStep, setSelectedStep] = useState('delimiter-separation');
  const [selectedGroupId, setSelectedGroupId] = useState(null);

  // Check if substring segmentation is active
  const isSubstringSegmentationActive = pipeline.find(step => step.id === 'substring-segmentation')?.active || false;

  // Process data through pipeline steps
  useEffect(() => {
    if (data) {
      // Clean up duplicated data before processing
      let cleanedData = { ...data };

      // Check if data is already a processed result with duplicate content
      const isDuplicated = (input) => {
        if (typeof input === 'string') {
          const halfLength = Math.floor(input.length / 2);
          for (let i = 1; i <= halfLength; i++) {
            if (input.substring(0, i) === input.substring(i, i * 2)) {
              return true;
            }
          }
        }
        return false;
      };

      // Clean up data if it's duplicated
      const cleanData = (input) => {
        if (typeof input === 'string' && isDuplicated(input)) {
          // Try to find a reasonable split point
          const halfLength = Math.floor(input.length / 2);
          return input.substring(0, halfLength);
        } else if (Array.isArray(input)) {
          return input.map(cleanData);
        } else if (typeof input === 'object' && input !== null) {
          const result = {};
          for (const key in input) {
            result[key] = cleanData(input[key]);
          }
          return result;
        }
        return input;
      };

      // Only clean if it appears to be duplicated
      if (Object.values(cleanedData).some(val => 
        typeof val === 'string' && isDuplicated(val)
      )) {
        cleanedData = cleanData(cleanedData);
      }

      // Process data through pipeline steps
      let result = { ...cleanedData };

      // Process bracket and delimiter steps
      pipeline.forEach(step => {
        if (step.active) {
          if (step.id === 'bracket-separation') {
            result = processBracketSeparation(result);
          } else if (step.id === 'delimiter-separation') {
            result = processDelimiterSeparation(result, step.config.delimiters);
          }
        }
      });

      // Process substring segmentation separately if active
      const segmentationStep = pipeline.find(step => step.id === 'substring-segmentation');
      if (segmentationStep && segmentationStep.active) {
        const segmentResult = processSubstringSegmentation(result, segmentationStep.config);
        setSegmentationData(segmentResult);

        // Process segmentation grouping if active
        const groupingStep = pipeline.find(step => step.id === 'segmentation-grouping');
        if (groupingStep && groupingStep.active) {
          const groupingResult = processSegmentationGrouping(segmentResult, groupingStep.config);
          setGroupingData(groupingResult);
        } else {
          setGroupingData(null);
        }
      } else {
        setSegmentationData(null);
        setGroupingData(null);
      }

      setProcessedData(result);
    } else {
      setProcessedData(null);
      setSegmentationData(null);
      setGroupingData(null);
    }
  }, [data, pipeline]);

  const toggleStepActive = (stepId) => {
    setPipeline(pipeline.map(step => 
      step.id === stepId ? { ...step, active: !step.active } : step
    ));
  };

  const selectStep = (stepId) => {
    setSelectedStep(stepId);
  };

  // Update delimiter configuration
  const updateDelimiters = (delimitersInput) => {
    setPipeline(pipeline.map(step => {
      if (step.id === 'delimiter-separation') {
        // If input is a string, convert to delimiter objects
        if (typeof delimitersInput === 'string') {
          const delimiters = delimitersInput.split('').map((char, idx) => ({
            value: char,
            enabled: true,
            order: idx + 1
          }));

          return { 
            ...step, 
            config: { 
              ...step.config, 
              delimiters
            } 
          };
        }

        // Input is already an array of delimiter objects
        return { 
          ...step, 
          config: { 
            ...step.config, 
            delimiters: delimitersInput
          } 
        };
      }
      return step;
    }));
  };

  // Update substring segmentation configuration
  const updateSegmentationConfig = (configUpdate) => {
    setPipeline(pipeline.map(step => {
      if (step.id === 'substring-segmentation') {
        return { 
          ...step, 
          config: { 
            ...step.config, 
            ...configUpdate 
          } 
        };
      }
      return step;
    }));
  };

  // Update segmentation grouping configuration
  const updateGroupingConfig = (configUpdate) => {
    setPipeline(pipeline.map(step => {
      if (step.id === 'segmentation-grouping') {
        return { 
          ...step, 
          config: { 
            ...step.config, 
            ...configUpdate 
          } 
        };
      }
      return step;
    }));
  };

  // Toggle number replacement feature
  const toggleNumberReplacement = () => {
    const groupStep = pipeline.find(step => step.id === 'segmentation-grouping');
    if (groupStep) {
      updateGroupingConfig({
        replaceNumbers: !groupStep.config.replaceNumbers
      });
    }
  };

  // Add a new empty group to the grouping step
  const addEmptyGroup = () => {
    const groupStep = pipeline.find(step => step.id === 'segmentation-grouping');
    if (groupStep) {
      const newGroup = {
        id: `group-${Date.now()}`,
        name: `Group ${groupStep.config.groups.length + 1}`,
        items: []
      };

      updateGroupingConfig({
        groups: [...groupStep.config.groups, newGroup]
      });
    }
  };

  // Update a group's name
  const updateGroupName = (groupId, newName) => {
    const groupStep = pipeline.find(step => step.id === 'segmentation-grouping');
    if (groupStep) {
      const updatedGroups = groupStep.config.groups.map(group => 
        group.id === groupId ? { ...group, name: newName } : group
      );

      updateGroupingConfig({ groups: updatedGroups });
    }
  };

  // Add a value to a specific group
  const addValueToGroup = (groupId, value) => {
    const groupStep = pipeline.find(step => step.id === 'segmentation-grouping');
    if (groupStep) {
      const updatedGroups = groupStep.config.groups.map(group => {
        if (group.id === groupId) {
          // Only add if not already in the group
          if (!group.items.includes(value)) {
            return { ...group, items: [...group.items, value] };
          }
        }
        return group;
      });

      updateGroupingConfig({ groups: updatedGroups });
    }
  };

  // Remove a value from a specific group
  const removeValueFromGroup = (groupId, value) => {
    const groupStep = pipeline.find(step => step.id === 'segmentation-grouping');
    if (groupStep) {
      const updatedGroups = groupStep.config.groups.map(group => {
        if (group.id === groupId) {
          return { ...group, items: group.items.filter(item => item !== value) };
        }
        return group;
      });

      updateGroupingConfig({ groups: updatedGroups });
    }
  };

  // Prepare pipeline steps with callbacks
  const preparedPipeline = pipeline.map(step => {
    if (step.id === 'delimiter-separation') {
      return {
        ...step,
        onUpdateDelimiters: updateDelimiters
      };
    } else if (step.id === 'substring-segmentation') {
      return {
        ...step,
        onUpdateConfig: updateSegmentationConfig
      };
    } else if (step.id === 'segmentation-grouping') {
      return {
        ...step,
        onUpdateConfig: updateGroupingConfig,
        onAddGroup: addEmptyGroup,
        onUpdateGroupName: updateGroupName,
        onToggleNumberReplacement: toggleNumberReplacement,
        onAddValueToGroup: addValueToGroup,
        onRemoveValueFromGroup: removeValueFromGroup
      };
    }
    return step;
  });

  // Check if we have grouping data
  const hasGroupingData = groupingData && groupingData.uniqueValues && Array.isArray(groupingData.uniqueValues);

  // Create an enhanced groupingData with required callbacks
  const enhancedGroupingData = hasGroupingData ? {
    ...groupingData,
    onAddGroup: addEmptyGroup,
    onUpdateGroupName: updateGroupName,
    onAddValueToGroup: addValueToGroup,
    onRemoveValueFromGroup: removeValueFromGroup
  } : null;

  return (
    <div className="separation-classifier" style={{ height: '100%', overflow: 'hidden' }}>
      <div className="separation-classifier-container">
        {/* Main content area */}
        <div className="main-content-area">
          <div className="classifier-layout" style={{ height: '100%', display: 'flex' }}>
            {/* Data View */}
            <div className="data-view" style={{ flex: 1, overflow: 'hidden', display: 'flex', flexDirection: 'column' }}>
              <h3>처리된 데이터 (회색: 미처리 영역)</h3>
              <div className="data-content" style={{ flex: 1, overflow: 'auto' }}>
                <DataDisplay 
                  processedData={processedData} 
                  flattenData={flattenData}
                  segmentationData={segmentationData}
                  groupingData={hasGroupingData ? {
                    ...groupingData,
                    selectedGroupId,
                  } : null}
                />
              </div>
            </div>

            {/* Right side - Pipeline */}
            <div className="pipeline-panel" style={{ width: '300px', overflow: 'hidden', display: 'flex', flexDirection: 'column' }}>
              <h3>파이프라인</h3>
              <div className="pipeline-list" style={{ flex: 1, overflow: 'auto' }}>
                {preparedPipeline.map((step, index) => (
                  <PipelineStep 
                    key={step.id}
                    step={step}
                    index={index}
                    isSelected={selectedStep === step.id}
                    onSelect={selectStep}
                    onToggle={toggleStepActive}
                  />
                ))}

                <button className="add-step">
                  + 처리 단계 추가
                </button>
              </div>

              <div className="pipeline-actions">
                <button className="reset-button" onClick={onReset}>
                  초기화
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Right side - Groups Panel (independent) */}
        {hasGroupingData && (
          <GroupsPanel 
            groupingData={enhancedGroupingData}
            selectedGroupId={selectedGroupId}
            setSelectedGroupId={setSelectedGroupId}
          />
        )}
      </div>
    </div>
  );
};

export default SeparationClassifier;
