import React, { useState, useEffect, useRef } from 'react';

const MiningView = ({ combinationHeaders, loading, error, testMode, onPatternSelect }) => {
  const [miningData, setMiningData] = useState(null);
  const [expandedNodes, setExpandedNodes] = useState({});
  const [highlightedPattern, setHighlightedPattern] = useState(null);
  const [algorithm, setAlgorithm] = useState('fpgrowth'); // Only use FP-Growth
  const [minSupport, setMinSupport] = useState(0.01); // Changed to 1% default
  const [minConfidence, setMinConfidence] = useState(0.01); // Changed to 1% default
  const [maxDepth, setMaxDepth] = useState(100); // No practical limit
  const [currentPath, setCurrentPath] = useState([]);
  const [showSettings, setShowSettings] = useState(false);
  const [ignoredNodes, setIgnoredNodes] = useState(new Set());
  const svgRef = useRef(null);

  // 데이터가 변경될 때마다 마이닝 실행
  useEffect(() => {
    if (combinationHeaders && combinationHeaders.length > 0) {
      generateMiningData(combinationHeaders, algorithm);
    } else {
      setMiningData(null);
    }
  }, [combinationHeaders, algorithm, minSupport, minConfidence, maxDepth]);

  // Effect to update SVG visualization when mining data changes
  useEffect(() => {
    if (miningData && svgRef.current) {
      renderMiningTree();
    }
  }, [miningData, expandedNodes, highlightedPattern]);

  // Function to render a unified hierarchical tree visualization
  const renderMiningTree = () => {
    if (!svgRef.current || !miningData) return;

    const svg = svgRef.current;
    // Clear previous content
    while (svg.firstChild) {
      svg.removeChild(svg.firstChild);
    }

    const CONTAINER_WIDTH = svg.clientWidth || 300;
    const NODE_HEIGHT = 32; // Reduced height
    const NODE_SPACING = 10; // Reduced spacing
    const LEVEL_INDENT = 35; // Increased indent for better hierarchy visualization

    // Calculate tree structure based on expanded nodes
    const treeNodes = [];
    let yOffset = 20;  // Initial top padding

    // Recursive function to add nodes
    const addNodesRecursively = (nodes, parentId, level, isVisible) => {
      if (!nodes || nodes.length === 0) return;

      // Sort nodes by support for better visualization
      const sortedNodes = [...nodes].sort((a, b) => b.support - a.support);

      sortedNodes.forEach(node => {
        // Skip if parent is collapsed or node is ignored
        if (!isVisible || ignoredNodes.has(node.id)) return;

        const isExpanded = expandedNodes[node.id] || false;

        // Add current node
        treeNodes.push({
          ...node,
          level,
          x: 10 + (level * LEVEL_INDENT),
          y: yOffset,
          isExpanded,
          parent: parentId
        });

        // Increment y-position for next node
        yOffset += NODE_HEIGHT + NODE_SPACING;

        // Process children if any
        if (node.children && node.children.length > 0) {
          addNodesRecursively(node.children, node.id, level + 1, isVisible && isExpanded);
        }
      });
    };

    // Add all nodes from mining data
    const sortedTopNodes = [...miningData].sort((a, b) => b.support - a.support);
    addNodesRecursively(sortedTopNodes, null, 0, true);

    // Set SVG height based on content
    svg.setAttribute('height', `${yOffset + 20}px`);

    // Create a container for the SVG elements
    const container = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    svg.appendChild(container);

    // Draw connections between nodes
    treeNodes.forEach(node => {
      if (node.parent) {
        const parent = treeNodes.find(n => n.id === node.parent);
        if (parent) {
          const path = document.createElementNS('http://www.w3.org/2000/svg', 'path');

          const startX = parent.x + 8;
          const startY = parent.y + (NODE_HEIGHT / 2);
          const endX = node.x;
          const endY = node.y + (NODE_HEIGHT / 2);

          // Create an elbow connector
          const d = `M ${startX} ${startY} L ${startX + 5} ${startY} L ${startX + 5} ${endY} L ${endX} ${endY}`;

          path.setAttribute('d', d);
          path.setAttribute('fill', 'none');

          // Highlight path if node is part of highlighted pattern
          const isHighlighted = highlightedPattern && (
            highlightedPattern.includes(node.id) && highlightedPattern.includes(parent.id)
          );

          // Improved highlight styling
          if (isHighlighted) {
            path.setAttribute('stroke', '#3498db'); // More vibrant highlight color
            path.setAttribute('stroke-width', '2');
            path.setAttribute('stroke-dasharray', '0'); // Solid line
          } else {
            path.setAttribute('stroke', '#eee');
            path.setAttribute('stroke-width', '1');
          }

          container.appendChild(path);
        }
      }
    });

    // Draw nodes
    treeNodes.forEach(node => {
      const nodeGroup = document.createElementNS('http://www.w3.org/2000/svg', 'g');
      nodeGroup.setAttribute('transform', `translate(${node.x}, ${node.y})`);

      // Node background (rectangle)
      const rect = document.createElementNS('http://www.w3.org/2000/svg', 'rect');

      // Calculate width based on content and level
      const baseWidth = CONTAINER_WIDTH - node.x - 30;
      const width = Math.min(baseWidth, 240);

      rect.setAttribute('width', width);
      rect.setAttribute('height', NODE_HEIGHT);
      rect.setAttribute('rx', 4);  // Smaller corner radius

      // Determine fill color based on level and highlight state
      const isHighlighted = highlightedPattern && highlightedPattern.includes(node.id);

      // Use a more monotone color scheme
      let fillColor = node.level === 0 ? '#f8f9fa' : '#ffffff'; 

      if (isHighlighted) {
        fillColor = '#e6f2fa'; // Lighter blue tint for highlighted nodes
      }

      rect.setAttribute('fill', fillColor);
      rect.setAttribute('stroke', isHighlighted ? '#3498db' : '#ddd'); // Blue for highlight
      rect.setAttribute('stroke-width', isHighlighted ? '1.5' : '1');

      // Make nodes interactive
      nodeGroup.style.cursor = 'pointer';

      // Node click to send pattern to API
      nodeGroup.addEventListener('click', (e) => {
        // Only handle click if it's not on the ignore button or expand button
        if (!e.target.classList.contains('ignore-button') && 
            !e.target.classList.contains('expand-indicator')) {
          sendPatternToAPI(node);
        }
      });

      // Toggle expansion only for nodes with children
      if (node.children && node.children.length > 0) {
        const indicator = document.createElementNS('http://www.w3.org/2000/svg', 'text');
        indicator.textContent = node.isExpanded ? '−' : '+';
        indicator.setAttribute('x', width - 15);
        indicator.setAttribute('y', NODE_HEIGHT / 2 + 4);
        indicator.setAttribute('font-size', '13px');
        indicator.setAttribute('font-weight', 'bold');
        indicator.setAttribute('fill', '#888');
        indicator.classList.add('expand-indicator');

        // Toggle expansion on click
        indicator.addEventListener('click', (e) => {
          e.stopPropagation(); // Prevent triggering the node click
          toggleNode(node.id);
        });

        nodeGroup.appendChild(indicator);
      }

      // Add X button for ignoring nodes
      const ignoreButton = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      ignoreButton.textContent = '✕';
      ignoreButton.setAttribute('x', width - 35);
      ignoreButton.setAttribute('y', NODE_HEIGHT / 2 + 4);
      ignoreButton.setAttribute('font-size', '11px');
      ignoreButton.setAttribute('text-anchor', 'middle');
      ignoreButton.setAttribute('fill', '#999');
      ignoreButton.classList.add('ignore-button');
      ignoreButton.style.cursor = 'pointer';

      // Add background circle for the X button
      const ignoreButtonBg = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
      ignoreButtonBg.setAttribute('cx', width - 35);
      ignoreButtonBg.setAttribute('cy', NODE_HEIGHT / 2);
      ignoreButtonBg.setAttribute('r', 8);
      ignoreButtonBg.setAttribute('fill', '#f5f5f5');
      ignoreButtonBg.setAttribute('stroke', '#ddd');
      ignoreButtonBg.setAttribute('stroke-width', '1');
      ignoreButtonBg.classList.add('ignore-button');
      ignoreButtonBg.style.cursor = 'pointer';

      // Handle ignore click
      const handleIgnoreClick = (e) => {
        e.stopPropagation(); // Prevent triggering the node click
        ignoreNode(node.id);
      };

      ignoreButtonBg.addEventListener('click', handleIgnoreClick);
      ignoreButton.addEventListener('click', handleIgnoreClick);

      // Hover effect for highlighting patterns
      nodeGroup.addEventListener('mouseenter', () => {
        // Find all ancestors and highlight the path
        const pattern = [node.id];
        let currentNode = node;

        while (currentNode.parent) {
          pattern.push(currentNode.parent);
          currentNode = treeNodes.find(n => n.id === currentNode.parent);
          if (!currentNode) break;
        }

        setHighlightedPattern(pattern);

        // Extract only the item names (not the full IDs) for the path display
        const simplifiedPath = [];
        pattern.forEach(id => {
          const parts = id.split('-');
          const lastPart = parts[parts.length - 1];
          simplifiedPath.unshift(lastPart);
        });

        setCurrentPath(simplifiedPath);
      });

      nodeGroup.addEventListener('mouseleave', () => {
        setHighlightedPattern(null);
        // Don't clear current path - keep it visible
      });

      // Node label
      const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      text.setAttribute('x', 8);
      text.setAttribute('y', NODE_HEIGHT / 2 + 4);
      text.setAttribute('font-size', node.level === 0 ? '13px' : '12px');
      text.setAttribute('font-weight', node.level === 0 ? 'bold' : 'normal');
      text.setAttribute('fill', '#333');

      // Make sure text fits
      const maxTextWidth = width - 60;
      let displayText = node.name;

      if (displayText.length > 25) {
        displayText = displayText.substring(0, 23) + '...';
      }

      text.textContent = displayText;

      // Support count
      const supportText = document.createElementNS('http://www.w3.org/2000/svg', 'text');
      supportText.setAttribute('x', width - 55);
      supportText.setAttribute('y', NODE_HEIGHT / 2 + 4);
      supportText.setAttribute('font-size', '12px');
      supportText.setAttribute('text-anchor', 'end');
      supportText.setAttribute('fill', '#555');
      supportText.setAttribute('font-weight', node.level === 0 ? 'bold' : 'normal');
      supportText.textContent = node.support;

      nodeGroup.appendChild(rect);
      nodeGroup.appendChild(ignoreButtonBg);
      nodeGroup.appendChild(ignoreButton);
      nodeGroup.appendChild(text);
      nodeGroup.appendChild(supportText);
      container.appendChild(nodeGroup);
    });
  };

  // Function to generate mining data using Eclat or FP Growth algorithm
  const generateMiningData = (combinationData, algo = 'eclat') => {
    if (!combinationData || combinationData.length === 0) {
      setMiningData(null);
      return;
    }

    // Step 1: Create transactions from header combinations
    const transactions = combinationData
      .filter(item => item.header_group)
      .map(item => item.header_group.split('|'));

    // Step 2: Count item frequencies for both algorithms
    const itemCounts = {};
    const totalTransactions = transactions.length;

    transactions.forEach(transaction => {
      transaction.forEach(item => {
        itemCounts[item] = (itemCounts[item] || 0) + 1;
      });
    });

    // Calculate minimum support threshold - use absolute minimum of 2
    const minSupportCount = Math.max(2, Math.floor(totalTransactions * minSupport));

    // Get frequent items sorted by frequency
    const frequentItems = Object.keys(itemCounts)
      .filter(item => itemCounts[item] >= minSupportCount)
      .sort((a, b) => itemCounts[b] - itemCounts[a]);

    // Create single tree structure with most frequent item as root
    if (frequentItems.length === 0) {
      setMiningData([]);
      return;
    }

    // Build a single hierarchical tree structure instead of multiple independent trees
    if (algo === 'eclat') {
      // ECLAT ALGORITHM IMPLEMENTATION

      // Step 1: Build vertical tid-lists (transaction ID lists) for each item
      const tidLists = {};
      frequentItems.forEach(item => {
        tidLists[item] = new Set();
      });

      transactions.forEach((transaction, tid) => {
        transaction.forEach(item => {
          if (frequentItems.includes(item)) {
            tidLists[item].add(tid);
          }
        });
      });

      // Create a single root node representing the most frequent item
      const rootItem = frequentItems[0];
      const rootSupport = tidLists[rootItem].size;

      const singleTreeRoot = {
        id: rootItem,
        name: rootItem,
        support: rootSupport,
        confidence: rootSupport / totalTransactions,
        children: []
      };

      // Build a tree structure following the frequency order of items
      const buildOrderedTree = (currentNode, currentPrefix, currentTids, level) => {
        if (level >= maxDepth) return;

        // Find index of the current node's item in the frequentItems list
        const currentItemIndex = frequentItems.indexOf(currentNode.name);

        // Check for next items in the frequency order
        for (let i = currentItemIndex + 1; i < frequentItems.length; i++) {
          const nextItem = frequentItems[i];
          const nextItemTids = tidLists[nextItem];

          // Calculate intersection
          const intersection = new Set([...currentTids].filter(tid => nextItemTids.has(tid)));
          const support = intersection.size;

          // Add to tree if support is sufficient
          if (support >= Math.max(2, minSupportCount)) {
            const newPrefix = [...currentPrefix, nextItem];
            const confidence = support / currentTids.size;

            // Add only if confidence is sufficient (reduced for deeper patterns)
            if (confidence >= (level > 2 ? minConfidence/2 : minConfidence)) {
              const childNode = {
                id: `${currentNode.id}-${nextItem}`,
                name: nextItem,
                support: support,
                confidence: confidence,
                children: []
              };

              currentNode.children.push(childNode);

              // Recursively build tree with this child as new parent
              buildOrderedTree(childNode, newPrefix, intersection, level + 1);
            }
          }
        }
      };

      // Start building the tree from the root
      buildOrderedTree(singleTreeRoot, [rootItem], tidLists[rootItem], 1);

      // Initialize expanded nodes for visualization
      const initialExpanded = {};
      initialExpanded[rootItem] = true; // Expand root node

      // Expand first level children
      if (singleTreeRoot.children && singleTreeRoot.children.length > 0) {
        singleTreeRoot.children.slice(0, 5).forEach(child => {
          initialExpanded[child.id] = true;

          // Also expand first grandchild if available
          if (child.children && child.children.length > 0) {
            initialExpanded[child.children[0].id] = true;
          }
        });
      }

      // Set the result as a single-item array containing the root
      setMiningData([singleTreeRoot]);
      setExpandedNodes(initialExpanded);

    } else {
      // FP-GROWTH for a single hierarchical tree

      // Step 3: Reorder transactions based on frequency
      const orderedTransactions = transactions.map(transaction => {
        return transaction
          .filter(item => frequentItems.includes(item))
          .sort((a, b) => itemCounts[b] - itemCounts[a]);
      }).filter(transaction => transaction.length > 0);

      // Create a single root node for the most frequent item
      const rootItem = frequentItems[0];

      const singleTreeRoot = {
        id: rootItem,
        name: rootItem,
        support: itemCounts[rootItem],
        confidence: itemCounts[rootItem] / totalTransactions,
        children: []
      };

      // Create a mapping of patterns to their support count
      const patternSupports = {};

      // First pass - gather all pattern supports
      orderedTransactions.forEach(transaction => {
        if (transaction.length < 2) return;

        for (let i = 0; i < transaction.length; i++) {
          for (let j = i + 1; j < transaction.length; j++) {
            const pattern = [transaction[i], transaction[j]];
            const key = pattern.join('-');
            patternSupports[key] = (patternSupports[key] || 0) + 1;
          }
        }
      });

      // Then build tree with those precomputed pattern supports
      const buildOrderedFPTree = (currentNode, level) => {
        if (level >= maxDepth) return;

        const currentItemIndex = frequentItems.indexOf(currentNode.name);

        for (let i = currentItemIndex + 1; i < frequentItems.length; i++) {
          const nextItem = frequentItems[i];

          // Check if the pair meets minimum support
          const patternKey = [currentNode.name, nextItem].join('-');
          const support = patternSupports[patternKey] || 0;

          if (support >= Math.max(2, minSupportCount)) {
            const confidence = support / itemCounts[currentNode.name];

            if (confidence >= (level > 2 ? minConfidence/2 : minConfidence)) {
              const childNode = {
                id: `${currentNode.id}-${nextItem}`,
                name: nextItem,
                support: support,
                confidence: confidence,
                children: []
              };

              currentNode.children.push(childNode);
              buildOrderedFPTree(childNode, level + 1);
            }
          }
        }
      };

      // Start building the tree from the root
      buildOrderedFPTree(singleTreeRoot, 1);

      // Initialize expanded nodes for visualization
      const initialExpanded = {};
      initialExpanded[rootItem] = true; // Expand root node

      // Expand first level children
      if (singleTreeRoot.children && singleTreeRoot.children.length > 0) {
        singleTreeRoot.children.slice(0, 5).forEach(child => {
          initialExpanded[child.id] = true;

          // Also expand first grandchild if available
          if (child.children && child.children.length > 0) {
            initialExpanded[child.children[0].id] = true;
          }
        });
      }

      // Set the result as a single-item array containing the root
      setMiningData([singleTreeRoot]);
      setExpandedNodes(initialExpanded);
    }
  };

  // Switch between algorithms
  const switchAlgorithm = (algo) => {
    setAlgorithm(algo);
  };

  // Change minimum support threshold
  const handleSupportChange = (e) => {
    const value = parseFloat(e.target.value);
    if (!isNaN(value) && value > 0 && value <= 1) {
      setMinSupport(value);
    }
  };

  // Change minimum confidence threshold
  const handleConfidenceChange = (e) => {
    const value = parseFloat(e.target.value);
    if (!isNaN(value) && value > 0 && value <= 1) {
      setMinConfidence(value);
    }
  };

  // Change maximum depth
  const handleDepthChange = (e) => {
    const value = parseInt(e.target.value);
    if (!isNaN(value) && value > 0) {
      setMaxDepth(value);
    }
  };

  // Function to toggle node expansion
  const toggleNode = (nodeId) => {
    setExpandedNodes(prev => ({
      ...prev,
      [nodeId]: !prev[nodeId]
    }));
  };

  // Toggle settings panel
  const toggleSettings = () => {
    setShowSettings(prev => !prev);
  };

  // Setup initial path on first render
  useEffect(() => {
    if (miningData && miningData.length > 0) {
      // Set initial path to root node
      const rootItem = miningData[0].name;
      setCurrentPath([rootItem]);
    }
  }, [miningData]);

  // Function to ignore a node
  const ignoreNode = (nodeId) => {
    setIgnoredNodes(prev => {
      const newIgnored = new Set(prev);
      newIgnored.add(nodeId);
      return newIgnored;
    });
  };

  // Function to send pattern to API
  const sendPatternToAPI = (node) => {
    // Get full pattern from node to root
    const getFullPattern = (nodeId) => {
      const parts = nodeId.split('-');
      return parts.filter(part => !ignoredNodes.has(part));
    };

    // Get full pattern without ignored nodes
    const pattern = getFullPattern(node.id);

    // Join with delimiter
    const patternString = pattern.join('|');

    // Send to API if onPatternSelect is provided
    if (onPatternSelect && patternString) {
      onPatternSelect(patternString);
      console.log(`전송된 패턴: ${patternString}`);
    }
  };

  return (
    <div className="tab-content">
      {loading ? (
        <div className="loading">로딩 중...</div>
      ) : error ? (
        <div className="error">에러: {error}</div>
      ) : combinationHeaders && combinationHeaders.length > 0 ? (
        <div className="mining-view">
          <div className="mining-header">
            <div className="current-path">
              {currentPath.map((item, index) => (
                <span key={item + index}>
                  {index > 0 && <span className="path-separator">→</span>}
                  <span className={index === 0 ? "path-root" : ""}>{item}</span>
                </span>
              ))}
            </div>
            <div className="header-actions">
              {ignoredNodes.size > 0 && (
                <button 
                  className="reset-button" 
                  onClick={() => setIgnoredNodes(new Set())}
                >
                  무시 초기화
                </button>
              )}
              <button className="settings-button" onClick={toggleSettings}>
                ⚙️
              </button>
            </div>
          </div>

          <div className="mining-tree-container">
            <svg 
              className="mining-tree-svg" 
              ref={svgRef} 
              width="100%" 
              height="500"
            ></svg>
          </div>

          {showSettings && (
            <div className="settings-panel">
              <div className="settings-header">
                <h3>패턴 설정</h3>
                <button className="close-button" onClick={toggleSettings}>×</button>
              </div>
              <div className="settings-content">
                <div className="threshold-item">
                  <div className="threshold-label">{(minSupport * 100).toFixed(0)}%</div>
                  <input 
                    type="range" 
                    min="0.01" 
                    max="0.5" 
                    step="0.01" 
                    value={minSupport}
                    onChange={handleSupportChange}
                  />
                  <div className="threshold-name">최소 지지도</div>
                </div>
                <div className="threshold-item">
                  <div className="threshold-label">{(minConfidence * 100).toFixed(0)}%</div>
                  <input 
                    type="range" 
                    min="0.01" 
                    max="0.9" 
                    step="0.01" 
                    value={minConfidence}
                    onChange={handleConfidenceChange}
                  />
                  <div className="threshold-name">최소 신뢰도</div>
                </div>
                <div className="threshold-item">
                  <div className="threshold-label">{maxDepth}</div>
                  <input 
                    type="range" 
                    min="3" 
                    max="100" 
                    step="1" 
                    value={maxDepth}
                    onChange={handleDepthChange}
                  />
                  <div className="threshold-name">최대 패턴 깊이</div>
                </div>
              </div>
            </div>
          )}
        </div>
      ) : (
        <div className="column-empty">
          {testMode 
            ? "마이닝 데이터를 로딩 중입니다..." 
            : "채널과 헤더를 선택하면 마이닝 결과가 표시됩니다."}
        </div>
      )}

      <style jsx>{`
